    <script>
        let selectedTool = null;
        let sceneObjects = [];
        let activeObject = null;
        let interactionMode = null;
        let offsetX, offsetY;

        const canvasSize = 800;

        function setup() {
            createCanvas(canvasSize, canvasSize).parent('drawing-canvas');
            noStroke();
        }

        function draw() {
            background(255);
            sceneObjects.forEach(drawObject);

            if (selectedTool) {
                fill(200);
                textAlign(CENTER, CENTER);
                textSize(16);
                text(selectedTool.value, mouseX, mouseY);
            }
        }

        function drawObject(object) {
            push();
            translate(object.x, object.y);
            rotate(radians(object.rotation));
            if (object.type === 'atom' || object.type === 'charge') {
                fill(0);
                textSize(16);
                text(object.value, 0, 0);
            } else if (object.type === 'structure') {
                drawStructure(object.value);
            }
            pop();
        }

        function drawStructure(structure) {
            switch (structure) {
                case 'water':
                    fill(0);
                    textSize(16);
                    text('O', 0, 0);
                    text('H', -20, -20);
                    text('H', 20, -20);
                    stroke(0);
                    line(-20, -20, 0, 0);
                    line(20, -20, 0, 0);
                    break;
                // Add more structures here
                default:
                    break;
            }
        }

        function mousePressed() {
            if (selectedTool) {
                sceneObjects.push({ type: selectedTool.type, value: selectedTool.value, x: mouseX, y: mouseY, rotation: 0 });
                selectedTool = null;
                selectAll('.selected').removeClass('selected');
            } else {
                for (let i = sceneObjects.length - 1; i >= 0; i--) {
                    const obj = sceneObjects[i];
                    const dx = mouseX - obj.x;
                    const dy = mouseY - obj.y;
                    const distance = sqrt(dx * dx + dy * dy);
                    if (distance < 25) {
                        activeObject = obj;
                        if (mouseButton === LEFT) {
                            interactionMode = 'translate';
                            offsetX = dx;
                            offsetY = dy;
                        } else if (mouseButton === RIGHT) {
                            interactionMode = 'rotate';
                            const angle = atan2(dy, dx);
                            activeObject.initialAngle = angle;
                        }
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (activeObject) {
                if (interactionMode === 'translate') {
                    activeObject.x = mouseX - offsetX;
                    activeObject.y = mouseY - offsetY;
                } else if (interactionMode === 'rotate') {
                    const angle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
                    activeObject.rotation = degrees(angle - activeObject.initialAngle);
                }
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        function keyPressed() {
            if (keyCode === BACKSPACE || keyCode === DELETE) {
                if (sceneObjects.length > 0) {
                    sceneObjects.pop();
                }
            }
        }

        document.querySelectorAll('#toolbar button').forEach(button => {
            button.addEventListener('click', () => {
                selectAll('.selected').removeClass('selected');
                button.classList.add('selected');
                selectedTool = { type: button.id.startsWith('btn-charge') ? 'charge' : 'atom', value: button.textContent };
            });
        });

        document.getElementById('btn-add').addEventListener('click', () => {
            const structure = document.getElementById('select-structure').value;
            if (structure) {
                sceneObjects.push({ type: 'structure', value: structure, x: canvasSize / 2, y: canvasSize / 2, rotation: 0 });
            }
        });

        document.getElementById('btn-undo').addEventListener('click', () => {
            if (sceneObjects.length > 0) {
                sceneObjects.pop();
            }
        });
    </script>
</body>
</html>
