    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="controls-container">
        <div>
            <label for="slider-amplitude">\\(a\\)</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude">1.50</span>
        </div>
        <div>
            <label for="slider-frequency">\\(f\\)</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency">0.30</span>
        </div>
        <div>
            <label for="slider-wavelength">\\(\\lambda\\)</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength">4.50</span>
        </div>
        <div>
            <label for="slider-time">\\(t\\)</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time">2.50</span>
        </div>
        <div>
            <label for="select-phase">initial phase \\(\\phi_0\\)</label>
            <select id="select-phase">
                <option value="-3.14159">-π</option>
                <option value="-2.0944">-2π/3</option>
                <option value="-1.5708">-π/2</option>
                <option value="-1.0472">-π/3</option>
                <option value="0">0</option>
                <option value="1.0472">π/3</option>
                <option value="1.5708">π/2</option>
                <option value="2.0944">2π/3</option>
                <option value="3.14159">π</option>
            </select>
        </div>
    </div>
    <div id="formula-display">
        \(y(x,t) = A \cos(\omega t - kx + \phi_0)\)
    </div>
    <div id="canvas-container"></div>

    <script>
        let A, f, λ, t, ϕ0, k, ω;

        function setup() {
            createCanvas(800, 600);
            updateParameters();
            drawFormula();
        }

        function draw() {
            background(255);
            drawAxes();
            drawGrid();
            drawWave();
            drawPointsAndVectors();
        }

        function updateParameters() {
            A = parseFloat(document.getElementById('slider-amplitude').value);
            f = parseFloat(document.getElementById('slider-frequency').value);
            λ = parseFloat(document.getElementById('slider-wavelength').value);
            t = parseFloat(document.getElementById('slider-time').value);
            ϕ0 = parseFloat(document.getElementById('select-phase').value);

            k = 2 * Math.PI / λ;
            ω = 2 * Math.PI * f;

            document.getElementById('value-amplitude').textContent = A.toFixed(2);
            document.getElementById('value-frequency').textContent = f.toFixed(2);
            document.getElementById('value-wavelength').textContent = λ.toFixed(2);
            document.getElementById('value-time').textContent = t.toFixed(2);
        }

        function drawFormula() {
            let formula;
            if (ϕ0 === Math.PI / 2) {
                formula = `y(x,t) = ${A.toFixed(2)} \\sin(kx - \\omega t)`;
            } else if (ϕ0 === -Math.PI / 2) {
                formula = `y(x,t) = -${A.toFixed(2)} \\sin(kx - \\omega t)`;
            } else if (ϕ0 === Math.PI) {
                formula = `y(x,t) = -${A.toFixed(2)} \\cos(kx - \\omega t)`;
            } else if (ϕ0 === 0) {
                formula = `y(x,t) = ${A.toFixed(2)} \\cos(kx - \\omega t)`;
            } else {
                formula = `y(x,t) = ${A.toFixed(2)} \\cos(${ω.toFixed(2)} t - ${k.toFixed(2)} x + ${ϕ0.toFixed(2)})`;
            }
            document.getElementById('formula-display').innerHTML = formula;
            MathJax.typesetPromise([document.getElementById('formula-display')]);
        }

        function drawAxes() {
            stroke(0);
            line(0, height / 2, width, height / 2); // x-axis
            line(width / 2, height, width / 2, 0); // y-axis
            textSize(16);
            textAlign(CENTER, BOTTOM);
            text('x', width, height / 2);
            textAlign(RIGHT, CENTER);
            text('y', width / 2, 0);
        }

        function drawGrid() {
            stroke(150);
            strokeWeight(1);
            for (let x = 1; x <= 16; x++) {
                let xPos = map(x, 1, 16, width / 2, width);
                line(xPos, height / 2, xPos, height / 2 - 10);
                line(xPos, height / 2, xPos, height / 2 + 10);
                textSize(12);
                textAlign(CENTER, TOP);
                text(x, xPos, height / 2 + 15);
            }
        }

        function drawWave() {
            stroke(0, 0, 255);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let x = 0; x <= width; x++) {
                let worldX = map(x, 0, width, 0, 16);
                let y = A * Math.cos(ω * t - k * worldX + ϕ0);
                let yPos = map(y, -A, A, height / 2, height / 2 - height);
                vertex(x, yPos);
            }
            endShape();
        }

        function drawPointsAndVectors() {
            stroke(0);
            strokeWeight(1);
            fill(0);
            for (let x = 1; x <= 16; x++) {
                let worldX = map(x, 1, 16, 0, 16);
                let y = A * Math.cos(ω * t - k * worldX + ϕ0);
                let yPos = map(y, -A, A, height / 2, height / 2 - height);
                ellipse(x, yPos, 5, 5);

                let v = -A * ω * Math.sin(ω * t - k * worldX + ϕ0);
                let vYPos = yPos - v * 15;
                drawArrow(x, yPos, vYPos, 'red', '\\(\\vec{v}\\)');

                let a = -A * ω * ω * Math.cos(ω * t - k * worldX + ϕ0);
                let aYPos = yPos - a * 5;
                drawArrow(x, yPos, aYPos, 'black', '\\(\\vec{a}\\)');
            }
        }

        function drawArrow(baseX, baseY, vectorY, color, label) {
            stroke(color);
            strokeWeight(2);
            fill(color);
            line(baseX, baseY, baseX, vectorY);
            triangle(baseX, vectorY, baseX - 5, vectorY + 5, baseX + 5, vectorY + 5);
            textSize(12);
            textAlign(CENTER, BOTTOM);
            fill(color);
            text(label, baseX, vectorY - 5);
        }

        document.getElementById('slider-amplitude').addEventListener('input', () => {
            updateParameters();
            drawFormula();
            redraw();
        });

        document.getElementById('slider-frequency').addEventListener('input', () => {
            updateParameters();
            drawFormula();
            redraw();
        });

        document.getElementById('slider-wavelength').addEventListener('input', () => {
            updateParameters();
            drawFormula();
            redraw();
        });

        document.getElementById('slider-time').addEventListener('input', () => {
            updateParameters();
            drawFormula();
            redraw();
        });

        document.getElementById('select-phase').addEventListener('change', () => {
            updateParameters();
            drawFormula();
            redraw();
        });
    </script>
</body>
</html>
