        <h3>Kepler orbits</h3>
        <div id="canvas-container"></div>
        <div id="energy-display">energy = <span id="energy-value">\\(-\\frac{1}{4}\\)</span></div>
    </div>

    <script>
        let ε = 0;
        let p = 2;

        function setup() {
            createCanvas(700, 500).parent('canvas-container');
            translate(width * 0.7, height / 2);
            scale(1, -1);
            noLoop();
        }

        function draw() {
            background('#f0f0f0');
            stroke(0);
            strokeWeight(1);
            line(-width / 2, 0, width / 2, 0); // x-axis
            line(0, -height / 2, 0, height / 2); // y-axis

            textSize(12);
            textAlign(CENTER, BOTTOM);
            text("Global'x", width / 2, 5);
            textAlign(RIGHT, CENTER);
            text("Global'y", -5, -height / 2);

            for (let i = -width / 2; i <= width / 2; i += 10) {
                line(i, -1, i, 1);
                text(i, i, 5);
            }

            for (let j = -height / 2; j <= height / 2; j += 10) {
                line(-1, j, 1, j);
                text(j, 5, -j);
            }

            fill(0);
            noStroke();
            ellipse(0, 0, 6, 6);

            stroke('#4a80b5');
            strokeWeight(2);
            noFill();

            if (ε === 0) {
                ellipse(-2, 0, 40, 40);
            } else if (ε < 1) {
                beginShape();
                for (let θ = 0; θ < TWO_PI; θ += 0.01) {
                    let r = p / (1 + ε * cos(θ));
                    let x = r * cos(θ);
                    let y = r * sin(θ);
                    vertex(x, y);
                }
                endShape();
            } else if (ε === 1) {
                beginShape();
                for (let θ = -2.2; θ <= 2.2; θ += 0.01) {
                    let r = p / (1 + ε * cos(θ));
                    let x = r * cos(θ);
                    let y = r * sin(θ);
                    vertex(x, y);
                }
                endShape();
            } else {
                beginShape();
                for (let θ = -acos(-1 / ε); θ <= acos(-1 / ε); θ += 0.01) {
                    let r = p / (1 + ε * cos(θ));
                    let x = r * cos(θ);
                    let y = r * sin(θ);
                    vertex(x, y);
                }
                endShape();
            }

            let E = -0.25 * (1 - ε * ε);
            let energyDisplay = select('#energy-value');
            if (ε === 0) {
                energyDisplay.html('\\(-\\frac{1}{4}\\)');
                MathJax.typesetPromise([energyDisplay.elt]).then(() => {});
            } else {
                energyDisplay.html(E.toFixed(2));
            }
        }

        function updateEccentricity() {
            ε = parseFloat(document.getElementById('slider-eccentricity').value);
            document.getElementById('value-eccentricity').textContent = ε.toFixed(1);
            redraw();
        }

        document.getElementById('slider-eccentricity').addEventListener('input', updateEccentricity);
    </script>
