                plot_bgcolor: 'white',
                grid: { color: 'gray' },
                dragmode: false,
                showlegend: true
            };

            Plotly.newPlot('plot2', traces, layout);
        }

        function updatePlot3(k, a) {
            const hgt = Array.from({ length: 101 }, (_, i) => i * 0.02);
