<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Quantum Computer Search Algorithms</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        .main-container {
            width: 90%;
            max-width: 1200px;
            margin: 20px;
        }
        .control-panel {
            display: flex;
            flex-direction: column;
            gap: 10px;
            margin-bottom: 20px;
        }
        .control-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .visualization-section {
            border: 1px solid #ccc;
            padding: 10px;
            display: flex;
            gap: 20px;
        }
        .plot {
            flex: 1;
        }
        .plot h4 {
            text-align: center;
        }
        .plot-buttons {
            display: flex;
            justify-content: space-around;
            margin-bottom: 10px;
        }
        .active {
            background-color: #007bff;
            color: white;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <h1>Quantum Computer Search Algorithms</h1>
        <div class="control-panel">
            <div class="control-group">
                <span>algorithm:</span>
                <button id="btn-unstructured">unstructured</button>
                <button id="btn-adiabatic">adiabatic</button>
                <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                <button id="btn-heuristic" class="active">heuristic</button>
            </div>
            <div class="control-group">
                <label for="slider-total-steps">number of steps to complete the search:</label>
                <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
                <span id="display-total-steps">10</span>
            </div>
            <div class="control-group">
                <label for="slider-current-step">current step (0 to 10):</label>
                <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
                <span id="display-current-step">0</span>
            </div>
        </div>
        <div class="visualization-section">
            <div class="plot">
                <h4>amplitudes</h4>
                <div id="plot-amplitudes"></div>
            </div>
            <div class="plot">
                <div class="plot-buttons">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability"></div>
            </div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.4.0/math.min.js"></script>
    <script>
        const N = 64;
        let selectedAlgorithm = 'heuristic';
        let totalSteps = 10;
        let currentStep = 0;
        let plotMode = 'conflicts';
        let solutions = [];
        let conflictNumbers = [];
        let stateVector = [];

        function init() {
            solutions = [Math.floor(Math.random() * N), Math.floor(Math.random() * N)];
            conflictNumbers = Array(N).fill(0);
            for (let i = 0; i < N; i++) {
                if (!solutions.includes(i)) {
                    conflictNumbers[i] = Math.min(
                        solutions[0] ^ i,
                        solutions[1] ^ i
                    ).toString(2).split('0').join('').length;
                }
            }
            stateVector = Array(N).fill(math.complex(1 / Math.sqrt(N)));
            update();
        }

        function update() {
            const s = currentStep / totalSteps;
            let newStateVector;
            switch (selectedAlgorithm) {
                case 'unstructured':
                    const oracle = math.diag(Array(N).fill(1).map((_, i) => solutions.includes(i) ? -1 : 1));
                    const diffusion = math.add(math.multiply(2, math.diag(Array(N).fill(1 / N))), math.identity(N)).map(v => -v);
                    const groverOperator = math.multiply(oracle, diffusion);
                    newStateVector = math.pow(groverOperator, currentStep).map(row => row.map(v => v.times(stateVector)));
                    break;
                case 'adiabatic':
                case 'discrete-adiabatic':
                case 'heuristic':
                    const H0 = math.diag(Array(N).fill(1).map((_, i) => solutions.includes(i) ? 0 : 1));
                    const Hp = math.diag(conflictNumbers);
                    const Hs = math.add(math.multiply(1 - s, H0), math.multiply(s, Hp));
                    const { values } = math.eigs(Hs);
                    newStateVector = values.col(0).toArray().map(v => math.complex(v));
                    break;
            }
            plotAmplitudes(newStateVector);
            plotProbability(newStateVector);
        }

        function plotAmplitudes(stateVector) {
            const traces = [
                {
                    x: stateVector.map(c => c.re),
                    y: stateVector.map(c => c.im),
                    mode: 'markers',
                    marker: {
                        size: 5,
                        color: conflictNumbers.map(c => c > 0 ? 'orange' : 'blue'),
                        opacity: 0.8
                    },
                    type: 'scattergl'
                }
            ];
            Plotly.newPlot('plot-amplitudes', traces, {
                title: 'Amplitudes',
                xaxis: { title: 'Re', range: [-1, 1] },
                yaxis: { title: 'Im', range: [-1, 1] }
            });
        }

        function plotProbability(stateVector) {
            const probabilities = stateVector.map(c => c.norm() ** 2);
            let trace;
            if (plotMode === 'conflicts') {
                const data = Array.from({ length: 8 }, () => ({ x: [], y: [] }));
                probabilities.forEach((p, i) => {
                    data[conflictNumbers[i]].x.push(conflictNumbers[i]);
                    data[conflictNumbers[i]].y.push(p);
                });
                trace = data.map(d => ({
                    x: d.x,
                    y: d.y,
                    type: 'bar',
                    marker: { color: 'skyblue' }
                }));
            } else {
                // Eigenvalues mode
                trace = [{
                    x: conflictNumbers,
                    y: probabilities,
                    type: 'bar',
                    marker: { color: 'skyblue' }
                }];
            }
            Plotly.newPlot('plot-probability', trace, {
                title: 'Probability Distribution',
                xaxis: { title: plotMode === 'conflicts' ? 'Number of Conflicts' : 'Eigenvalue' },
                yaxis: { title: 'Probability', range: [0, 1] }
            });
        }

        document.getElementById('btn-unstructured').addEventListener('click', () => {
            selectedAlgorithm = 'unstructured';
            totalSteps = 5;
            document.getElementById('slider-total-steps').max = totalSteps;
            document.getElementById('slider-total-steps').value = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            document.getElementById('slider-current-step').value = 0;
            updateButtons();
            update();
        });

        document.getElementById('btn-adiabatic').addEventListener('click', () => {
            selectedAlgorithm = 'adiabatic';
            totalSteps = 200;
            document.getElementById('slider-total-steps').max = totalSteps;
            document.getElementById('slider-total-steps').value = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            document.getElementById('slider-current-step').value = 0;
            updateButtons();
            update();
        });

        document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => {
            selectedAlgorithm = 'discrete-adiabatic';
            totalSteps = 200;
            document.getElementById('slider-total-steps').max = totalSteps;
            document.getElementById('slider-total-steps').value = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            document.getElementById('slider-current-step').value = 0;
            updateButtons();
            update();
        });

        document.getElementById('btn-heuristic').addEventListener('click', () => {
            selectedAlgorithm = 'heuristic';
            totalSteps = 10;
            document.getElementById('slider-total-steps').max = totalSteps;
            document.getElementById('slider-total-steps').value = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            document.getElementById('slider-current-step').value = 0;
            updateButtons();
            update();
        });

        document.getElementById('slider-total-steps').addEventListener('input', (e) => {
            totalSteps = parseInt(e.target.value);
            document.getElementById('display-total-steps').textContent = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            if (parseInt(document.getElementById('slider-current-step').value) > totalSteps) {
                document.getElementById('slider-current-step').value = totalSteps;
            }
            document.getElementById('display-current-step').textContent = document.getElementById('slider-current-step').value;
            update();
        });

        document.getElementById('slider-current-step').addEventListener('input', (e) => {
            currentStep = parseInt(e.target.value);
            document.getElementById('display-current-step').textContent = currentStep;
            update();
        });

        document.getElementById('btn-conflicts').addEventListener('click', () => {
            plotMode = 'conflicts';
            updateButtons();
            update();
        });

        document.getElementById('btn-eigenvalues').addEventListener('click', () => {
            plotMode = 'eigenvalues';
            updateButtons();
            update();
        });

        function updateButtons() {
            const buttons = document.querySelectorAll('.control-panel button');
            buttons.forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${selectedAlgorithm}`).classList.add('active');
            document.getElementById(`btn-${plotMode}`).classList.add('active');
        }

        init();
    </script>
