<body>
    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" min="0.01" max="0.20" step="0.005" value="0.08">
                <span id="value-pressure">0.08</span>
            </div>
            <div id="control-group-temperature" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" min="450" max="490" step="1" value="475">
                <span id="value-temperature">475</span>
            </div>
            <select id="select-plot">
                <option value="vs_temp">fugacity versus temperature</option>
                <option value="vs_press">fugacity versus pressure</option>
            </select>
            <div id="checkbox-container" style="display: none;">
                <input type="checkbox" id="checkbox-high-pressure" checked>
                <label for="checkbox-high-pressure">high pressure</label>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const sliderPressure = document.getElementById('slider-pressure');
        const valuePressure = document.getElementById('value-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const valueTemperature = document.getElementById('value-temperature');
        const selectPlot = document.getElementById('select-plot');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');
        const controlGroupPressure = document.getElementById('control-group-pressure');
        const controlGroupTemperature = document.getElementById('control-group-temperature');
        const checkboxContainer = document.getElementById('checkbox-container');
        const plotContainer = document.getElementById('plot-container');

        sliderPressure.addEventListener('input', updatePlot);
        valuePressure.textContent = sliderPressure.value.toFixed(2);

        sliderTemperature.addEventListener('input', updatePlot);
        valueTemperature.textContent = parseInt(sliderTemperature.value);

        selectPlot.addEventListener('change', () => {
            if (selectPlot.value === 'vs_temp') {
                controlGroupPressure.style.display = '';
                controlGroupTemperature.style.display = 'none';
                checkboxContainer.style.display = 'none';
            } else {
                controlGroupPressure.style.display = 'none';
                controlGroupTemperature.style.display = '';
                checkboxContainer.style.display = '';
            }
            updatePlot();
        });

        checkboxHighPressure.addEventListener('change', updatePlot);

        function updatePlot() {
            const plotType = selectPlot.value;
            if (plotType === 'vs_temp') {
                const pressure = parseFloat(sliderPressure.value);
                const pressureDisplay = pressure.toFixed(2);
                valuePressure.textContent = pressureDisplay;
                plotFugacityVsTemperature(pressure);
            } else {
                const temperature = parseInt(sliderTemperature.value);
                const temperatureDisplay = temperature;
                valueTemperature.textContent = temperatureDisplay;
                plotFugacityVsPressure(temperature);
            }
        }

        function plotFugacityVsTemperature(pressure) {
            const x = [];
            const y = [];
            const metastableX = [];
            const metastableY = [];
            const saturationTemp = calculateSaturationTemperature(pressure);
            const fSat = calculateSaturationFugacity(saturationTemp);

            for (let temp = 280; temp <= 400; temp++) {
                x.push(temp);
                y.push(calculateSaturationFugacity(temp));
                if (temp >= saturationTemp) {
                    metastableX.push(temp);
                    metastableY.push(calculateSaturationFugacity(temp));
                }
            }

            const data = [
                { x: x, y: y, mode: 'lines', name: 'Stable Liquid', line: { color: '#0000FF' } },
                { x: [saturationTemp, 400], y: [pressure, pressure], mode: 'lines', name: 'Stable Vapor', line: { color: '#0000FF' } },
                { x: metastableX, y: metastableY, mode: 'lines', name: 'Metastable Liquid', line: { color: '#A0A0FF', dash: 'dash' } },
                { x: [280, saturationTemp], y: [pressure, pressure], mode: 'lines', name: 'Metastable Vapor', line: { color: '#A0A0FF', dash: 'dash' } },
                { x: [saturationTemp], y: [pressure], mode: 'markers', name: 'Saturation Point', marker: { color: '#000000', size: 8 } },
                { x: [saturationTemp, saturationTemp], y: [0, pressure], mode: 'lines', name: 'Indicator Line', line: { color: '#000000', dash: 'dash' } }
            ];

            const layout = {
                title: 'Fugacity versus Temperature',
                xaxis: { title: 'temperature (°C)', range: [280, 400] },
                yaxis: { title: 'fugacity (bar)', range: [0, 0.22] },
                paper_bgcolor: '#fff',
                gridcolor: '#e0e0e0',
                font: { size: 14 }
            };

            Plotly.newPlot(plotContainer, data, layout);
        }

        function plotFugacityVsPressure(temperature) {
            const x = [];
            const y = [];
            const metastableX = [];
            const metastableY = [];
            const saturationPressure = calculateSaturationPressure(temperature);
            const fSat = calculateSaturationFugacity(celsiusToKelvin(saturationPressure));

            for (let press = 0; press <= 3.0; press += 0.01) {
                x.push(press);
                y.push(calculateVaporFugacity(press, temperature));
                if (press >= saturationPressure) {
                    metastableX.push(press);
                    metastableY.push(calculateVaporFugacity(press, temperature));
                }
            }

            const data = [
                { x: x, y: y, mode: 'lines', name: 'Stable Vapor', line: { color: '#0000FF' } },
                { x: [saturationPressure, 3.0], y: [fSat, fSat], mode: 'lines', name: 'Stable Liquid', line: { color: '#0000FF' } },
                { x: metastableX, y: metastableY, mode: 'lines', name: 'Metastable Vapor', line: { color: '#A0A0FF', dash: 'dash' } },
                { x: [0, saturationPressure], y: [fSat, fSat], mode: 'lines', name: 'Metastable Liquid', line: { color: '#A0A0FF', dash: 'dash' } },
                { x: [saturationPressure], y: [fSat], mode: 'markers', name: 'Saturation Point', marker: { color: '#000000', size: 8 } },
                { x: [saturationPressure, saturationPressure], y: [0, fSat], mode: 'lines', name: 'Indicator Line', line: { color: '#000000', dash: 'dash' } }
            ];

            const layout = {
                title: 'Fugacity versus Pressure',
                xaxis: { title: 'pressure (MPa)', range: [0, 3.0] },
                yaxis: { title: 'fugacity (MPa)', range: [0, 3.0] },
                paper_bgcolor: '#fff',
                gridcolor: '#e0e0e0',
                font: { size: 14 }
            };

            Plotly.newPlot(plotContainer, data, layout);
        }

        function calculateSaturationTemperature(pressure) {
            return (5818 / (8.65 - Math.log10(pressure))) - 230;
        }

        function calculateSaturationFugacity(temperature) {
            return Math.pow(10, 8.65 - 5818 / (230 + temperature));
        }

        function calculateSaturationPressure(temperature) {
            return Math.pow(10, 10.83 - 5047 / temperature);
        }

        function calculateVaporFugacity(pressure, temperature) {
            if (checkboxHighPressure.checked) {
                return pressure * Math.exp(-25 * pressure / temperature);
            } else {
                return pressure;
            }
        }

        function celsiusToKelvin(celsius) {
            return celsius + 273.15;
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
