    <script>
        let theta = 0.44879;
        const sinTheta = Math.sin(theta);
        const cosTheta = Math.cos(theta);
        const R = 150;
        let cX = R * 0.3;
        let cY = R * 0.7;

        function setup() {
            createCanvas(400, 400);
            updateCanvas();
            updatePlot();
        }

        function draw() {
            // Nothing to do here
        }

        function updateCanvas() {
            background(255);
            translate(width / 2, height);
            scale(1, -1);

            // Draw semi-circle
            noFill();
            stroke(0);
            arc(0, 0, R * 2, R * 2, 0, PI);

            // Draw axes
            stroke(0);
            line(-R, 0, R, 0);
            line(0, 0, 0, R);

            // Draw vectors
            drawVector(a, color(0, 0, 255));
            drawVector(b, color(255, 0, 0));
            drawVector(c, color(0, 255, 0));

            // Optional vectors
            if (document.getElementById('checkbox-first-order-vec').checked) {
                drawVector(dErr, color(255, 165, 0), true);
            }
            if (document.getElementById('checkbox-optimal-pair').checked) {
                drawVector(cOpt, color(0, 255, 255));
                drawVector(dOpt, color(255, 140, 0));
            }

            // Optional circles and arcs
            if (document.getElementById('checkbox-metric-circle').checked) {
                drawingContext.setLineDash([5, 5]);
                stroke(0, 0, 255);
                ellipse(cX, cY, dist(cX, cY, aX, aY) * 2, dist(cX, cY, aX, aY) * 2);
                drawingContext.setLineDash([]);
            }
            if (document.getElementById('checkbox-metric-region').checked || document.getElementById('checkbox-first-order-region').checked) {
                drawingContext.setLineDash([5, 5]);
                stroke(0, 128, 0);
                ellipse(0, 0, cX * 2, cX * 2);
                stroke(165, 42, 42);
                ellipse(0, 0, dOptX * 2, dOptX * 2);
                drawingContext.setLineDash([]);
            }
        }

        function drawVector(vector, color, dashed = false) {
            stroke(color);
            if (dashed) {
                drawingContext.setLineDash([5, 5]);
            }
            line(0, 0, vector.x, vector.y);
            drawingContext.setLineDash([]);
            fill(color);
            noStroke();
            triangle(vector.x, vector.y, vector.x - 10, vector.y - 10, vector.x - 10, vector.y + 10);
        }

        function updatePlot() {
            const sin2Theta = Math.pow(sinTheta, 2);
            const xData = [];
            const yData = [];
            for (let x = 0; x <= sin2Theta; x += 0.01) {
                yData.push(Math.pow(sinTheta * 2 - Math.sqrt(x), 2));
                xData.push(x);
            }

            const traces = [
                {
                    x: xData,
                    y: yData,
                    mode: 'lines',
                    line: { color: 'blue' }
                },
                {
                    x: [errorCA],
                    y: [errorDB],
                    mode: 'markers',
                    marker: { color: 'red', size: 10 }
                }
            ];

            if (document.getElementById('checkbox-optimal-pair').checked) {
                traces.push({
                    x: [sin2Theta / 4],
                    y: [sin2Theta / 4],
                    mode: 'markers',
                    marker: { color: 'green', size: 10 }
                });
            }

            Plotly.newPlot('plot-error', traces, {
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(D, B)', range: [0, 1.5] }
            });
        }

        function updateVectors() {
            aX = R * sinTheta;
            aY = R * cosTheta;
            bX = -R * sinTheta;
            bY = R * cosTheta;
            const dOptAngle = Math.atan2(bY - cY, bX - cX);
            dOptX = R * Math.cos(dOptAngle);
            dOptY = R * Math.sin(dOptAngle);
            errorCA = 0.5 * Math.pow(dist(cX, cY, aX, aY), 2);
            errorDB = 0.5 * Math.pow(dist(dOptX, dOptY, bX, bY), 2);
        }

        function mousePressed() {
            if (dist(mouseX - width / 2, height - mouseY, cX, cY) < 10) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                const dx = mouseX - pmouseX;
                const dy = mouseY - pmouseY;
                const newX = cX + dx;
                const newY = cY - dy;
                const distance = dist(newX, newY, 0, 0);
                if (distance <= R) {
                    cX = newX;
                    cY = newY;
                    updateVectors();
                    updateCanvas();
                    updatePlot();
                }
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        document.getElementById('slider-theta').addEventListener('input', (event) => {
            theta = parseFloat(event.target.value);
            sinTheta = Math.sin(theta);
            cosTheta = Math.cos(theta);
            updateVectors();
            updateCanvas();
            updatePlot();
            document.getElementById('span-theta-value').textContent = theta.toFixed(5);
            document.getElementById('span-sinsq-theta-value').textContent = Math.pow(sinTheta, 2).toFixed(5);
        });

        document.getElementById('checkbox-metric-circle').addEventListener('change', () => {
            updateCanvas();
        });

        document.getElementById('checkbox-metric-region').addEventListener('change', () => {
            updateCanvas();
        });

        document.getElementById('checkbox-first-order-vec').addEventListener('change', () => {
            updateCanvas();
        });

        document.getElementById('checkbox-first-order-region').addEventListener('change', () => {
            updateCanvas();
        });

        document.getElementById('checkbox-optimal-pair').addEventListener('change', () => {
            updateCanvas();
            updatePlot();
        });

        document.getElementById('checkbox-optimal-region').addEventListener('change', () => {
            updateCanvas();
        });

        let isDragging = false;
        let aX, aY, bX, bY, dOptX, dOptY, errorCA, errorDB;
        updateVectors();
        setup();
    </script>
</body>
</html>
