            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        .control-row label {
            margin-right: 10px;
        }
        .control-row input[type="range"] {
            flex-grow: 1;
        }
        .control-row span {
            margin-left: 10px;
        }
        #plot-container {
            padding: 20px;
            border: 1px solid #ddd;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const sliderPoints = document.getElementById('slider-points');
        const labelPoints = document.getElementById('label-points');
        const sliderTime = document.getElementById('slider-time');
        const labelTime = document.getElementById('label-time');
        const plotContainer = document.getElementById('plot-container');

        function calculateAnalyticalSolution(numX, t) {
            const xValues = [];
            const yValues = [];
            for (let i = 0; i <= numX; i++) {
                const x = i / numX;
                let T = 0;
                for (let n = 1; n <= 100; n++) {
                    T += (8 / Math.PI ** 2) * (1 / n ** 2) * Math.sin(n * Math.PI / 2) * Math.sin(n * Math.PI * x) * Math.exp(-2 * n ** 2 * Math.PI ** 2 * t);
                }
                xValues.push(x);
                yValues.push(T);
            }
            return { x: xValues, y: yValues };
        }

        function calculateNumericalSolution(numPoints, t) {
            const xValues = [];
            const yValues = [];
            const N = numPoints - 1;
            for (let j = 0; j <= N; j++) {
                const x = (Math.cos(j * Math.PI / N) + 1) / 2;
                let T = 0;
                for (let n = 1; n <= 100; n++) {
                    T += (8 / Math.PI ** 2) * (1 / n ** 2) * Math.sin(n * Math.PI / 2) * Math.sin(n * Math.PI * x) * Math.exp(-2 * n ** 2 * Math.PI ** 2 * t);
                }
                xValues.push(x);
                yValues.push(T);
            }
            return { x: xValues, y: yValues };
        }

        function updatePlot() {
            const numPoints = parseInt(sliderPoints.value);
            const t = parseFloat(sliderTime.value);

            const analyticalData = calculateAnalyticalSolution(200, t);
            const numericalData = calculateNumericalSolution(numPoints, t);

            const trace1 = {
                type: 'scatter',
                mode: 'lines',
                x: analyticalData.x,
                y: analyticalData.y,
                line: { color: 'blue', width: 3 }
            };

            const trace2 = {
                type: 'scatter',
                mode: 'markers',
                x: numericalData.x,
                y: numericalData.y,
                marker: { color: 'red', size: 8 }
            };

            const data = [trace1, trace2];

            const layout = {
                xaxis: {
                    title: 'x',
                    range: [0, 1],
                    tickmode: 'linear',
                    dtick: 0.2
                },
                yaxis: {
                    title: 'T',
                    range: [-0.1, 1.1],
                    tickmode: 'linear',
                    dtick: 0.2
                },
                showlegend: false,
                grid: {
                    xgap: 1,
                    ygap: 1,
                    color: 'lightgrey'
                },
                margin: {
                    t: 20,
                    r: 20,
                    b: 40,
                    l: 40
                }
            };

            Plotly.newPlot(plotContainer, data, layout);
        }

        sliderPoints.addEventListener('input', () => {
            labelPoints.textContent = sliderPoints.value;
            updatePlot();
        });

        sliderTime.addEventListener('input', () => {
            labelTime.textContent = sliderTime.value.toFixed(3);
            updatePlot();
        });

        window.onload = updatePlot;
    </script>
</body>
</html>
