            for (let i = 0; i < k; i++) {
                let mean = groupMeans[i];
                let sd = groupSDs[i];
                let yTop = map(mean + sd, yMin, yMax, height, 0);
                let yBottom = map(mean - sd, yMin, yMax, height, 0);
