            text("MS Between", 5, barChartHeight + barHeight / 2);
            text("MS Within", 5, barChartHeight + 2 * barHeight + barHeight / 2);
            text("F Ratio", 5, barChartHeight + 3 * barHeight + barHeight / 2);

            fill("#4691A5");
