<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>GEV Distributions Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            height: 100vh;
        }
        #main-container {
            display: flex;
            flex-direction: row;
            height: 100%;
        }
        #control-panel {
            width: 350px;
            padding: 20px;
            background-color: #f4f4f4;
            overflow-y: auto;
        }
        #pdf-plot-area {
            flex: 1;
            padding: 20px;
        }
        .slider-wrapper {
            margin-bottom: 15px;
        }
        .slider-wrapper label {
            display: block;
            margin-bottom: 5px;
        }
        .slider-wrapper input[type="text"] {
            width: 50px;
            text-align: right;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3 style="color: blue;">GEV Distribution 1</h3>
            <div class="slider-wrapper">
                <label for="slider-mu1">location μ₁</label>
                <input type="range" id="slider-mu1" min="2" max="6" step="0.01" value="3.87">
                <input type="text" id="display-mu1" readonly value="3.87">
            </div>
            <div class="slider-wrapper">
                <label for="slider-sigma1">scale σ₁</label>
                <input type="range" id="slider-sigma1" min="0.1" max="0.5" step="0.01" value="0.198">
                <input type="text" id="display-sigma1" readonly value="0.198">
            </div>
            <div class="slider-wrapper">
                <label for="slider-xi1">shape ξ₁</label>
                <input type="range" id="slider-xi1" min="-0.5" max="0.5" step="0.01" value="-0.05">
                <input type="text" id="display-xi1" readonly value="-0.05">
            </div>

            <h3 style="color: red;">GEV Distribution 2</h3>
            <div class="slider-wrapper">
                <label for="slider-mu2">location μ₂</label>
                <input type="range" id="slider-mu2" min="2" max="6" step="0.01" value="3.87">
                <input type="text" id="display-mu2" readonly value="3.87">
            </div>
            <div class="slider-wrapper">
                <label for="slider-sigma2">scale σ₂</label>
                <input type="range" id="slider-sigma2" min="0.1" max="0.5" step="0.01" value="0.3">
                <input type="text" id="display-sigma2" readonly value="0.3">
            </div>
            <div class="slider-wrapper">
                <label for="slider-xi2">shape ξ₂</label>
                <input type="range" id="slider-xi2" min="-0.5" max="0.5" step="0.01" value="-0.05">
                <input type="text" id="display-xi2" readonly value="-0.05">
            </div>

            <h3>Return Level Curves</h3>
            <div class="slider-wrapper">
                <label for="slider-pmax">P_max</label>
                <input type="range" id="slider-pmax" min="10" max="1000" step="1" value="100">
                <input type="text" id="display-pmax" readonly value="100">
            </div>
            <div id="plot-return-level"></div>
        </div>
        <div id="pdf-plot-area">
            <div id="plot-pdf"></div>
        </div>
    </div>

    <script>
        function gevPdf(x, mu, sigma, xi) {
            if (xi === 0) {
                const t = Math.exp(-(x - mu) / sigma);
                return (1 / sigma) * t * Math.exp(-t);
            } else {
                const t = Math.pow(1 + xi * ((x - mu) / sigma), -1 / xi);
                return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        function gevReturnLevel(p, mu, sigma, xi) {
            if (xi === 0) {
                return mu - sigma * Math.log(-Math.log(1 - 1 / p));
            } else {
                return mu - (sigma / xi) * Math.pow(1 - Math.pow(-Math.log(1 - 1 / p), -xi), 1 / xi);
            }
        }

        function update() {
            const mu1 = parseFloat(document.getElementById('slider-mu1').value);
            const sigma1 = parseFloat(document.getElementById('slider-sigma1').value);
            const xi1 = parseFloat(document.getElementById('slider-xi1').value);

            const mu2 = parseFloat(document.getElementById('slider-mu2').value);
            const sigma2 = parseFloat(document.getElementById('slider-sigma2').value);
            const xi2 = parseFloat(document.getElementById('slider-xi2').value);

            const pMax = parseInt(document.getElementById('slider-pmax').value);

            // Update PDF plot
            const x = Array.from({ length: 100 }, (_, i) => 2.5 + (7 - 2.5) * i / 99);
            const y1 = x.map(val => gevPdf(val, mu1, sigma1, xi1));
            const y2 = x.map(val => gevPdf(val, mu2, sigma2, xi2));

            Plotly.newPlot('plot-pdf', [
                { x: x, y: y1, type: 'scatter', mode: 'lines', fill: 'tozeroy', fillcolor: 'rgba(0, 0, 255, 0.2)', line: { color: 'blue' } },
                { x: x, y: y2, type: 'scatter', mode: 'lines', fill: 'tozeroy', fillcolor: 'rgba(255, 0, 0, 0.2)', line: { color: 'red' } }
            ], {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [2.5, 7], showticklabels: false },
                yaxis: { autorange: true, showticklabels: false }
            });

            // Update Return Level plot
            const pValues = Array.from({ length: pMax }, (_, i) => i + 1);
            const rl1 = pValues.map(p => gevReturnLevel(p, mu1, sigma1, xi1));
            const rl2 = pValues.map(p => gevReturnLevel(p, mu2, sigma2, xi2));

            Plotly.newPlot('plot-return-level', [
                { x: pValues, y: rl1, type: 'scatter', mode: 'lines', line: { color: 'blue' } },
                { x: pValues, y: rl2, type: 'scatter', mode: 'lines', line: { color: 'red' } }
            ], {
