                    if (t >= signal[i][0] && t < signal[i + 1][0]) {
                        return signal[i][1] + (signal[i + 1][1] - signal[i][1]) * (t - signal[i][0]) / (signal[i + 1][0] - signal[i][0]);
                    }
                }
                return 0;
            });

            const y = t.map(t => x[a * t - b]);
            const z = t.map(t => x[t - b]);
