                ],
                annotations: [
                    { x: T, y: 0.5, text: 'T', showarrow: true, arrowhead: 1, ax: 0, ay: -20, font: { color: 'red' } },
                    { x: b, y: 0.5, text: 'b', showarrow: true, arrowhead: 1, ax: 0, ay: -20, font: { color: 'green' } }
                ]
            };

            const layoutBottom = {
                xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true },
