            T = b / a;
            document.getElementById('slider-t').value = T;
            updateSliders();
            updatePlots();
        });

        document.getElementById('slider-b').addEventListener('input', () => {
            b = parseFloat(document.getElementById('slider-b').value);
            T = b / a;
            document.getElementById('slider-t').value = T;
            updateSliders();
            updatePlots();
