        document.getElementById('slider-t').addEventListener('input', () => {
            T = parseFloat(document.getElementById('slider-t').value);
            b = a * T;
            document.getElementById('slider-b').value = b;
            updateSliders();
            updatePlots();
        });

        document.querySelectorAll('#control-panel input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                const traceIndex = parseInt(checkbox.id.replace('check-', '')) - 1;
                Plotly.restyle('plot-bottom', 'visible', [checkbox.checked], [traceIndex]);
            });
        });

        updatePlots();
    </script>
</body>
