<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label>units in x direction</label>
                <input type="radio" name="unitsX" id="radio-x-0" value="0"> 0
                <input type="radio" name="unitsX" id="radio-x-1" value="1"> 1
