                <input type="radio" name="unitsY" id="radio-y-3" value="3"> 3
                <input type="radio" name="unitsY" id="radio-y-4" value="4"> 4
            </div>
            <div>
                <label>trim</label>
                <input type="range" id="slider-trim" min="0.65" max="0.9" step="0.001" value="0.8">
