    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight - 50);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;

        const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444);
        const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
        dirLight.position.set(1, 1, 1).normalize();
        scene.add(hemiLight);
        scene.add(dirLight);

        camera.position.z = 5;

        const trimSlider = document.getElementById('slider-trim');
        const trimValueLabel = document.getElementById('label-trim-value');
        trimSlider.addEventListener('input', () => {
            trimValueLabel.textContent = trimSlider.value.toFixed(3);
            updateScene();
        });

        const checkboxes = [
            document.getElementById('checkbox-double'),
            document.getElementById('checkbox-sphere'),
            document.getElementById('checkbox-triangles')
        ];
        checkboxes.forEach(checkbox => {
            checkbox.addEventListener('change', updateScene);
        });

        const radioButtonsX = Array.from(document.querySelectorAll('input[name="unitsX"]'));
        const radioButtonsY = Array.from(document.querySelectorAll('input[name="unitsY"]'));
        radioButtonsX.forEach(radio => radio.addEventListener('change', updateScene));
        radioButtonsY.forEach(radio => radio.addEventListener('change', updateScene));

        document.getElementById('btn-reset-view').addEventListener('click', () => {
            controls.reset();
        });

        function updateScene() {
            scene.clear();
            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(trimSlider.value);
            const double = checkboxes[0].checked;
            const sphere = checkboxes[1].checked;
            const triangles = checkboxes[2].checked;

            if (sphere) {
                const sphereGeometry = new THREE.SphereGeometry(0.5, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 80 });
                const sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphereMesh.position.set(0, -1, 0);
                scene.add(sphereMesh);
            }

            if (triangles) {
                const triangleGeometry = new THREE.BufferGeometry();
                const vertices = [];
                for (let i = 0; i < unitsX + 1; i++) {
                    for (let j = 0; j < unitsY + 1; j++) {
                        const x = i * 2;
                        const y = j * Math.sqrt(3);
                        const z = 0;
                        vertices.push(x, y, z);
                    }
                }
                triangleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                const triangleMaterial = new THREE.MeshBasicMaterial({ color: 0x800080, transparent: true, opacity: 0.4 });
                const triangleMesh = new THREE.Mesh(triangleGeometry, triangleMaterial);
                scene.add(triangleMesh);

                const pointsMaterial = new THREE.PointsMaterial({ color: 0x000000, size: 3 });
                const points = new THREE.Points(triangleGeometry, pointsMaterial);
                scene.add(points);
            }

            const basisVectorA = new THREE.Vector3(2, 0, 0);
            const basisVectorB = new THREE.Vector3(1, Math.sqrt(3), 0);

            for (let i = 0; i <= unitsX; i++) {
                for (let j = 0; j <= unitsY; j++) {
                    const position = new THREE.Vector3(i * 2, j * Math.sqrt(3), 0);
                    if ((i + j) % 2 === 1) {
                        position.add(new THREE.Vector3(1, Math.sqrt(3) / 2, 0));
                    }
                    addShape(position, trim, false);
                    if (double) {
                        addShape(position.clone().add(basisVectorA.add(basisVectorB).multiplyScalar(0.5)), trim, true);
                    }
                }
            }
        }

        function addShape(position, trim, isDouble) {
            const geometry = new THREE.BufferGeometry();
            const vertices = [];
            const colors = [];

            const radius = 1;
            const segments = 100;
            const angleStep = Math.PI * 2 / segments;

            for (let i = 0; i <= segments; i++) {
                const angle = i * angleStep;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = 0;
                vertices.push(x, y, z);

                const t = i / segments;
                const color = isDouble ? new THREE.Color(0x80ffff).lerp(new THREE.Color(0x008080), t) : new THREE.Color(0xffc0cb).lerp(new THREE.Color(0x904090), t);
                colors.push(color.r, color.g, color.b);
            }

            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshStandardMaterial({ vertexColors: true });
            const mesh = new THREE.Mesh(geometry, material);
            mesh.position.copy(position);
            mesh.rotation.x = Math.PI / 2;
            mesh.scale.set(trim, trim, trim);
            scene.add(mesh);

            const edges = new THREE.EdgesGeometry(geometry);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
            const line = new THREE.LineSegments(edges, lineMaterial);
            line.position.copy(position);
            line.rotation.x = Math.PI / 2;
            line.scale.set(trim, trim, trim);
            scene.add(line);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / (window.innerHeight - 50);
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - 50);
        });
    </script>
</body>
</html>
