            border: 1px solid #ccc;
        }
        #description-panel {
            padding: 10px;
            margin-top: 50px;
            background-color: #fff;
            border: 1px solid #ccc;
            border-radius: 5px;
        }
        #control-panel {
            padding: 10px;
            display: flex;
            flex-direction: column;
        }
        #control-panel > div {
            margin-bottom: 10px;
        }
        #function-buttons button, #encoding-buttons button {
            margin-right: 5px;
            padding: 5px 10px;
            cursor: pointer;
        }
        #function-buttons button.active, #encoding-buttons button.active {
            background-color: #ccc;
            border-style: inset;
        }
        #canvas-container {
            flex-grow: 1;
            display: flex;
            justify-content: center;
            align-items: center;
            border: 1px solid #ccc;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
</head>
<body>
    <button id="info-button">+</button>
    <div id="description-panel">
        <p>This demo visualizes analog signals and their encoded versions using different methods.</p>
    </div>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-start-time">start time</label>
            <input type="range" id="slider-start-time" value="-1.0" min="-5" max="5" step="0.1">
            <label for="slider-total-time">total time</label>
            <input type="range" id="slider-total-time" value="2.2" min="1" max="5" step="0.1">
            <label>function</label>
            <div id="function-buttons">
                <button id="btn-f">f</button>
                <button id="btn-g" class="active">g</button>
                <button id="btn-sin">sin</button>
                <button id="btn-cos">cos</button>
                <button id="btn-tan">tan</button>
            </div>
            <label>encoding method</label>
            <div id="encoding-buttons">
                <button id="btn-asdm" class="active">ASDM</button>
                <button id="btn-iaf">IAF</button>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let startTime = -1.0;
        let totalTime = 2.2;
        let functionType = 'g';
        let encodingMethod = 'ASDM';

        const functions = {
            f: t => 0.5 * t,
            g: t => Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(3 * 2 * Math.PI * t),
            sin: t => Math.sin(2 * Math.PI * t),
            cos: t => Math.cos(2 * Math.PI * t),
            tan: t => Math.tan(Math.PI * t)
        };

        const ASDM = (analogFunction, startTime, endTime, dt) => {
            const flips = [];
            let currentTime = startTime;
            let outputLevel = 0.25;
            let integral = 0;
            const B = 0.25;
            const delta = 0.05;

            while (currentTime <= endTime) {
                integral += (analogFunction(currentTime) - outputLevel) * dt;
                if ((outputLevel === B && integral < -delta) || (outputLevel === -B && integral > delta)) {
                    flips.push(currentTime);
                    outputLevel = outputLevel === B ? -B : B;
                    integral = 0;
                }
                currentTime += dt;
            }

            return flips;
        };

        const IAF = (analogFunction, startTime, endTime, dt) => {
            const spikes = [];
            let currentTime = startTime;
            let integral = 0;
            const theta = 0.05;
            const y_spike = 0.3;

            while (currentTime <= endTime) {
                integral += analogFunction(currentTime) * dt;
                if (integral >= theta) {
                    spikes.push(currentTime);
                    integral = 0;
                }
                currentTime += dt;
            }

            return spikes;
        };

        let myp5;

        function setup() {
            myp5 = createCanvas(800, 600).parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            stroke(0);
            line(width / 2, 0, width / 2, height);
            line(0, height / 2, width, height / 2);

            const xAxisMin = map(startTime, startTime, startTime + totalTime, 0, width);
            const xAxisMax = map(startTime + totalTime, startTime, startTime + totalTime, 0, width);
            const yAxisMin = map(-1.2, -1.2, 1.2, height, 0);
            const yAxisMax = map(1.2, -1.2, 1.2, height, 0);

            stroke(0, 0, 255);
            beginShape();
            for (let t = startTime; t <= startTime + totalTime; t += 0.01) {
                const x = map(t, startTime, startTime + totalTime, 0, width);
                const y = map(functions[functionType](t), -1.2, 1.2, height, 0);
                vertex(x, y);
            }
            endShape();

            stroke(255, 0, 0);
            if (encodingMethod === 'ASDM') {
                const flips = ASDM(functions[functionType], startTime, startTime + totalTime, 0.01);
                for (let i = 0; i < flips.length; i++) {
                    const x1 = map(flips[i], startTime, startTime + totalTime, 0, width);
                    const x2 = i < flips.length - 1 ? map(flips[i + 1], startTime, startTime + totalTime, 0, width) : width;
                    line(x1, yAxisMin, x1, yAxisMax);
                    line(x1, yAxisMin, x2, yAxisMin);
                }
            } else if (encodingMethod === 'IAF') {
                const spikes = IAF(functions[functionType], startTime, startTime + totalTime, 0.01);
                for (let t of spikes) {
                    const x = map(t, startTime, startTime + totalTime, 0, width);
                    line(x, 0, x, height / 2);
                    fill(255, 0, 0);
                    noStroke();
                    ellipse(x, height / 2, 5, 5);
                    stroke(255, 0, 0);
                }
            }
        }

        function redrawPlot() {
            redraw();
        }

        document.getElementById('info-button').addEventListener('click', () => {
            const panel = document.getElementById('description-panel');
            if (panel.style.display === 'none') {
                panel.style.display = 'block';
            } else {
                panel.style.display = 'none';
            }
        });

        document.getElementById('slider-start-time').addEventListener('input', (e) => {
            startTime = parseFloat(e.target.value);
            redrawPlot();
        });

        document.getElementById('slider-total-time').addEventListener('input', (e) => {
            totalTime = parseFloat(e.target.value);
            redrawPlot();
        });

        const functionButtons = document.querySelectorAll('#function-buttons button');
        functionButtons.forEach(button => {
            button.addEventListener('click', () => {
                functionButtons.forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                functionType = button.id.replace('btn-', '');
                redrawPlot();
            });
        });

        const encodingButtons = document.querySelectorAll('#encoding-buttons button');
        encodingButtons.forEach(button => {
            button.addEventListener('click', () => {
                encodingButtons.forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                encodingMethod = button.id.replace('btn-', '');
                redrawPlot();
            });
        });

        window.onload = () => {
            setup();
            draw();
        };
    </script>
</body>
</html>
