            height: 100%;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #FFFFFF;
            border-radius: 8px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        fieldset {
            border: none;
            margin-bottom: 20px;
        }
        legend {
            font-weight: bold;
            margin-bottom: 10px;
        }
        input[type="range"] {
            width: 100%;
            margin-bottom: 10px;
        }
        button {
            padding: 5px 10px;
            margin-right: 5px;
            cursor: pointer;
            border: none;
            border-radius: 4px;
            background-color: #007BFF;
            color: white;
        }
        button.active {
            background-color: #0056b3;
        }
        #visualization-container {
            flex-grow: 1;
            position: relative;
        }
        canvas {
            width: 100%;
            height: 100%;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <fieldset>
                <legend>time</legend>
                <input type="range" id="time-slider" min="0" max="20" step="0.1" value="0">
                <div id="play-controls">
                    <button id="reset-button">«</button>
                    <button id="play-pause-button">►</button>
                    <button id="step-button">»</button>
                </div>
            </fieldset>
            <fieldset>
                <legend>plot points</legend>
                <input type="range" id="plot-points-slider" min="10" max="80" step="1" value="40">
            </fieldset>
            <fieldset>
                <legend>pressure wave</legend>
                <div id="p-wave-type-selector">
                    <button id="p-wave-sine-button" class="active"><svg viewBox="0 0 40 30"><path d="M0,15 Q 10,0 20,15 T 40,15" stroke="blue" fill="none"/></svg></button>
                    <button id="p-wave-pulse-button"><svg viewBox="0 0 40 30"><path d="M0,15 Q 20,0 40,15" stroke="blue" fill="none"/></svg></button>
                </div>
                <label for="p-wave-amplitude-slider">amplitude</label>
                <input type="range" id="p-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
                <label for="p-wave-wavelength-slider">wavelength</label>
                <input type="range" id="p-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.0">
            </fieldset>
            <fieldset>
                <legend>shear wave</legend>
                <div id="s-wave-type-selector">
                    <button id="s-wave-sine-button" class="active"><svg viewBox="0 0 40 30"><path d="M0,15 Q 10,0 20,15 T 40,15" stroke="blue" fill="none"/></svg></button>
                    <button id="s-wave-pulse-button"><svg viewBox="0 0 40 30"><path d="M0,15 Q 20,0 40,15" stroke="blue" fill="none"/></svg></button>
                </div>
                <label for="s-wave-amplitude-slider">amplitude</label>
                <input type="range" id="s-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
                <label for="s-wave-wavelength-slider">wavelength</label>
                <input type="range" id="s-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.5">
                <label>direction</label>
                <div id="s-wave-direction-selector">
                    <button id="s-wave-dir-xy-button" class="active">x-y</button>
                    <button id="s-wave-dir-z-button">z</button>
                </div>
            </fieldset>
            <fieldset>
                <legend>viewpoint</legend>
                <div id="viewpoint-selector">
                    <button id="viewpoint-top-button" class="active">Top</button>
                    <button id="viewpoint-default-button">Default</button>
                </div>
            </fieldset>
        </div>
        <div id="visualization-container"></div>
    </div>

    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let time = 0;
        let isPlaying = false;
        let plotPoints = 40;
        let pWaveType = 'sine';
        let sWaveType = 'sine';
        let sWaveDirection = 'xy';

        const geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints - 1, plotPoints - 1);
        const colors = [];

        for (let i = 0; i < geometry.attributes.position.count; i++) {
            colors.push(0, 0, 1); // Default blue color
        }

        geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

        const material = new THREE.MeshLambertMaterial({ vertexColors: true });
        const mesh = new THREE.Mesh(geometry, material);
        scene.add(mesh);

        const wireframe = new THREE.WireframeGeometry(geometry);
        const line = new THREE.LineSegments(wireframe, new THREE.LineBasicMaterial({ color: 0x556B2F }));
        scene.add(line);

        camera.position.z = 15;

        function waveFunctionSine(u) {
            return Math.sin(2 * Math.PI * u);
        }

        function waveFunctionGaussian(u) {
            return Math.exp(-u * u);
        }

        function updateVertices() {
            const positions = geometry.attributes.position.array;
            const colors = geometry.attributes.color.array;

            for (let i = 0; i < positions.length; i += 3) {
                const x = positions[i];
                const y = positions[i + 1];

                const r = Math.sqrt(x * x + y * y);
                let pDisplacement = 0;
                let sDisplacement = 0;

                if (r > 0) {
                    const pU = (r / parseFloat(document.getElementById('p-wave-wavelength-slider').value)) - time;
                    const sU = (r / parseFloat(document.getElementById('s-wave-wavelength-slider').value)) - time;

                    if (pWaveType === 'sine') {
                        pDisplacement = parseFloat(document.getElementById('p-wave-amplitude-slider').value) * waveFunctionSine(pU);
                    } else {
                        pDisplacement = parseFloat(document.getElementById('p-wave-amplitude-slider').value) * waveFunctionGaussian(pU);
                    }

                    if (sWaveType === 'sine') {
                        sDisplacement = parseFloat(document.getElementById('s-wave-amplitude-slider').value) * waveFunctionSine(sU);
                    } else {
                        sDisplacement = parseFloat(document.getElementById('s-wave-amplitude-slider').value) * waveFunctionGaussian(sU);
                    }

                    if (sWaveDirection === 'xy') {
                        positions[i] += -y * sDisplacement / r;
                        positions[i + 1] += x * sDisplacement / r;
                    } else {
                        positions[i + 2] += sDisplacement;
                    }

                    const z = positions[i + 2] + pDisplacement;
                    const colorValue = Math.max(0, Math.min(1, (z + 1) / 2));
                    colors[i] = colorValue;
                    colors[i + 1] = 1 - colorValue;
                    colors[i + 2] = 1 - colorValue;
                }
            }

            geometry.attributes.position.needsUpdate = true;
            geometry.attributes.color.needsUpdate = true;
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.01;
                document.getElementById('time-slider').value = time;
            }

            updateVertices();
            renderer.render(scene, camera);
        }

        document.getElementById('play-pause-button').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('play-pause-button').textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', () => {
            time = 0;
            isPlaying = false;
            document.getElementById('play-pause-button').textContent = '►';
            document.getElementById('time-slider').value = time;
            updateVertices();
        });

        document.getElementById('step-button').addEventListener('click', () => {
            time += 0.1;
            document.getElementById('time-slider').value = time;
            updateVertices();
        });

        document.getElementById('time-slider').addEventListener('input', () => {
            time = parseFloat(document.getElementById('time-slider').value);
            updateVertices();
        });

        document.getElementById('plot-points-slider').addEventListener('input', () => {
            plotPoints = parseInt(document.getElementById('plot-points-slider').value);
            geometry.dispose();
            const newGeometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints - 1, plotPoints - 1);
            mesh.geometry = newGeometry;
            updateVertices();
        });

        document.getElementById('p-wave-sine-button').addEventListener('click', () => {
            pWaveType = 'sine';
            document.getElementById('p-wave-sine-button').classList.add('active');
            document.getElementById('p-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', () => {
            pWaveType = 'pulse';
            document.getElementById('p-wave-sine-button').classList.remove('active');
            document.getElementById('p-wave-pulse-button').classList.add('active');
        });

        document.getElementById('s-wave-sine-button').addEventListener('click', () => {
            sWaveType = 'sine';
            document.getElementById('s-wave-sine-button').classList.add('active');
            document.getElementById('s-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', () => {
            sWaveType = 'pulse';
            document.getElementById('s-wave-sine-button').classList.remove('active');
            document.getElementById('s-wave-pulse-button').classList.add('active');
        });

        document.getElementById('s-wave-dir-xy-button').addEventListener('click', () => {
            sWaveDirection = 'xy';
            document.getElementById('s-wave-dir-xy-button').classList.add('active');
            document.getElementById('s-wave-dir-z-button').classList.remove('active');
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', () => {
            sWaveDirection = 'z';
            document.getElementById('s-wave-dir-xy-button').classList.remove('active');
            document.getElementById('s-wave-dir-z-button').classList.add('active');
        });

        document.getElementById('viewpoint-top-button').addEventListener('click', () => {
            camera.type = 'OrthographicCamera';
            camera.left = -10;
            camera.right = 10;
            camera.top = 10;
            camera.bottom = -10;
            camera.near = -10;
            camera.far = 10;
            camera.position.set(0, 20, 0);
            camera.lookAt(new THREE.Vector3(0, 0, 0));
            camera.updateProjectionMatrix();
            document.getElementById('viewpoint-top-button').classList.add('active');
            document.getElementById('viewpoint-default-button').classList.remove('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', () => {
            camera.type = 'PerspectiveCamera';
            camera.position.set(10, 10, 10);
            camera.lookAt(new THREE.Vector3(0, 0, 0));
            document.getElementById('viewpoint-top-button').classList.remove('active');
            document.getElementById('viewpoint-default-button').classList.add('active');
        });

        animate();
    </script>
</body>
</html>
