<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Lill's Method Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            background-color: #f0f0f0;
        }
        #demo-container {
            display: flex;
            width: 80%;
            height: 80%;
        }
        #control-panel {
            width: 25%;
            padding: 20px;
            background-color: #fff;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        #visualization-area {
            width: 75%;
            display: flex;
            flex-direction: column;
        }
        #canvas-container {
            flex-grow: 1;
            background-color: #ddd;
            border-radius: 5px;
            overflow: hidden;
        }
        #formula-display {
            padding: 10px;
            text-align: center;
        }
        #results-table {
            width: 100%;
            border-collapse: collapse;
        }
        #results-table th, #results-table td {
            padding: 8px;
            text-align: center;
            border-bottom: 1px solid #ddd;
        }
    </style>
</head>
<body>
    <!-- Main container -->
    <div id="demo-container">

        <!-- Left Column: Control Panel -->
        <div id="control-panel">
            <!-- z slider -->
            <div>
                <label for="slider-z">z</label>
                <input type="range" id="slider-z" min="-5" max="5" step="0.01" value="1.5">
                <span id="label-z">1.5</span>
            </div>
            <!-- plot range slider -->
            <div>
                <label for="slider-plot-range">plot range</label>
                <input type="range" id="slider-plot-range" min="1" max="10" step="0.5" value="4">
                <span id="label-plot-range">4</span>
            </div>
            <!-- Checkboxes -->
            <div>
                <input type="checkbox" id="check-show-labels">
                <label for="check-show-labels">show labels</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-circle">
                <label for="check-show-circle">show circle</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-axes">
                <label for="check-show-axes">show axes</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-grid-lines">
                <label for="check-show-grid-lines">show grid lines</label>
            </div>
            <!-- Buttons -->
            <button id="btn-new-example">new example</button>
            <button id="btn-golden-ratio">golden ratio</button>
        </div>

        <!-- Right Column: Visualization -->
        <div id="visualization-area">
            <!-- p5.js canvas -->
            <div id="canvas-container"></div>

            <!-- Formula and Table -->
            <div id="formula-display">P(z) = <span id="formula-text">z^2 - z - 1</span></div>
            <table id="results-table">
                <thead>
                    <tr>
                        <th>P(z)</th>
                        <th>|L<sub>3</sub> L'<sub>2</sub>|</th>
                        <th>z</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="value-pz">-0.25</td>
                        <td id="value-l3l2">0.25</td>
                        <td id="value-z">1.5</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>

    <!-- MathJax CDN script -->
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <!-- p5.js CDN script -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        let a = 1, b = -1, c = -1;
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;

        function setup() {
            createCanvas(600, 400).parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);
            scale(1, -1);

            const scaleFactor = min(width / (2 * plotRange), height / (2 * plotRange));
            scale(scaleFactor);

            // Calculate points
            const O = { x: 0, y: 0 };
            const L1 = { x: a, y: 0 };
            const L2 = { x: a, y: b };
            const L3 = { x: a - c, y: b };
            const L1_prime = { x: a, y: -z * a };
            const L2_prime = { x: a + b * z + a * z * z, y: b };

            // Draw grid and axes
            if (showGridLines || showAxes) {
                stroke(200);
                for (let i = -plotRange; i <= plotRange; i++) {
                    if (i === 0 && !showAxes) continue;
                    line(i, -plotRange, i, plotRange);
                    line(-plotRange, i, plotRange, i);
                }
            }

            if (showAxes) {
                stroke(0);
                strokeWeight(2);
                line(-plotRange, 0, plotRange, 0);
                line(0, -plotRange, 0, plotRange);
            }

            // Draw coefficient path
            stroke(0);
            strokeWeight(1);
            fill(0);
            line(O.x, O.y, L1.x, L1.y);
            ellipse(L1.x, L1.y, 5, 5);
            line(L1.x, L1.y, L2.x, L2.y);
            ellipse(L2.x, L2.y, 5, 5);
            line(L2.x, L2.y, L3.x, L3.y);
            ellipse(L3.x, L3.y, 5, 5);

            // Draw test path
            stroke(0);
            line(O.x, O.y, L1_prime.x, L1_prime.y);
            ellipse(L1_prime.x, L1_prime.y, 5, 5);
            stroke(255, 0, 255);
            line(L1_prime.x, L1_prime.y, L2_prime.x, L2_prime.y);
            ellipse(L2_prime.x, L2_prime.y, 5, 5);
            stroke(255, 0, 0);
            line(L2_prime.x, L2_prime.y, L3.x, L3.y);

            // Draw circle
            if (showCircle) {
                const C = { x: (a - c) / 2, y: b / 2 };
                const d = sqrt((a - c) ** 2 + b ** 2);
                noFill();
                ellipse(C.x, C.y, d, d);
            }

            // Draw labels
            if (showLabels) {
                fill(0);
                textSize(12);
                textAlign(CENTER, BOTTOM);
                text("O", O.x, O.y);
                text("L1", L1.x, L1.y);
                text("L2", L2.x, L2.y);
                text("L3", L3.x, L3.y);
                text("L1'", L1_prime.x, L1_prime.y);
                text("L2'", L2_prime.x, L2_prime.y);
            }
        }

        function update() {
            const pz = a * z * z + b * z + c;
            const l3l2 = abs(L3.y - L2_prime.y);
            document.getElementById('value-pz').textContent = pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = l3l2.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(4);
            redraw();
        }

        function resetCoefficients(aVal, bVal, cVal) {
            a = aVal;
            b = bVal;
            c = cVal;
            document.getElementById('formula-text').textContent = `${a}z^2 + ${b}z + ${c}`;
            MathJax.typesetPromise([document.getElementById('formula-display')]);
            update();
        }

        document.getElementById('slider-z').addEventListener('input', (e) => {
            z = parseFloat(e.target.value);
            document.getElementById('label-z').textContent = z.toFixed(4);
            update();
        });

        document.getElementById('slider-plot-range').addEventListener('input', (e) => {
            plotRange = parseFloat(e.target.value);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);
            update();
        });

        document.getElementById('check-show-labels').addEventListener('change', (e) => {
            showLabels = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-circle').addEventListener('change', (e) => {
            showCircle = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-axes').addEventListener('change', (e) => {
            showAxes = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', (e) => {
            showGridLines = e.target.checked;
            redraw();
        });

        document.getElementById('btn-new-example').addEventListener('click', () => {
            const aVal = random([-3, -2, -1, 1, 2, 3]);
            const bVal = randomInt(-5, 5);
            const cVal = randomInt(-5, 5);
            resetCoefficients(aVal, bVal, cVal);
        });

        document.getElementById('btn-golden-ratio').addEventListener('click', () => {
            resetCoefficients(1, -1, -1);
        });
    </script>
</body>
</html>
