            display: flex;
            justify-content: space-between;
            align-items: center;
        }
        #view-switcher {
            display: flex;
            gap: 5px;
        }
        .view-switcher-button {
            padding: 5px 10px;
            cursor: pointer;
            border: 1px solid #ccc;
            border-radius: 3px;
            background-color: #fff;
        }
        .view-switcher-button.active {
            background-color: #ddd;
            border-style: inset;
        }
        #info-button-container {
            cursor: pointer;
        }
        #parameter-controls {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #slider-alpha {
            width: 300px;
        }
        #main-display {
            display: flex;
            justify-content: center;
            align-items: center;
        }
        #plot-view {
            width: 600px;
            height: 600px;
        }
        #table-view {
            width: 100%;
        }
        #solutions-table {
            width: 100%;
            border-collapse: collapse;
        }
        #solutions-table th, #solutions-table td {
            border: 1px solid #000;
            text-align: center;
            padding: 5px;
        }
        #solutions-table th {
            background-color: #f2f2f2;
        }
        #solutions-table tbody tr:nth-child(even) {
            background-color: #f9f9f9;
        }
        #solutions-table tbody tr:hover {
            background-color: #ddd;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <!-- Main container for the application -->
    <div id="app-container">

        <!-- Top-level controls for switching views -->
        <div id="header-controls">
            <div id="view-switcher">
                <button id="btn-plot" class="view-switcher-button active">T₂(α)</button>
                <button id="btn-table" class="view-switcher-button">solution list</button>
                <button id="btn-turning-points" class="view-switcher-button">turning points</button>
            </div>
            <div id="info-button-container">
                <button id="btn-info">+</button>
            </div>
        </div>

        <!-- Slider control for the alpha parameter -->
        <div id="parameter-controls">
            <label for="slider-alpha">α</label>
            <input type="range" id="slider-alpha" min="0.00" max="0.07" step="0.0001" value="0.039">
            <span id="alpha-value-display">0.039</span>
        </div>

        <!-- Main display area for plot or table -->
        <div id="main-display">
            <!-- p5.js canvas will be created here for the plots -->
            <div id="plot-view"></div>

            <!-- Table view for the solution list -->
            <div id="table-view" style="display: none;">
                <table id="solutions-table">
                    <thead>
                        <tr>
                            <th style="background-color: lightyellow;">root</th>
                            <th style="background-color: lightgreen;">X₁</th>
                            <th style="background-color: lightblue;">X₂</th>
                            <th style="background-color: magenta;">T₁</th>
                            <th style="background-color: lightcoral;">T₂</th>
                        </tr>
                    </thead>
                    <tbody id="solutions-table-body">
                        <!-- Rows will be dynamically generated by JavaScript -->
                    </tbody>
                </table>
            </div>
        </div>

    </div>

    <script>
        const solutionCurveData = [
            { alpha: 0.039, x1: 0.1, x2: 0.2, t1: 1.1, t2: 2.1 },
            { alpha: 0.040, x1: 0.11, x2: 0.21, t1: 1.2, t2: 2.2 },
            // Add more data points as needed
        ];

        const turningPointsData = [
            { alpha: 0.035, t2: 1.8 },
            { alpha: 0.045, t2: 2.8 },
            // Add more turning points as needed
        ];

        let currentView = 'plot_solutions';
        let currentAlpha = 0.039;

        function setup() {
            createCanvas(600, 600).parent('plot-view');
            noLoop();
        }

        function draw() {
            background(255);
            drawAxes();
            drawSolutionCurve();

            if (currentView === 'plot_solutions') {
                drawVerticalLine(currentAlpha);
                drawIntersectionPoints(currentAlpha);
            } else if (currentView === 'plot_turning_points') {
                drawTurningPoints();
            }
        }

        function drawAxes() {
            stroke(0);
            line(50, height - 50, width - 50, height - 50); // X-axis
            line(50, height - 50, 50, 50); // Y-axis

            textSize(12);
            fill(0);
            textAlign(CENTER, TOP);
            text('α', width - 30, height - 30);
            textAlign(RIGHT, CENTER);
            text('T₂(α)', 30, 60);

            // X-axis ticks and labels
            for (let i = 0; i <= 7; i++) {
                let x = map(i * 0.01, 0, 0.07, 50, width - 50);
                line(x, height - 50, x, height - 45);
                if (i % 2 === 0) {
                    textAlign(CENTER, TOP);
                    text(i * 0.01.toFixed(2), x, height - 35);
                }
            }

            // Y-axis ticks and labels
            for (let i = 0; i <= 8; i++) {
                let y = map(i, 0, 8, height - 50, 50);
                line(50, y, 55, y);
                if (i % 2 === 0) {
                    textAlign(RIGHT, CENTER);
                    text(i.toFixed(1), 45, y);
                }
            }
        }

        function drawSolutionCurve() {
            stroke(255, 0, 0);
            noFill();
            strokeWeight(2);
            beginShape();
            for (let point of solutionCurveData) {
                let x = map(point.alpha, 0, 0.07, 50, width - 50);
                let y = map(point.t2, 0, 8, height - 50, 50);
                vertex(x, y);
            }
            endShape();
        }

        function drawVerticalLine(alpha) {
            stroke(0, 255, 0);
            strokeWeight(1);
            dashLine(map(alpha, 0, 0.07, 50, width - 50), 50, map(alpha, 0, 0.07, 50, width - 50), height - 50);
        }

        function dashLine(x1, y1, x2, y2) {
            let d = dist(x1, y1, x2, y2);
            let dashLength = 5;
            let numDashes = floor(d / dashLength);
            let dashSpacing = d / numDashes;
            for (let i = 0; i < numDashes; i++) {
                let t = i / numDashes;
                let px = lerp(x1, x2, t);
                let py = lerp(y1, y2, t);
                let nx = lerp(x1, x2, t + dashSpacing / d);
                let ny = lerp(y1, y2, t + dashSpacing / d);
                line(px, py, nx, ny);
            }
        }

        function drawIntersectionPoints(alpha) {
            let intersections = [];
            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                let p1 = solutionCurveData[i];
                let p2 = solutionCurveData[i + 1];
                if ((p1.alpha < alpha && p2.alpha > alpha) || (p1.alpha > alpha && p2.alpha < alpha)) {
                    let t = (alpha - p1.alpha) / (p2.alpha - p1.alpha);
                    let x1 = lerp(p1.x1, p2.x1, t);
                    let x2 = lerp(p1.x2, p2.x2, t);
                    let t1 = lerp(p1.t1, p2.t1, t);
                    let t2 = lerp(p1.t2, p2.t2, t);
                    intersections.push({ x1, x2, t1, t2 });
                }
            }

            intersections.forEach((intersection, index) => {
                fill(0, 0, 255);
                noStroke();
                ellipse(map(alpha, 0, 0.07, 50, width - 50), map(intersection.t2, 0, 8, height - 50, 50), 8, 8);
                fill(0);
                textSize(12);
                textAlign(CENTER, BOTTOM);
                text(index + 1, map(alpha, 0, 0.07, 50, width - 50), map(intersection.t2, 0, 8, height - 50, 50));
            });
        }

        function drawTurningPoints() {
            turningPointsData.forEach((point, index) => {
                fill(0, 255, 0);
                noStroke();
                ellipse(map(point.alpha, 0, 0.07, 50, width - 50), map(point.t2, 0, 8, height - 50, 50), 8, 8);
                fill(0);
                textSize(12);
                textAlign(CENTER, BOTTOM);
                text(index + 1, map(point.alpha, 0, 0.07, 50, width - 50), map(point.t2, 0, 8, height - 50, 50));
            });
        }

        document.getElementById('btn-plot').addEventListener('click', () => {
            currentView = 'plot_solutions';
            updateView();
        });

        document.getElementById('btn-table').addEventListener('click', () => {
            currentView = 'table';
            updateView();
        });

        document.getElementById('btn-turning-points').addEventListener('click', () => {
            currentView = 'plot_turning_points';
            updateView();
        });

        document.getElementById('slider-alpha').addEventListener('input', (event) => {
            currentAlpha = parseFloat(event.target.value);
            document.getElementById('alpha-value-display').textContent = currentAlpha.toFixed(3);
            if (currentView === 'plot_solutions') {
                redraw();
            } else if (currentView === 'table') {
                updateTable();
            }
        });

        function updateView() {
            document.querySelectorAll('.view-switcher-button').forEach(button => button.classList.remove('active'));
            document.getElementById(`btn-${currentView.replace('_', '-')}`).classList.add('active');

            document.getElementById('plot-view').style.display = currentView === 'plot_solutions' || currentView === 'plot_turning_points' ? 'block' : 'none';
            document.getElementById('table-view').style.display = currentView === 'table' ? 'block' : 'none';
            document.getElementById('parameter-controls').style.display = currentView !== 'plot_turning_points' ? 'flex' : 'none';

            if (currentView === 'plot_solutions' || currentView === 'plot_turning_points') {
                redraw();
            } else if (currentView === 'table') {
                updateTable();
            }
        }

        function updateTable() {
            const tableBody = document.getElementById('solutions-table-body');
            tableBody.innerHTML = '';

            let intersections = [];
            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                let p1 = solutionCurveData[i];
                let p2 = solutionCurveData[i + 1];
                if ((p1.alpha < currentAlpha && p2.alpha > currentAlpha) || (p1.alpha > currentAlpha && p2.alpha < currentAlpha)) {
                    let t = (currentAlpha - p1.alpha) / (p2.alpha - p1.alpha);
                    let x1 = lerp(p1.x1, p2.x1, t);
                    let x2 = lerp(p1.x2, p2.x2, t);
                    let t1 = lerp(p1.t1, p2.t1, t);
                    let t2 = lerp(p1.t2, p2.t2, t);
                    intersections.push({ x1, x2, t1, t2 });
                }
            }

            intersections.forEach((intersection, index) => {
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${index + 1}</td>
                    <td>${intersection.x1.toFixed(4)}</td>
                    <td>${intersection.x2.toFixed(4)}</td>
                    <td>${intersection.t1.toFixed(4)}</td>
                    <td>${intersection.t2.toFixed(4)}</td>
                `;
                tableBody.appendChild(row);
            });
        }

        updateView();
    </script>
</body>
</html>
