<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Logarithmic Spiral Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 100vw;
            height: 100vh;
        }
        #control-panel {
            width: 80%;
            margin-top: 20px;
        }
        #controls-row-1, #controls-row-2 {
            display: flex;
            justify-content: space-between;
            margin-bottom: 10px;
        }
        #type-controls, #item-controls {
            display: flex;
            align-items: center;
        }
        button {
            margin-left: 10px;
            padding: 5px 10px;
            cursor: pointer;
        }
        .active {
            background-color: #ddd;
        }
        #growth-slider-container {
            display: flex;
            align-items: center;
        }
        #slider-growth {
            width: 100%;
            margin: 0 10px;
        }
        #canvas-container {
            width: 80%;
            height: calc(100% - 200px);
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="controls-row-1">
                <div id="type-controls">
                    <span>type</span>
                    <button id="btn-type-single">single</button>
                    <button id="btn-type-double" class="active">double</button>
                </div>
                <div id="item-controls">
                    <span>item</span>
                    <button id="btn-item-none">none</button>
                    <button id="btn-item-point" class="active">point</button>
                    <button id="btn-item-fox">fox</button>
                    <button id="btn-item-filled-fox">filled fox</button>
                </div>
            </div>
            <div id="controls-row-2">
                <div id="growth-slider-container">
                    <label for="slider-growth">spiral growth</label>
                    <input type="range" id="slider-growth" min="0.01" max="0.5" step="0.0005" value="0.15">
                    <span id="display-growth">0.15</span>
                    <button id="btn-reset-growth">+</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };

        const fox_vertices = [
            { x: -15, y: 0 }, { x: -10, y: -5 }, { x: -5, y: -4 }, { x: 0, y: -5 },
            { x: 5, y: -5 }, { x: 8, y: -2 }, { x: 10, y: -6 }, { x: 12, y: -6 },
            { x: 15, y: 0 }, { x: 12, y: 6 }, { x: 10, y: 6 }, { x: 8, y: 2 },
            { x: 5, y: 5 }, { x: 0, y: 5 }, { x: -5, y: 4 }, { x: -10, y: 5 }
        ];

        let dragging_z0 = false;
        let dragging_z_plus = false;
        let dragging_z_minus = false;

        function setup() {
            createCanvas(windowWidth * 0.8, windowHeight * 0.75).parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);

            if (spiralType === 'single') {
                drawSingleSpiral();
            } else {
                drawDoubleSpiral();
            }

            drawLocators();
        }

        function drawSingleSpiral() {
            stroke('#4A6DA8');
            strokeWeight(2);
            beginShape();
            for (let t = -8 * PI; t <= 8 * PI; t += 0.01) {
                let x = z0_loc.x + exp(spiralGrowth * t) * cos(t);
                let y = z0_loc.y - exp(spiralGrowth * t) * sin(t);
                vertex(x, y);
            }
            endShape();

            if (itemType === 'point') {
                drawPointsOnCurve(-8 * PI, 8 * PI);
            } else if (itemType === 'fox' || itemType === 'filled-fox') {
                drawFoxesOnCurve(-8 * PI, 8 * PI);
            }
        }

        function drawDoubleSpiral() {
            stroke('#4A6DA8');
            strokeWeight(2);
            let epsilon = 0.01;

            // First branch
            beginShape();
            for (let t = -8 * PI; t >= -epsilon; t -= 0.01) {
                let w = exp((spiralGrowth + 1j) * t);
                let z = (z_plus_loc.x + z_plus_loc.y * 1j * w - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1);
                vertex(z.re, -z.im);
            }
            endShape();

            // Second branch
            beginShape();
            for (let t = epsilon; t <= 8 * PI; t += 0.01) {
                let w = exp((spiralGrowth + 1j) * t);
                let z = (z_plus_loc.x + z_plus_loc.y * 1j * w - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1);
                vertex(z.re, -z.im);
            }
            endShape();

            if (itemType === 'point') {
                drawPointsOnCurve(-8 * PI, -epsilon);
                drawPointsOnCurve(epsilon, 8 * PI);
            } else if (itemType === 'fox' || itemType === 'filled-fox') {
                drawFoxesOnCurve(-8 * PI, -epsilon);
                drawFoxesOnCurve(epsilon, 8 * PI);
            }

            fill(0);
            textSize(12);
            textAlign(CENTER, CENTER);
            text("z0Loc", (z_plus_loc.x + z_minus_loc.x) / 2, -(z_plus_loc.y + z_minus_loc.y) / 2);
        }

        function drawPointsOnCurve(start, end) {
            fill('#CB3335');
            noStroke();
            for (let t = start; t <= end; t += 0.5) {
                let x, y;
                if (spiralType === 'single') {
                    x = z0_loc.x + exp(spiralGrowth * t) * cos(t);
                    y = z0_loc.y - exp(spiralGrowth * t) * sin(t);
                } else {
                    let w = exp((spiralGrowth + 1j) * t);
                    let z = (z_plus_loc.x + z_plus_loc.y * 1j * w - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1);
                    x = z.re;
                    y = -z.im;
                }
                ellipse(x, y, 5, 5);
            }
        }

        function drawFoxesOnCurve(start, end) {
            for (let t = start; t <= end; t += 1) {
                let x, y;
                if (spiralType === 'single') {
                    x = z0_loc.x + exp(spiralGrowth * t) * cos(t);
                    y = z0_loc.y - exp(spiralGrowth * t) * sin(t);
                } else {
                    let w = exp((spiralGrowth + 1j) * t);
                    let z = (z_plus_loc.x + z_plus_loc.y * 1j * w - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1);
                    x = z.re;
                    y = -z.im;
                }

                let dx, dy;
                if (spiralType === 'single') {
                    dx = -exp(spiralGrowth * t) * sin(t);
                    dy = -exp(spiralGrowth * t) * cos(t);
                } else {
                    let dw = (spiralGrowth + 1j) * exp((spiralGrowth + 1j) * t);
                    let dz = ((z_plus_loc.x + z_plus_loc.y * 1j * dw - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1)) -
                             ((z_plus_loc.x + z_plus_loc.y * 1j * w - z_minus_loc.x - z_minus_loc.y * 1j) / (w - 1) ** 2);
                    dx = dz.re;
                    dy = -dz.im;
                }

                let angle = atan2(dy, dx);
                push();
                translate(x, y);
                rotate(angle);
                scale(1, 1);
                if (itemType === 'fox') {
                    noFill();
                    stroke('#D6292D');
                    strokeWeight(1);
                } else {
                    fill('#D6292D');
                    noStroke();
                }
                beginShape();
                for (let v of fox_vertices) {
                    vertex(v.x, v.y);
                }
                endShape(CLOSE);
                pop();
            }
        }

        function drawLocators() {
            fill(255, 255, 0, 200);
            stroke(0);
            strokeWeight(2);
            ellipse(z0_loc.x, z0_loc.y, 20, 20);
            ellipse(z_plus_loc.x, z_plus_loc.y, 20, 20);

            fill(0, 255, 0, 200);
            ellipse(z_minus_loc.x, z_minus_loc.y, 20, 20);

            strokeWeight(1);
            line(z0_loc.x - 10, z0_loc.y, z0_loc.x + 10, z0_loc.y);
            line(z0_loc.x, z0_loc.y - 10, z0_loc.x, z0_loc.y + 10);
            line(z_plus_loc.x - 10, z_plus_loc.y, z_plus_loc.x + 10, z_plus_loc.y);
            line(z_plus_loc.x, z_plus_loc.y - 10, z_plus_loc.x, z_plus_loc.y + 10);
            line(z_minus_loc.x - 10, z_minus_loc.y, z_minus_loc.x + 10, z_minus_loc.y);
            line(z_minus_loc.x, z_minus_loc.y - 10, z_minus_loc.x, z_minus_loc.y + 10);
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, z0_loc.x, z0_loc.y) < 10) {
                dragging_z0 = true;
            } else if (dist(mouseX, mouseY, z_plus_loc.x, z_plus_loc.y) < 10) {
                dragging_z_plus = true;
            } else if (dist(mouseX, mouseY, z_minus_loc.x, z_minus_loc.y) < 10) {
                dragging_z_minus = true;
            }
        }

        function mouseDragged() {
            if (dragging_z0) {
                z0_loc.x = mouseX - width / 2;
                z0_loc.y = mouseY - height / 2;
            } else if (dragging_z_plus) {
                z_plus_loc.x = mouseX - width / 2;
                z_plus_loc.y = mouseY - height / 2;
            } else if (dragging_z_minus) {
                z_minus_loc.x = mouseX - width / 2;
                z_minus_loc.y = mouseY - height / 2;
            }
            redraw();
        }

        function mouseReleased() {
            dragging_z0 = false;
            dragging_z_plus = false;
            dragging_z_minus = false;
        }

        document.getElementById('btn-type-single').addEventListener('click', () => {
            document.getElementById('btn-type-single').classList.add('active');
            document.getElementById('btn-type-double').classList.remove('active');
            spiralType = 'single';
            redraw();
        });

        document.getElementById('btn-type-double').addEventListener('click', () => {
            document.getElementById('btn-type-single').classList.remove('active');
            document.getElementById('btn-type-double').classList.add('active');
            spiralType = 'double';
            redraw();
        });

        document.getElementById('btn-item-none').addEventListener('click', () => {
            document.getElementById('btn-item-none').classList.add('active');
            document.getElementById('btn-item-point').classList.remove('active');
            document.getElementById('btn-item-fox').classList.remove('active');
            document.getElementById('btn-item-filled-fox').classList.remove('active');
            itemType = 'none';
            redraw();
        });

        document.getElementById('btn-item-point').addEventListener('click', () => {
            document.getElementById('btn-item-none').classList.remove('active');
            document.getElementById('btn-item-point').classList.add('active');
            document.getElementById('btn-item-fox').classList.remove('active');
            document.getElementById('btn-item-filled-fox').classList.remove('active');
            itemType = 'point';
            redraw();
        });

        document.getElementById('btn-item-fox').addEventListener('click', () => {
            document.getElementById('btn-item-none').classList.remove('active');
            document.getElementById('btn-item-point').classList.remove('active');
            document.getElementById('btn-item-fox').classList.add('active');
            document.getElementById('btn-item-filled-fox').classList.remove('active');
            itemType = 'fox';
            redraw();
        });

        document.getElementById('btn-item-filled-fox').addEventListener('click', () => {
            document.getElementById('btn-item-none').classList.remove('active');
            document.getElementById('btn-item-point').classList.remove('active');
            document.getElementById('btn-item-fox').classList.remove('active');
            document.getElementById('btn-item-filled-fox').classList.add('active');
            itemType = 'filled-fox';
            redraw();
        });

        document.getElementById('slider-growth').addEventListener('input', (event) => {
            spiralGrowth = parseFloat(event.target.value);
            document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
            redraw();
        });

        document.getElementById('btn-reset-growth').addEventListener('click', () => {
            spiralGrowth = 0.15;
            document.getElementById('slider-growth').value = spiralGrowth;
            document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
            redraw();
        });
    </script>
</body>
</html>
