<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Dijkstra's Algorithm Visualization</title>
    <style>
        body {
            display: flex;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 200px;
            padding: 20px;
            background-color: #f4f4f4;
            display: flex;
            flex-direction: column;
        }
        #viz-container {
            flex-grow: 1;
            padding: 20px;
        }
        .vertex-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 10px;
        }
        .vertex-grid button {
            padding: 10px;
            cursor: pointer;
        }
        .vertex-grid button.selected {
            background-color: #333;
            color: white;
        }
        #canvas-container {
            width: 100%;
            height: 600px;
            position: relative;
        }
        canvas {
            border: 1px solid #000;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="control-panel">
        <button id="start-over-btn">start over</button>
        <div>
            <label>initial vertex</label>
            <div class="vertex-grid">
                <button id="btn-m" class="selected">m</button>
                <button id="btn-n">n</button>
                <button id="btn-o">o</button>
                <button id="btn-p">p</button>
                <button id="btn-i">i</button>
                <button id="btn-j">j</button>
                <button id="btn-k">k</button>
                <button id="btn-l">l</button>
                <button id="btn-e">e</button>
                <button id="btn-f">f</button>
                <button id="btn-g">g</button>
                <button id="btn-h">h</button>
                <button id="btn-a">a</button>
                <button id="btn-b">b</button>
                <button id="btn-c">c</button>
                <button id="btn-d">d</button>
            </div>
        </div>
        <div>
            <label>example type</label>
            <button id="fixed-example-btn">fixed example</button>
            <button id="random-example-btn">random example</button>
        </div>
        <div>
            <label>implement algorithm</label>
            <button id="next-step-btn">next step</button>
        </div>
    </div>
    <div id="viz-container">
        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container"></div>
    </div>

    <script>
        let canvas;
        let vertices = 'mnopijklabcdefgh';
        let vertexPositions = {
            m: { x: 100, y: 100 },
            n: { x: 200, y: 100 },
            o: { x: 300, y: 100 },
            p: { x: 400, y: 100 },
            i: { x: 100, y: 200 },
            j: { x: 200, y: 200 },
            k: { x: 300, y: 200 },
            l: { x: 400, y: 200 },
            e: { x: 100, y: 300 },
            f: { x: 200, y: 300 },
            g: { x: 300, y: 300 },
            h: { x: 400, y: 300 },
            a: { x: 100, y: 400 },
            b: { x: 200, y: 400 },
            c: { x: 300, y: 400 },
            d: { x: 400, y: 400 }
        };

        let edges = [
            ['m', 'n'], ['n', 'o'], ['o', 'p'],
            ['i', 'j'], ['j', 'k'], ['k', 'l'],
            ['e', 'f'], ['f', 'g'], ['g', 'h'],
            ['a', 'b'], ['b', 'c'], ['c', 'd'],
            ['m', 'i'], ['i', 'e'], ['e', 'a'],
            ['n', 'j'], ['j', 'f'], ['f', 'b'],
            ['o', 'k'], ['k', 'g'], ['g', 'c'],
            ['p', 'l'], ['l', 'h'], ['h', 'd']
        ];

        let weights = {
            'mn': 8, 'no': 7, 'op': 7,
            'ij': 9, 'jk': 7, 'kl': 1,
            'ef': 1, 'fg': 7, 'gh': 6,
            'ab': 10, 'bc': 4, 'cd': 8,
            'mi': 6, 'ie': 9, 'ea': 9,
            'nj': 7, 'jf': 6, 'fb': 5,
            'ok': 1, 'kg': 8, 'gc': 8,
            'pl': 2, 'lh': 1, 'hd': 7
        };

        let startVertex = 'm';
        let distances = {};
        let visited = new Set();
        let shortestPathTree = {};

        function setup() {
            canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();

            vertices.forEach(v => distances[v] = Infinity);
            distances[startVertex] = 0;

            document.getElementById('btn-' + startVertex).classList.add('selected');

            redraw();
        }

        function draw() {
            clear();
            stroke(0);
            strokeWeight(1);

            // Draw edges
            edges.forEach(([v1, v2]) => {
                let pos1 = vertexPositions[v1];
                let pos2 = vertexPositions[v2];
                line(pos1.x, pos1.y, pos2.x, pos2.y);
            });

            // Draw vertices
            vertices.forEach(v => {
                let pos = vertexPositions[v];
                fill(0);
                ellipse(pos.x, pos.y, 10, 10);
                fill(0);
                textSize(12);
                text(v, pos.x + 15, pos.y + 5);
                textSize(10);
                text(distances[v] === Infinity ? '∞' : '(' + distances[v] + ')', pos.x, pos.y - 15);
            });

            // Highlight visited vertices
            visited.forEach(v => {
                let pos = vertexPositions[v];
                fill(255, 0, 0);
                ellipse(pos.x, pos.y, 10, 10);
                fill(255, 0, 0);
                textSize(10);
                text(distances[v] === Infinity ? '∞' : '(' + distances[v] + ')', pos.x, pos.y - 15);
            });

            // Draw shortest path tree
            for (let v in shortestPathTree) {
                let u = shortestPathTree[v];
                let pos1 = vertexPositions[u];
                let pos2 = vertexPositions[v];
                stroke(0, 0, 255);
                strokeWeight(3);
                line(pos1.x, pos1.y, pos2.x, pos2.y);
                stroke(0);
                strokeWeight(1);
            }
        }

        function nextStep() {
            if (vertices.length === visited.size) {
                document.getElementById('next-step-btn').disabled = true;
                return;
            }

            let unvisited = vertices.filter(v => !visited.has(v));
            let minVertex = unvisited.reduce((min, v) => distances[v] < distances[min] ? v : min);
            visited.add(minVertex);

            edges.forEach(([v1, v2]) => {
                if (v1 === minVertex && !visited.has(v2)) {
                    let newDist = distances[minVertex] + weights[v1 + v2];
                    if (newDist < distances[v2]) {
                        distances[v2] = newDist;
                        shortestPathTree[v2] = minVertex;
                    }
                } else if (v2 === minVertex && !visited.has(v1)) {
                    let newDist = distances[minVertex] + weights[v2 + v1];
                    if (newDist < distances[v1]) {
                        distances[v1] = newDist;
                        shortestPathTree[v1] = minVertex;
                    }
                }
            });

            redraw();
        }

        document.getElementById('start-over-btn').addEventListener('click', () => {
            location.reload();
        });

        document.querySelectorAll('.vertex-grid button').forEach(btn => {
            btn.addEventListener('click', () => {
                let newStartVertex = btn.id.replace('btn-', '');
                if (newStartVertex !== startVertex) {
                    document.getElementById('btn-' + startVertex).classList.remove('selected');
                    btn.classList.add('selected');
                    startVertex = newStartVertex;
                    distances = {};
                    visited = new Set();
                    shortestPathTree = {};
                    vertices.forEach(v => distances[v] = Infinity);
                    distances[startVertex] = 0;
                    document.getElementById('title-display').textContent = `Dijkstra's algorithm starting at vertex ${startVertex}`;
                    document.getElementById('next-step-btn').disabled = false;
                    redraw();
                }
            });
        });

        document.getElementById('fixed-example-btn').addEventListener('click', () => {
            weights = {
                'mn': 8, 'no': 7, 'op': 7,
                'ij': 9, 'jk': 7, 'kl': 1,
                'ef': 1, 'fg': 7, 'gh': 6,
                'ab': 10, 'bc': 4, 'cd': 8,
                'mi': 6, 'ie': 9, 'ea': 9,
                'nj': 7, 'jf': 6, 'fb': 5,
                'ok': 1, 'kg': 8, 'gc': 8,
                'pl': 2, 'lh': 1, 'hd': 7
            };
            distances = {};
            visited = new Set();
            shortestPathTree = {};
            vertices.forEach(v => distances[v] = Infinity);
            distances[startVertex] = 0;
            document.getElementById('next-step-btn').disabled = false;
            redraw();
        });

        document.getElementById('random-example-btn').addEventListener('click', () => {
            edges.forEach(([v1, v2]) => {
                weights[v1 + v2] = Math.floor(Math.random() * 10) + 1;
                weights[v2 + v1] = weights[v1 + v2];
            });
            distances = {};
            visited = new Set();
            shortestPathTree = {};
            vertices.forEach(v => distances[v] = Infinity);
            distances[startVertex] = 0;
            document.getElementById('next-step-btn').disabled = false;
            redraw();
        });

        document.getElementById('next-step-btn').addEventListener('click', nextStep);

        window.onload = setup;
    </script>
</body>
</html>
