<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>3D Polyhedron Visualization</title>
    <style>
        body, html {
            margin: 0;
            padding: 0;
            overflow: hidden;
        }
        #app-container {
            display: flex;
            height: 100vh;
        }
        #controls-panel {
            width: 320px;
            padding: 20px;
            background-color: #f9f9f9;
            display: flex;
            flex-direction: column;
        }
        #controls-panel label {
            margin-top: 10px;
        }
        #controls-panel input[type="range"] {
            width: 100%;
        }
        #controls-panel button {
            margin-top: 10px;
            padding: 10px;
            cursor: pointer;
        }
        #controls-panel button.active {
            background-color: #ddd;
            border-style: inset;
        }
        #canvas-container {
            flex-grow: 1;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <label for="slider-sphere-diameter">diameter of sphere</label>
            <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">

            <label for="polyhedron-buttons">polyhedron vertices</label>
            <div id="polyhedron-buttons">
                <button id="btn-cube">Cube</button>
                <button id="btn-dodecahedron">Dodecahedron</button>
                <button id="btn-icosahedron">Icosahedron</button>
                <button id="btn-octahedron" class="active">Octahedron</button>
                <button id="btn-tetrahedron">Tetrahedron</button>
            </div>

            <label for="slider-cone-height">height</label>
            <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">

            <label for="slider-cone-base">base</label>
            <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
        </div>
        <div id="canvas-container"></div>
    </div>

    <!-- Include three.js and OrbitControls -->
    <script src="https://cdn.jsdelivr.net/npm/three@0.147.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.147.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        const appContainer = document.getElementById('app-container');
        const controlsPanel = document.getElementById('controls-panel');
        const canvasContainer = document.getElementById('canvas-container');

        const sliderSphereDiameter = document.getElementById('slider-sphere-diameter');
        const polyhedronButtons = document.getElementById('polyhedron-buttons');
        const sliderConeHeight = document.getElementById('slider-cone-height');
        const sliderConeBase = document.getElementById('slider-cone-base');

        let scene, camera, renderer, orbitControls, sphere, cones = [];

        function initThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 1000);
            camera.position.set(0, 0, 8);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
            orbitControls.enableDamping = true;
            orbitControls.dampingFactor = 0.25;

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight1.position.set(1, 1, 1);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.3);
            directionalLight2.position.set(-1, -1, -1);
            scene.add(directionalLight2);

            createSphere();
            updateCones();
        }

        function createSphere() {
            const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
            const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });
            sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphere);
        }

        function updateCones() {
            const sphereRadius = parseFloat(sliderSphereDiameter.value) / 2;
            const coneHeight = parseFloat(sliderConeHeight.value);
            const coneBase = parseFloat(sliderConeBase.value);

            const coneGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);

            const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });

            const selectedPolyhedron = Array.from(polyhedronButtons.children).find(button => button.classList.contains('active')).id.split('-')[1];

            let polyhedronGeometry;
            switch (selectedPolyhedron) {
                case 'cube': polyhedronGeometry = new THREE.BoxGeometry(1, 1, 1); break;
                case 'dodecahedron': polyhedronGeometry = new THREE.DodecahedronGeometry(1); break;
                case 'icosahedron': polyhedronGeometry = new THREE.IcosahedronGeometry(1); break;
                case 'octahedron': polyhedronGeometry = new THREE.OctahedronGeometry(1); break;
                case 'tetrahedron': polyhedronGeometry = new THREE.TetrahedronGeometry(1); break;
            }

            const positions = polyhedronGeometry.attributes.position.array;
            const numVertices = positions.length / 3;

            cones.forEach(cone => scene.remove(cone));
            cones = [];

            for (let i = 0; i < numVertices; i++) {
                const x = positions[i * 3];
                const y = positions[i * 3 + 1];
                const z = positions[i * 3 + 2];

                const vertexVector = new THREE.Vector3(x, y, z);
                const direction = vertexVector.normalize();

                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);
                const cone = new THREE.Mesh(coneGeometry, coneMaterial);
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);

                scene.add(cone);
                cones.push(cone);
            }
        }

        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            renderer.render(scene, camera);
        }

        sliderSphereDiameter.addEventListener('input', () => {
            sphere.scale.set(parseFloat(sliderSphereDiameter.value) / 2, parseFloat(sliderSphereDiameter.value) / 2, parseFloat(sliderSphereDiameter.value) / 2);
            updateCones();
        });

        sliderConeHeight.addEventListener('input', updateCones);
        sliderConeBase.addEventListener('input', updateCones);

        polyhedronButtons.addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                Array.from(polyhedronButtons.children).forEach(button => button.classList.remove('active'));
                event.target.classList.add('active');
                updateCones();
            }
        });

        window.addEventListener('resize', onWindowResize);

        initThreeJS();
        animate();
    </script>
</body>
</html>
