<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Analemma Simulation</title>
    <style>
        #main-container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f0f0f0;
            display: flex;
            flex-direction: column;
        }
        #visualization-area {
            flex-grow: 1;
            padding: 20px;
            position: relative;
        }
        label {
            margin-top: 10px;
        }
        input[type="range"] {
            width: 100%;
        }
        input[disabled] {
            opacity: 0.5;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="select-planet">planet</label>
            <select id="select-planet">
                <option value="manual" selected>-- choose orbit manually --</option>
                <option value="Earth">Earth</option>
                <option value="Mars">Mars</option>
            </select>
            <div id="manual-controls">
                <input type="checkbox" id="checkbox-manual" checked>
                <label for="checkbox-manual">choose orbit manually</label>
                <div>
                    <label for="slider-tilt">axis angle relative to ecliptic</label>
                    <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
                </div>
                <div>
                    <label for="slider-equinox">spring equinox point</label>
                    <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
                </div>
                <div>
                    <label for="slider-eccentricity">orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
                </div>
            </div>
            <div id="display-controls">
                <input type="checkbox" id="checkbox-snapshots" checked>
                <label for="checkbox-snapshots">show sun snapshots</label>
                <input type="checkbox" id="checkbox-scales" checked>
                <label for="checkbox-scales">show scales</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="analemma-canvas"></canvas>
        </div>
    </div>

    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let canvas;
        let analemmaPoints = [];
        let tilt = 23.5;
        let eccentricity = 0.1;
        let equinoxAngle = 0;
        let showSnapshots = true;
        let showScales = true;

        function setup() {
            canvas = createCanvas(600, 400);
            canvas.parent('visualization-area');
            updateControls();
            redrawAnalemma();
        }

        function draw() {
            clear();
            fill(255);
            noStroke();
            rect(0, 0, width, height);

            updateControls();

            calculateAnalemmaPoints();
            drawOrbitEllipse();
            if (showScales) drawScales();
            drawAnalemmaCurve();
            if (showSnapshots) drawSunSnapshots();
        }

        function updateControls() {
            const planet = document.getElementById('select-planet').value;
            const manualCheckbox = document.getElementById('checkbox-manual');
            const tiltSlider = document.getElementById('slider-tilt');
            const equinoxSlider = document.getElementById('slider-equinox');
            const eccentricitySlider = document.getElementById('slider-eccentricity');
            const snapshotsCheckbox = document.getElementById('checkbox-snapshots');
            const scalesCheckbox = document.getElementById('checkbox-scales');

            if (planet !== 'manual') {
                manualCheckbox.checked = false;
                tiltSlider.value = planetData[planet].tilt;
                equinoxSlider.value = planetData[planet].equinox;
                eccentricitySlider.value = planetData[planet].eccentricity;
            } else {
                manualCheckbox.checked = true;
            }

            tiltSlider.disabled = !manualCheckbox.checked;
            equinoxSlider.disabled = !manualCheckbox.checked;
            eccentricitySlider.disabled = !manualCheckbox.checked;

            tilt = parseFloat(tiltSlider.value);
            equinoxAngle = parseFloat(equinoxSlider.value) * Math.PI / 180;
            eccentricity = parseFloat(eccentricitySlider.value);
            showSnapshots = snapshotsCheckbox.checked;
            showScales = scalesCheckbox.checked;
        }

        function calculateAnalemmaPoints() {
            analemmaPoints = [];
            for (let d = 0; d < 365; d++) {
                const M = (2 * Math.PI * d) / 365.25;
                let E = M;
                for (let i = 0; i < 10; i++) {
                    E = M + eccentricity * Math.sin(E);
                }
                const v = 2 * Math.atan2(Math.sqrt(1 + eccentricity) * Math.sin(E / 2), Math.sqrt(1 - eccentricity) * Math.cos(E / 2));
                const lambda = v + equinoxAngle;
                const y = Math.asin(Math.sin(tilt * Math.PI / 180) * Math.sin(lambda));
                const alpha = Math.atan2(Math.cos(tilt * Math.PI / 180) * Math.sin(lambda), Math.cos(lambda));
                const EoT = M - alpha;
                const x = EoT * 200;
                const yCoord = y * 200;
                analemmaPoints.push({ x: x, y: yCoord });
            }
        }

        function drawOrbitEllipse() {
            noFill();
            stroke(150);
            ellipse(width / 2, 50, (1 + eccentricity) * 400, 400);
        }

        function drawScales() {
            stroke(0);
            line(0, height / 2, width, height / 2);
            line(width / 2, 0, width / 2, height);
            for (let i = -40; i <= 40; i += 10) {
                line(i * 5, height / 2 - 5, i * 5, height / 2 + 5);
                line(width / 2 - 5, i * 5, width / 2 + 5, i * 5);
                text(i, i * 5 - 10, height / 2 + 15);
                text(-i, width / 2 + 10, -i * 5 + 15);
            }
        }

        function drawAnalemmaCurve() {
            noFill();
            stroke('orange');
            strokeWeight(2);
            beginShape();
            for (let point of analemmaPoints) {
                vertex(point.x + width / 2, height / 2 - point.y);
            }
            endShape();
        }

        function drawSunSnapshots() {
            fill('orange');
            noStroke();
            for (let i = 0; i < analemmaPoints.length; i += 20) {
                const point = analemmaPoints[i];
                circle(point.x + width / 2, height / 2 - point.y, 8);
            }
        }

        document.getElementById('select-planet').addEventListener('change', () => {
            redrawAnalemma();
        });

        document.getElementById('checkbox-manual').addEventListener('change', () => {
            redrawAnalemma();
        });

        document.getElementById('slider-tilt').addEventListener('input', () => {
            redrawAnalemma();
        });

        document.getElementById('slider-equinox').addEventListener('input', () => {
            redrawAnalemma();
        });

        document.getElementById('slider-eccentricity').addEventListener('input', () => {
            redrawAnalemma();
        });

        document.getElementById('checkbox-snapshots').addEventListener('change', () => {
            redrawAnalemma();
        });

        document.getElementById('checkbox-scales').addEventListener('change', () => {
            redrawAnalemma();
        });

        function redrawAnalemma() {
            requestAnimationFrame(draw);
        }
    </script>
</body>
</html>
