        #control-panel {
            width: 30%;
            background-color: #fff;
            padding: 20px;
            border-right: 1px solid #ccc;
        }
        #visualization-panel {
            width: 70%;
            display: flex;
            flex-direction: column;
            padding: 20px;
        }
        #visualization-container {
            display: flex;
            justify-content: space-between;
            height: 100%;
        }
        #canvas-algo-wrapper, #canvas-optimal-wrapper {
            flex: 1;
            margin: 10px;
        }
        canvas {
            border: 1px solid #000;
        }
        #table-container {
            margin-top: 20px;
        }
        .vertex-btn {
            margin-right: 5px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="algorithm-choice">algorithm</label>
                <input type="radio" id="algo-cheapest-link" name="algorithm-choice" value="cheapest-link">
                <label for="algo-cheapest-link">cheapest link</label>
                <input type="radio" id="algo-nearest-neighbor" name="algorithm-choice" value="nearest-neighbor" checked>
                <label for="algo-nearest-neighbor">nearest neighbor</label>
            </div>
            <button id="btn-start-over">start over</button>
            <div>
                <button id="btn-fixed-example">fixed example</button>
                <button id="btn-random-example">random example</button>
            </div>
            <div id="starting-vertex-selector">
                <label>starting vertex for nearest neighbor</label>
                <div id="vertex-buttons-container">
                    <button id="btn-vertex-A" class="vertex-btn" data-vertex="A">A</button>
                    <button id="btn-vertex-B" class="vertex-btn" data-vertex="B">B</button>
                    <button id="btn-vertex-C" class="vertex-btn" data-vertex="C">C</button>
                    <button id="btn-vertex-D" class="vertex-btn" data-vertex="D">D</button>
                    <button id="btn-vertex-E" class="vertex-btn" data-vertex="E">E</button>
                    <button id="btn-vertex-F" class="vertex-btn" data-vertex="F">F</button>
                    <button id="btn-vertex-G" class="vertex-btn" data-vertex="G">G</button>
                </div>
            </div>
            <div>
                <label for="btn-next-step">Implement algorithm</label>
                <button id="btn-next-step" disabled>next step</button>
            </div>
            <div>
                <label for="checkbox-optimal">show optimal solution</label>
                <input type="checkbox" id="checkbox-optimal">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="visualization-container">
                <div id="algo-result-container">
                    <h3 id="title-algo">The nearest neighbor algorithm</h3>
                    <div id="canvas-algo-wrapper"></div>
                    <p id="text-example-type-algo">fixed example</p>
                    <div id="table-container">
                        <h4 id="table-title"></h4>
                        <table id="table-edge-list"></table>
                    </div>
                    <p id="text-total-weight"></p>
                </div>
                <div id="optimal-result-container" style="display: none;">
                    <h3 id="title-optimal">An optimal solution</h3>
                    <div id="canvas-optimal-wrapper"></div>
                    <p id="text-example-type-optimal"></p>
                    <p id="text-optimal-weight"></p>
                </div>
            </div>
        </div>
    </div>

    <script>
        let currentAlgorithm = 'nearest-neighbor';
        let graphData = {
            vertices: [
                { label: 'A', x: 200, y: 60 },
                { label: 'B', x: 80, y: 120 },
                { label: 'C', x: 60, y: 240 },
                { label: 'D', x: 150, y: 340 },
                { label: 'E', x: 250, y: 340 },
                { label: 'F', x: 340, y: 240 },
                { label: 'G', x: 320, y: 120 }
            ],
            adjacencyMatrix: [
                [-, 11, 16, 44, 30, 44, 23],
                [11, -, 12, 12, 10, 30, 25],
                [16, 12, -, 43, 32, 36, 44],
                [44, 12, 43, -, 41, 30, 35],
                [30, 10, 32, 41, -, 31, 20],
                [44, 30, 36, 30, 31, -, 18],
                [23, 25, 44, 35, 20, 18, -]
            ],
            nnPaths: {
                A: { path: ['A', 'B', 'E', 'G', 'F', 'D', 'C', 'A'], weight: 148 },
                B: { path: ['B', 'E', 'G', 'F', 'A', 'C', 'D', 'B'], weight: 142 }
            },
            optimalPath: { path: ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'], weight: 138 }
        };
        let currentPath = [];
        let totalWeight = 0;
        let stepCount = 0;
        let startingVertex = null;
        let p5CanvasAlgo, p5CanvasOptimal;

        function setup() {
            p5CanvasAlgo = createCanvas(400, 400).parent('canvas-algo-wrapper');
            p5CanvasOptimal = createCanvas(400, 400).parent('canvas-optimal-wrapper');
            loadFixedExample();
        }

        function draw() {
            background(255);
            drawGraph(p5CanvasAlgo, graphData.vertices, graphData.adjacencyMatrix, currentPath);
            if (document.getElementById('checkbox-optimal').checked) {
                drawGraph(p5CanvasOptimal, graphData.vertices, graphData.adjacencyMatrix, graphData.optimalPath.path, true);
            }
        }

        function drawGraph(canvas, vertices, adjacencyMatrix, path, isOptimal = false) {
            canvas.background(255);
            canvas.stroke(0);
            canvas.fill(255);

            // Draw edges
            for (let i = 0; i < vertices.length; i++) {
                for (let j = i + 1; j < vertices.length; j++) {
                    let weight = adjacencyMatrix[i][j];
                    if (weight !== -) {
                        canvas.stroke(128);
                        canvas.strokeWeight(1);
                        canvas.line(vertices[i].x, vertices[i].y, vertices[j].x, vertices[j].y);
                        canvas.fill(0);
                        canvas.textAlign(CENTER, CENTER);
                        canvas.text(weight, (vertices[i].x + vertices[j].x) / 2, (vertices[i].y + vertices[j].y) / 2);
                    }
                }
            }

            // Draw path
            if (path.length > 1) {
                canvas.stroke(isOptimal ? 'blue' : 'orange');
                canvas.strokeWeight(3);
                for (let i = 0; i < path.length - 1; i++) {
                    let v1 = vertices.find(v => v.label === path[i]);
                    let v2 = vertices.find(v => v.label === path[i + 1]);
                    canvas.line(v1.x, v1.y, v2.x, v2.y);
                }
            }

            // Draw vertices
            canvas.noStroke();
            canvas.fill(255);
            for (let vertex of vertices) {
                canvas.ellipse(vertex.x, vertex.y, 20, 20);
                canvas.fill(0);
                canvas.textAlign(CENTER, CENTER);
                canvas.text(vertex.label, vertex.x, vertex.y);
            }
        }

        function loadFixedExample() {
            graphData.nnPaths.A = { path: ['A', 'B', 'E', 'G', 'F', 'D', 'C', 'A'], weight: 148 };
            graphData.nnPaths.B = { path: ['B', 'E', 'G', 'F', 'A', 'C', 'D', 'B'], weight: 142 };
            graphData.optimalPath = { path: ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'], weight: 138 };
            resetSimulation();
        }

        function resetSimulation() {
            currentPath = [];
            totalWeight = 0;
            stepCount = 0;
            startingVertex = null;
            document.getElementById('btn-next-step').disabled = true;
            document.getElementById('title-algo').innerText = "Select a starting vertex";
            document.getElementById('table-title').innerText = "";
            document.getElementById('table-edge-list').innerHTML = "";
            document.getElementById('text-total-weight').innerText = "";
            document.querySelectorAll('.vertex-btn').forEach(btn => btn.disabled = false);
        }

        document.getElementById('algo-cheapest-link').addEventListener('change', () => {
            currentAlgorithm = 'cheapest-link';
            document.getElementById('starting-vertex-selector').style.display = 'none';
            resetSimulation();
        });

        document.getElementById('algo-nearest-neighbor').addEventListener('change', () => {
            currentAlgorithm = 'nearest-neighbor';
            document.getElementById('starting-vertex-selector').style.display = 'block';
            resetSimulation();
        });

        document.getElementById('btn-start-over').addEventListener('click', () => {
            resetSimulation();
        });

        document.getElementById('btn-fixed-example').addEventListener('click', () => {
            loadFixedExample();
        });

        document.getElementById('btn-random-example').addEventListener('click', () => {
            // Generate random graph
            resetSimulation();
        });

        document.getElementById('starting-vertex-selector').addEventListener('click', (event) => {
            if (event.target.classList.contains('vertex-btn')) {
                startingVertex = event.target.getAttribute('data-vertex');
                document.getElementById('title-algo').innerText = `The nearest neighbor algorithm starting at vertex ${startingVertex}`;
                document.querySelectorAll('.vertex-btn').forEach(btn => btn.disabled = true);
                document.getElementById('btn-next-step').disabled = false;
            }
        });

        document.getElementById('btn-next-step').addEventListener('click', () => {
            if (currentAlgorithm === 'nearest-neighbor') {
                if (stepCount === 0) {
                    currentPath.push(startingVertex);
                }
                let currentVertex = currentPath[currentPath.length - 1];
                let neighbors = graphData.vertices.filter(v => !currentPath.includes(v.label));
                neighbors.sort((a, b) => {
                    let weightA = graphData.adjacencyMatrix[graphData.vertices.findIndex(v => v.label === currentVertex)][graphData.vertices.findIndex(v => v.label === a.label)];
                    let weightB = graphData.adjacencyMatrix[graphData.vertices.findIndex(v => v.label === currentVertex)][graphData.vertices.findIndex(v => v.label === b.label)];
                    return weightA - weightB;
                });
                currentPath.push(neighbors[0].label);
                totalWeight += graphData.adjacencyMatrix[graphData.vertices.findIndex(v => v.label === currentVertex)][graphData.vertices.findIndex(v => v.label === neighbors[0].label)];
                stepCount++;
                if (stepCount === graphData.vertices.length) {
                    currentPath.push(startingVertex);
                    totalWeight += graphData.adjacencyMatrix[graphData.vertices.findIndex(v => v.label === neighbors[0].label)][graphData.vertices.findIndex(v => v.label === startingVertex)];
                    document.getElementById('btn-next-step').disabled = true;
                }
                updateTableAndWeight();
            }
        });

        document.getElementById('checkbox-optimal').addEventListener('change', (event) => {
            if (event.target.checked) {
                document.getElementById('optimal-result-container').style.display = 'block';
            } else {
                document.getElementById('optimal-result-container').style.display = 'none';
            }
        });

        function updateTableAndWeight() {
            document.getElementById('table-title').innerText = "Selected Edges";
            let tableBody = document.createElement('tbody');
            for (let i = 0; i < currentPath.length - 1; i++) {
                let row = document.createElement('tr');
                let cell1 = document.createElement('td');
                cell1.innerText = `${currentPath[i]}-${currentPath[i + 1]}`;
                let cell2 = document.createElement('td');
                cell2.innerText = graphData.adjacencyMatrix[graphData.vertices.findIndex(v => v.label === currentPath[i])][graphData.vertices.findIndex(v => v.label === currentPath[i + 1])];
                row.appendChild(cell1);
                row.appendChild(cell2);
                tableBody.appendChild(row);
            }
            document.getElementById('table-edge-list').appendChild(tableBody);
            document.getElementById('text-total-weight').innerText = `Total Weight: ${totalWeight}`;
        }

        window.onload = setup;
    </script>
</body>
</html>
