        function draw() {
            clear();
            background(240);
            translate(width / 2, height / 2);
            scale(xScale, -yScale);

            // Draw camera axes
            strokeWeight(3);
            stroke('#0000CD');
            line(0, 0, u[0], u[1]);
            text('u', u[0] + 10, u[1] + 10);
            stroke('#6495ED');
            line(0, 0, v[0], v[1]);
            text('v', v[0] + 10, v[1] + 10);
            stroke('#0000CD');
            line(0, 0, w[0], w[1]);
            text('w', w[0] + 10, w[1] + 10);

            // Draw points
            fill('#FF0000');
            ellipse(A[0], A[1], 10, 10);
            text('A', A[0] + 10, A[1] + 10);
            fill('#FFA500');
            ellipse(B[0], B[1], 10, 10);
            text('B', B[0] + 10, B[1] + 10);
            fill('#FF0000');
            ellipse(0, 0, 6, 6);
            text('O', 10, 10);

            // Draw projections
            fill('#00FF00');
            ellipse(u[0] * A[0], u[1] * A[1], 5, 5);
            ellipse(v[0] * A[0], v[1] * A[1], 5, 5);
            ellipse(w[0] * A[0], w[1] * A[1], 5, 5);
            ellipse(u[0] * B[0], u[1] * B[1], 5, 5);
            ellipse(v[0] * B[0], v[1] * B[1], 5, 5);
            ellipse(w[0] * B[0], w[1] * B[1], 5, 5);

            // Draw lines from points to projections
            stroke('#808080');
            line(A[0], A[1], u[0] * A[0], u[1] * A[1]);
            line(A[0], A[1], v[0] * A[0], v[1] * A[1]);
            line(A[0], A[1], w[0] * A[0], w[1] * A[1]);
            line(B[0], B[1], u[0] * B[0], u[1] * B[1]);
            line(B[0], B[1], v[0] * B[0], v[1] * B[1]);
            line(B[0], B[1], w[0] * B[0], w[1] * B[1]);
        }

        function attachEventListeners() {
            const sliders = document.querySelectorAll('#control-panel input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', () => {
                    const valueSpan = document.getElementById(`value-${slider.id}`);
                    valueSpan.textContent = slider.value;
