        function calculateAndDraw() {
            const au = parseFloat(document.getElementById('slider-au').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);
            xScale = parseFloat(document.getElementById('slider-x-scale').value);
            yScale = parseFloat(document.getElementById('slider-y-scale').value);

            const M = math.matrix([[au, bu], [av, bv], [aw, bw]]);
            const svd = math.svd(M);
            const S_hat = svd.U;
            const K = math.matrix([S_hat.subset(math.index(0, [0, 1])), S_hat.subset(math.index(1, [0, 1])), S_hat.subset(math.index(2, [0, 1]))]);
            const l_vec = math.lusolve(K, [1, 1, 1]);

            if (l_vec.length === 0 || !math.isPositiveDefinite(math.diag(l_vec))) {
                console.error('Calculation failed or L is not positive definite.');
                return;
            }

            const L = math.diag(l_vec);
            const Q = math.sqrtm(L);
