                let x, y;
                switch (type) {
                    case '2A1':
                        x = u;
                        y = u * u;
                        break;
                    case '2A2':
                        x = 2 * u * u * u;
                        y = 3 * u * u;
                        break;
                    case '2B2':
                        x = u * u;
                        y = u * u * u * u;
                        break;
                }
                vertices.push(x + t1, y + t2, 0);
            }
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            const material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
            const line = new THREE.Line(geometry, material);
            scene.add(line);
        }

        function render3D(type) {
            scene.clear();
            const geometry = new THREE.ParametricGeometry((u, v) => {
                const U = u * 4 - 2;
                const V = v * 8 - 4;
                let x, y, z;
                switch (type) {
                    case '3A1p':
                        x = U;
                        y = V;
                        z = 0.3 * (U * U + V * V) + t1 * U + t2 * V;
                        break;
                    case '3A1m':
                        x = U;
                        y = V;
                        z = -0.3 * (U * U + V * V) + t1 * U + t2 * V;
                        break;
                    case '3A2':
                        x = 2 * U * U * U + t1;
                        y = 3 * U * U + t2;
                        z = V;
                        break;
                    case '3A3':
                        x = 3 * U * U * U * U + U * U * V;
                        y = -4 * U * U * U - 2 * U * V + t1 * 2;
                        z = V + t2 * 2;
                        break;
                    case '3B2p':
                        x = U;
                        y = V;
                        z = (U * U + t1) * (U * U + t1) + V * V + t2;
                        break;
                    case '3B2m':
                        x = U;
                        y = V;
                        z = -(U * U * U * U + t1 * 2 * U * U + V * V - t2);
                        break;
                    case '3B3':
                        x = U;
                        y = V;
                        z = U * U * U + t1 * U + V * V + t2;
                        break;
                    case '3C3p':
                        x = U;
                        y = V;
                        z = U * U * U - 3 * U * V * V + t1 * 2 * (U * U + V * V) + t2 * 4 * U;
                        break;
                    case '3C3m':
                        x = U;
                        y = V;
                        z = U * U * U + V * V * V + t1 * 4 * U + t2 * 4 * V;
                        break;
                }
                return new THREE.Vector3(x, y, z);
            }, 50, 50);
            const material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
            const mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);

            if (type === '3A3' || type === '3C3m') {
                const wireframe = new THREE.LineSegments(geometry.computeLineDistances(), new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 1 }));
                scene.add(wireframe);
            }
        }

        function render() {
            if (currentDimension === '2D') {
                render2D(currentType);
            } else {
                render3D(currentType);
            }
            renderer.render(scene, camera);
        }

        function updateHandlePosition(event) {
            const rect = event.target.getBoundingClientRect();
            const x = event.clientX - rect.left - 10;
            const y = event.clientY - rect.top - 10;
            document.getElementById('time-handle').style.left = `${x}px`;
            document.getElementById('time-handle').style.top = `${y}px`;

            t1 = (x / rect.width) * 2 - 1;
            t2 = ((rect.height - y) / rect.height) * 2 - 1;
            render();
        }

        document.getElementById('time-pad').addEventListener('mousedown', (event) => {
            document.addEventListener('mousemove', updateHandlePosition);
            document.addEventListener('mouseup', () => {
                document.removeEventListener('mousemove', updateHandlePosition);
            });
            updateHandlePosition(event);
        });

        document.getElementById('time-pad').addEventListener('click', updateHandlePosition);

        const buttons = document.querySelectorAll('#control-panel button');
        buttons.forEach(button => {
            button.addEventListener('click', () => {
                buttons.forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                currentType = button.id.replace('btn-', '');
                currentDimension = currentType.length > 2 ? '3D' : '2D';
                initCamera();
                render();
            });
        });

        initCamera();
        initLights();
        render();
        window.addEventListener('resize', () => {
            renderer.setSize(window.innerWidth - 200, window.innerHeight);
            camera.aspect = (window.innerWidth - 200) / window.innerHeight;
            camera.updateProjectionMatrix();
            render();
        });
    </script>
</body>
</html>
