    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <select id="select-plot">
                    <option value="semibatch">Semibatch</option>
                    <option value="batch">Batch</option>
                    <option value="selectivity">Selectivity</option>
                </select>
                <div>
                    <span id="cycler-feed-label">feed A to B</span>
                    <button id="btn-feed-down">▼</button>
                    <button id="btn-feed-up">▲</button>
                </div>
            </div>
            <div>
                <div class="slider-container">
                    <label for="slider-feed-rate">feed rate</label>
                    <input type="range" id="slider-feed-rate" min="10" max="200" value="10" step="1">
                    <input type="number" id="input-feed-rate" value="10">
                </div>
                <div class="slider-container">
                    <label for="slider-ua">UA</label>
                    <input type="range" id="slider-ua" min="5000" max="30000" value="15000" step="100">
                    <input type="number" id="input-ua" value="15000">
                </div>
                <div class="slider-container">
                    <label for="slider-dh1">\( \Delta H_1 \)</label>
                    <input type="range" id="slider-dh1" min="500" max="5000" value="1000" step="50">
                    <input type="number" id="input-dh1" value="1000">
                </div>
                <div class="slider-container">
                    <label for="slider-dh2">\( \Delta H_2 \)</label>
                    <input type="range" id="slider-dh2" min="500" max="5000" value="3000" step="50">
                    <input type="number" id="input-dh2" value="3000">
                </div>
            </div>
