        <div id="plot-area">
            <canvas id="plot-canvas"></canvas>
            <div id="legend"></div>
        </div>
    </div>

    <script>
        let plotType = 'semibatch';
        let feedConfig = 'feed A to B';
        let feedRate = 10;
        let ua = 15000;
        let dh1 = 1000;
        let dh2 = 3000;

        const V = 100; // Reactor Volume in L
        const T_initial = T_coolant = T_feed = 330; // Initial and Coolant Temperature in K
        const R = 8.314; // Gas Constant in J/mol/K
        const Cp_total = 150000; // Total Heat Capacity in J/K
        const k1_0 = 3e4; // Rate constant parameter 1 in L²/(mol²·h)
        const E1 = 30000; // Activation energy 1 in J/mol
        const k2_0 = 1e5; // Rate constant parameter 2 in L/(mol·h)
        const E2 = 40000; // Activation energy 2 in J/mol
        const totalMoles = 100; // Total initial moles for each reactant in mol
        const dt = 0.01; // Time step in hours
        const tf = totalMoles / feedRate; // Feed duration in hours

        let plotCanvas;
        let legend;

        function setup() {
            plotCanvas = createCanvas(800, 600).parent('plot-area');
            legend = select('#legend');

            updateControls();
            simulateAndDraw();
        }

        function draw() {
            clear();
            drawAxes();
            drawCurves();
            drawLegend();
        }

        function drawAxes() {
            stroke(0);
            line(50, height - 50, 50, 50); // Y-axis
            line(50, height - 50, width - 50, height - 50); // X-axis

            // Y-axis ticks and labels
            let yStep = (height - 100) / 5;
            for (let i = 0; i <= 5; i++) {
                let y = height - 50 - i * yStep;
                line(45, y, 50, y);
                if (plotType === 'selectivity') {
                    let value = map(i, 0, 5, 0, 1.2);
                    text(nf(value, 1, 2), 20, y);
                } else {
                    let value = map(i, 0, 5, 0, 100);
                    text(nf(value, 0, 0), 20, y);
                }
            }

            // X-axis ticks and labels
            let xStep = (width - 100) / 4;
            for (let i = 0; i <= 4; i++) {
                let x = 50 + i * xStep;
                line(x, height - 45, x, height - 50);
                text(nf(i * 0.5, 1, 1), x, height - 30);
            }

            // Grid
            stroke(200);
            for (let i = 1; i < 5; i++) {
                line(50, height - 50 - i * yStep, width - 50, height - 50 - i * yStep);
            }
            for (let i = 1; i < 4; i++) {
                line(50 + i * xStep, height - 50, 50 + i * xStep, height - 50);
            }
        }

        function drawCurves() {
            let data = simulate();
            strokeWeight(2);

            stroke('#2ca02c');
            drawDashedLine(data.Na);

            stroke('#000000');
            drawDashedLine(data.Nb);

            stroke('#1f77b4');
            drawSolidLine(data.Nd);

            stroke('#d62728');
            drawSolidLine(data.Nu);

            if (plotType === 'selectivity') {
                stroke('#800080');
                drawSolidLine(data.S_ratio);
            }
        }

        function drawDashedLine(data) {
            beginShape();
            for (let i = 0; i < data.length; i++) {
                let x = map(i * dt, 0, 2, 50, width - 50);
                let y = map(data[i], 0, 100, height - 50, 50);
                vertex(x, y);
            }
            endShape();
        }

        function drawSolidLine(data) {
            beginShape();
            for (let i = 0; i < data.length; i++) {
                let x = map(i * dt, 0, 2, 50, width - 50);
                let y = map(data[i], 0, 100, height - 50, 50);
                vertex(x, y);
            }
            endShape();
        }

        function drawLegend() {
            legend.html('');
            let entries = [
                { label: 'Na', color: '#2ca02c', style: 'dashed' },
                { label: 'Nb', color: '#000000', style: 'dashed' },
                { label: 'Nd', color: '#1f77b4', style: 'solid' },
                { label: 'Nu', color: '#d62728', style: 'solid' }
            ];
            if (plotType === 'selectivity') {
                entries.push({ label: 'S_ratio', color: '#800080', style: 'solid' });
            }

            entries.forEach(entry => {
                let span = createSpan().parent(legend);
                span.style('display', 'inline-block');
                span.style('margin-right', '10px');
                let line = createDiv().parent(span);
                line.style('width', '10px');
                line.style('height', '10px');
                line.style('display', 'inline-block');
                line.style('margin-right', '5px');
                line.style('background-color', entry.color);
                if (entry.style === 'dashed') {
                    line.style('border', '1px dashed black');
                }
                let label = createSpan(entry.label).parent(span);
                MathJax.typesetPromise([label.elt()]);
            });
        }

        function simulate() {
            let data = {
                Na: [],
                Nb: [],
                Nd: [],
                Nu: [],
                S_ratio: []
            };

            let Y = [totalMoles, totalMoles, 0, 0, T_initial];
            let Y_batch = [...Y];
            let Y_semi = [...Y];

            for (let t = 0; t <= 2; t += dt) {
                let k1 = k1_0 * exp(-E1 / (R * Y[4]));
                let k2 = k2_0 * exp(-E2 / (R * Y[4]));
                let r1 = k1 * (Y[0] / V) * (Y[1] / V) ** 2;
                let r2 = k2 * (Y[0] / V) * (Y[1] / V);
                let dT = (ua * (T_coolant - Y[4]) + (-dh1 * r1 - dh2 * r2) * V) / Cp_total;

                let dY = [
                    -(r1 + r2) * V,
                    -(r1 + r2) * V,
                    r1 * V,
                    r2 * V,
                    dT
                ];

                Y = Y.map((y, i) => y + dY[i] * dt);

                data.Na.push(Y[0]);
                data.Nb.push(Y[1]);
                data.Nd.push(Y[2]);
                data.Nu.push(Y[3]);

                if (plotType === 'batch' || plotType === 'selectivity') {
                    let k1_batch = k1_0 * exp(-E1 / (R * Y_batch[4]));
                    let k2_batch = k2_0 * exp(-E2 / (R * Y_batch[4]));
                    let r1_batch = k1_batch * (Y_batch[0] / V) * (Y_batch[1] / V) ** 2;
                    let r2_batch = k2_batch * (Y_batch[0] / V) * (Y_batch[1] / V);
                    let dT_batch = (ua * (T_coolant - Y_batch[4]) + (-dh1 * r1_batch - dh2 * r2_batch) * V) / Cp_total;

                    let dY_batch = [
                        -(r1_batch + r2_batch) * V,
                        -(r1_batch + r2_batch) * V,
                        r1_batch * V,
                        r2_batch * V,
                        dT_batch
                    ];

                    Y_batch = Y_batch.map((y, i) => y + dY_batch[i] * dt);

                    data.Na.push(Y_batch[0]);
                    data.Nb.push(Y_batch[1]);
                    data.Nd.push(Y_batch[2]);
                    data.Nu.push(Y_batch[3]);
                }

                if (plotType === 'semibatch' || plotType === 'selectivity') {
                    let F_A0 = (t < tf) ? feedRate : 0;
                    let F_B0 = (t < tf) ? feedRate : 0;

                    let k1_semi = k1_0 * exp(-E1 / (R * Y_semi[4]));
                    let k2_semi = k2_0 * exp(-E2 / (R * Y_semi[4]));
                    let r1_semi = k1_semi * (Y_semi[0] / V) * (Y_semi[1] / V) ** 2;
                    let r2_semi = k2_semi * (Y_semi[0] / V) * (Y_semi[1] / V);
                    let dT_semi = (ua * (T_coolant - Y_semi[4]) + (-dh1 * r1_semi - dh2 * r2_semi) * V) / Cp_total;

                    let dY_semi = [
                        F_A0 - (r1_semi + r2_semi) * V,
                        F_B0 - (r1_semi + r2_semi) * V,
                        r1_semi * V,
                        r2_semi * V,
                        dT_semi
                    ];

                    Y_semi = Y_semi.map((y, i) => y + dY_semi[i] * dt);

                    data.Na.push(Y_semi[0]);
                    data.Nb.push(Y_semi[1]);
                    data.Nd.push(Y_semi[2]);
                    data.Nu.push(Y_semi[3]);
                }

                if (plotType === 'selectivity') {
                    let S_batch = Y_batch[2] / Y_batch[3];
                    let S_semi = Y_semi[2] / Y_semi[3];
                    data.S_ratio.push(S_semi / S_batch);
                }
            }

            return data;
        }

        function updateControls() {
            select('#select-plot').value(plotType);
            select('#cycler-feed-label').html(feedConfig);
            select('#slider-feed-rate').value(feedRate);
            select('#input-feed-rate').value(feedRate);
            select('#slider-ua').value(ua);
            select('#input-ua').value(ua);
            select('#slider-dh1').value(dh1);
            select('#input-dh1').value(dh1);
            select('#slider-dh2').value(dh2);
            select('#input-dh2').value(dh2);

            select('#slider-feed-rate').input(updateFeedRate);
            select('#input-feed-rate').input(updateFeedRate);
            select('#slider-ua').input(updateUA);
            select('#input-ua').input(updateUA);
            select('#slider-dh1').input(updateDH1);
            select('#input-dh1').input(updateDH1);
            select('#slider-dh2').input(updateDH2);
            select('#input-dh2').input(updateDH2);
            select('#select-plot').change(updatePlotType);
            select('#btn-feed-up').mousePressed(cycleFeedUp);
            select('#btn-feed-down').mousePressed(cycleFeedDown);
        }

        function updateFeedRate() {
            feedRate = parseFloat(select('#slider-feed-rate').value());
            select('#input-feed-rate').value(feedRate);
            simulateAndDraw();
        }

        function updateUA() {
            ua = parseFloat(select('#slider-ua').value());
            select('#input-ua').value(ua);
            simulateAndDraw();
        }

        function updateDH1() {
            dh1 = parseFloat(select('#slider-dh1').value());
            select('#input-dh1').value(dh1);
            simulateAndDraw();
        }

        function updateDH2() {
            dh2 = parseFloat(select('#slider-dh2').value());
            select('#input-dh2').value(dh2);
            simulateAndDraw();
        }

        function updatePlotType() {
            plotType = select('#select-plot').value();
            simulateAndDraw();
        }

        function cycleFeedUp() {
            if (feedConfig === 'feed A to B') {
                feedConfig = 'feed B to A';
            } else {
                feedConfig = 'feed A to B';
            }
            select('#cycler-feed-label').html(feedConfig);
            simulateAndDraw();
        }

        function cycleFeedDown() {
            if (feedConfig === 'feed A to B') {
                feedConfig = 'feed B to A';
            } else {
                feedConfig = 'feed A to B';
            }
            select('#cycler-feed-label').html(feedConfig);
            simulateAndDraw();
        }

        function simulateAndDraw() {
            noLoop();
            simulate();
            loop();
        }

        window.onload = function() {
            setup();
            draw();
        };
    </script>
</body>
</html>
