</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Tree type</h4>
            <div id="tree-type-buttons">
                <button id="btn-rt" class="active">Random Tree</button>
                <button id="btn-rrt">RRT</button>
                <button id="btn-rrt-star">RRT*</button>
            </div>
            <h4>obstacle type</h4>
            <select id="select-obstacle">
                <option value="narrow-passage" selected>narrow passage</option>
                <option value="empty">empty</option>
                <option value="random-rects">random rects</option>
            </select>
            <h4>number of nodes to add:</h4>
            <div id="add-nodes-buttons">
                <button id="btn-add-1">1</button>
                <button id="btn-add-10">10</button>
                <button id="btn-add-100">100</button>
                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>
            <h4>exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>
            <h4>goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>
        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let treeType = 'Random Tree';
        let obstacleType = 'narrow-passage';
        let startNode = { x: 50, y: 250 };
        let goalNode = { x: 450, y: 450 };
        let nodes = [startNode];
        let path = [];
        let goalReached = false;
        let bias = 0;
        let goalRadius = 1;

        function setup() {
            createCanvas(500, 500).parent('canvas-container');
            drawObstacles();
            drawNodes();
            drawGoal();
        }

        function draw() {
            background(255);
            drawObstacles();
            drawNodes();
            drawEdges();
            drawPath();
            drawGoal();
            document.getElementById('status-text').textContent = `${nodes.length} nodes, ${goalReached ? `path length ${path.length.toFixed(2)}` : 'goal not yet reached'}`;
        }

        function drawObstacles() {
            stroke('blue');
            strokeWeight(8);
            noFill();
            rect(0, 0, width, height);
            if (obstacleType === 'narrow-passage') {
                rect(100, 100, 300, 200);
                rect(100, 300, 300, 200);
            } else if (obstacleType === 'random-rects') {
                rect(150, 150, 100, 100);
                rect(350, 350, 100, 100);
            }
        }

        function drawNodes() {
            fill('red');
            noStroke();
            nodes.forEach(node => circle(node.x, node.y, 5));
        }

        function drawEdges() {
            if (treeType === 'RRT*' && path.length > 0) {
                stroke(0);
                strokeWeight(0.5);
                for (let i = 1; i < path.length; i++) {
                    line(path[i].x, path[i].y, path[i - 1].x, path[i - 1].y);
                }
            }
        }

        function drawPath() {
            if (goalReached) {
                stroke('green');
                strokeWeight(3);
                for (let i = 1; i < path.length; i++) {
                    line(path[i].x, path[i].y, path[i - 1].x, path[i - 1].y);
                }
            }
        }

        function drawGoal() {
            fill(255, 255, 0, 100);
            noStroke();
            circle(goalNode.x, goalNode.y, goalRadius * 2);
            stroke(0);
            line(goalNode.x - 5, goalNode.y, goalNode.x + 5, goalNode.y);
            line(goalNode.x, goalNode.y - 5, goalNode.x, goalNode.y + 5);
        }

        function addNodes(count) {
            for (let i = 0; i < count; i++) {
                let newNode = getRandomNode();
                if (treeType === 'Random Tree') {
                    nodes.push(newNode);
                } else if (treeType === 'RRT') {
                    rrt(newNode);
                } else if (treeType === 'RRT*') {
                    rrtStar(newNode);
                }
                checkGoal();
            }
            redraw();
        }

        function getRandomNode() {
            return { x: random(width), y: random(height) };
        }

        function rrt(newNode) {
            let nearestNode = findNearestNode(newNode);
            let direction = p5.Vector.sub(newNode, nearestNode);
            direction.setMag(10);
            let nextNode = p5.Vector.add(nearestNode, direction);
            nodes.push(nextNode);
        }

        function rrtStar(newNode) {
            let nearestNode = findNearestNode(newNode);
            let direction = p5.Vector.sub(newNode, nearestNode);
            direction.setMag(10);
            let nextNode = p5.Vector.add(nearestNode, direction);
            nodes.push(nextNode);
            rewire(nextNode);
        }

        function findNearestNode(node) {
            let minDist = Infinity;
            let nearest = null;
            for (let n of nodes) {
                let dist = dist(node.x, node.y, n.x, n.y);
                if (dist < minDist) {
                    minDist = dist;
                    nearest = n;
                }
            }
            return nearest;
        }

        function rewire(node) {
            // Rewiring logic for RRT*
        }

        function checkGoal() {
            let lastNode = nodes[nodes.length - 1];
            if (dist(lastNode.x, lastNode.y, goalNode.x, goalNode.y) <= goalRadius) {
                goalReached = true;
                tracePath();
            }
        }

        function tracePath() {
            let currentNode = nodes[nodes.length - 1];
            path = [];
            while (currentNode) {
                path.unshift(currentNode);
                currentNode = currentNode.parent;
            }
        }

        document.getElementById('btn-rt').addEventListener('click', () => {
            setActiveButton('btn-rt');
            resetSimulation();
        });

        document.getElementById('btn-rrt').addEventListener('click', () => {
            setActiveButton('btn-rrt');
            resetSimulation();
        });

        document.getElementById('btn-rrt-star').addEventListener('click', () => {
            setActiveButton('btn-rrt-star');
            resetSimulation();
        });

        document.getElementById('select-obstacle').addEventListener('change', () => {
            obstacleType = document.getElementById('select-obstacle').value;
            resetSimulation();
        });

        document.getElementById('btn-add-1').addEventListener('click', () => addNodes(1));
        document.getElementById('btn-add-10').addEventListener('click', () => addNodes(10));
        document.getElementById('btn-add-100').addEventListener('click', () => addNodes(100));
        document.getElementById('btn-add-200').addEventListener('click', () => addNodes(200));
        document.getElementById('btn-add-500').addEventListener('click', () => addNodes(500));

        document.getElementById('slider-bias').addEventListener('input', () => {
            bias = parseFloat(document.getElementById('slider-bias').value);
            document.getElementById('bias-value-label').textContent = bias.toFixed(2);
        });

        document.getElementById('slider-radius').addEventListener('input', () => {
            goalRadius = parseFloat(document.getElementById('slider-radius').value);
            document.getElementById('radius-value-label').textContent = goalRadius.toFixed(2);
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', resetSimulation);

        function resetSimulation() {
            treeType = document.querySelector('.active').id.replace('btn-', '');
            obstacleType = document.getElementById('select-obstacle').value;
            nodes = [startNode];
            path = [];
            goalReached = false;
            redraw();
        }

        function setActiveButton(id) {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(id).classList.add('active');
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, goalNode.x, goalNode.y) <= goalRadius) {
                goalNode.x = mouseX;
                goalNode.y = mouseY;
                resetSimulation();
            }
        }

        function mouseDragged() {
            if (dist(mouseX, mouseY, goalNode.x, goalNode.y) <= goalRadius) {
                goalNode.x = mouseX;
                goalNode.y = mouseY;
                redraw();
            }
        }
    </script>
</body>
</html>
