            document.getElementById('three-canvas').appendChild(renderer.domElement);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);

            const ambientLight = new THREE.AmbientLight(0x666666);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            const potentialSurfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y_surf = rho * rho + 1 / (rho * rho);
                const z_surf = -rho * Math.sin(theta);
                target.set(x, y_surf, z_surf);
            }, 32, 32);
            const potentialSurfaceMaterial = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });
            const potentialSurface = new THREE.Mesh(potentialSurfaceGeometry, potentialSurfaceMaterial);
            scene.add(potentialSurface);

            const centralCylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const centralCylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });
            const centralCylinder = new THREE.Mesh(centralCylinderGeometry, centralCylinderMaterial);
            centralCylinder.position.y = 1.25;
            scene.add(centralCylinder);

            updateVisualization();

            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function updateVisualization() {
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const v = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            const ds = 0.05;
            const steps = Math.floor(t / ds);
            const points = [];

            const Y = [rho0, 0, 0];
            for (let i = 0; i <= steps; i++) {
                const s = i * ds;
                const k1 = [Y[1], -(2 * Y[0] - 2 / Math.pow(Y[0], 3)) - 4 * Math.pow(lambda, 2) * Y[0] + 4 * e0 / Math.pow(Y[0], 3), 2 * (e0 / Math.pow(Y[0], 2) - lambda)];
                const k2 = [
                    Y[1] + 0.5 * ds * k1[0],
                    -(2 * (Y[0] + 0.5 * ds * k1[0]) - 2 / Math.pow(Y[0] + 0.5 * ds * k1[0], 3)) - 4 * Math.pow(lambda, 2) * (Y[0] + 0.5 * ds * k1[0]) + 4 * e0 / Math.pow(Y[0] + 0.5 * ds * k1[0], 3),
                    2 * (e0 / Math.pow(Y[0] + 0.5 * ds * k1[0], 2) - lambda)
                ];
                const k3 = [
                    Y[1] + 0.5 * ds * k2[0],
                    -(2 * (Y[0] + 0.5 * ds * k2[0]) - 2 / Math.pow(Y[0] + 0.5 * ds * k2[0], 3)) - 4 * Math.pow(lambda, 2) * (Y[0] + 0.5 * ds * k2[0]) + 4 * e0 / Math.pow(Y[0] + 0.5 * ds * k2[0], 3),
                    2 * (e0 / Math.pow(Y[0] + 0.5 * ds * k2[0], 2) - lambda)
                ];
                const k4 = [
                    Y[1] + ds * k3[0],
                    -(2 * (Y[0] + ds * k3[0]) - 2 / Math.pow(Y[0] + ds * k3[0], 3)) - 4 * Math.pow(lambda, 2) * (Y[0] + ds * k3[0]) + 4 * e0 / Math.pow(Y[0] + ds * k3[0], 3),
                    2 * (e0 / Math.pow(Y[0] + ds * k3[0], 2) - lambda)
                ];

                Y[0] += ds / 6 * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]);
                Y[1] += ds / 6 * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]);
                Y[2] += ds / 6 * (k1[2] + 2 * k2[2] + 2 * k3[2] + k4[2]);

                const u_rho = Y[0] * Y[0] + 1 / (Y[0] * Y[0]);
                const sqrt_arg = e0 - u_rho;
                const z = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * v * s + zeta0);
                const x = Y[0] * Math.cos(Y[2]);
                const y = Y[0] * Math.sin(Y[2]);

                points.push(new THREE.Vector3(x, z, -y));
            }

            if (ionPath) {
                scene.remove(ionPath);
                ionPath.geometry.dispose();
                ionPath.material.dispose();
            }

            const curve = new THREE.CatmullRomCurve3(points);
            const tubeGeometry = new THREE.TubeGeometry(curve, 512, 0.04, 8);
            const tubeMaterial = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
            ionPath = new THREE.Mesh(tubeGeometry, tubeMaterial);
            ionPath.name = 'ionPath';
            scene.add(ionPath);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        document.getElementById('slider-t').addEventListener('input', (event) => {
            document.getElementById('input-t').value = event.target.value;
            updateVisualization();
