<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>H₂⁺ Molecular Orbital Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f4f4f9;
        }
        #app-container {
            width: 80%;
            max-width: 1200px;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        #control-panel {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #potential-energy-plot-container, #bonding-plot-container, #antibonding-plot-container {
            width: 100%;
            height: 400px;
        }
        #density-plots-container {
            display: flex;
            justify-content: space-around;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="slider-r">R (Å)</label>
            <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
            <output id="slider-r-value">0.001</output>
        </div>
        <div id="potential-energy-plot-container"></div>
        <div id="density-plots-container">
            <div id="bonding-plot-container"></div>
            <div id="antibonding-plot-container"></div>
        </div>
    </div>

    <script>
        const sliderR = document.getElementById('slider-r');
        const sliderRValue = document.getElementById('slider-r-value');

        function updateSliderValue() {
            const value = parseFloat(sliderR.value).toFixed(3);
            sliderRValue.textContent = value;
            updatePlots(value);
        }

        sliderR.addEventListener('input', updateSliderValue);

        function calculatePotentialEnergy(R_au) {
            const R_values = Array.from({ length: 100 }, (_, i) => 0.2 + i * 0.148); // From 0.2 to 15 in AU
            const E_b = [];
            const E_a = [];

            for (const R of R_values) {
                const S_ab = (1 + R + R * R / 3) * Math.exp(-R);
                const J = (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
                const K = (1 + R) * Math.exp(-R);
                const E_b_value = (J + K) / (1 + S_ab);
                const E_a_value = (J - K) / (1 - S_ab);
                E_b.push(E_b_value);
                E_a.push(E_a_value);
            }

            return { R_values, E_b, E_a };
        }

        function createPotentialEnergyPlot(R_values, E_b, E_a) {
            const trace1 = {
                x: R_values.map(R => R * 0.529177), // Convert to Å
                y: E_b,
                mode: 'lines',
                name: 'bonding PE',
                line: { color: 'blue' }
            };

            const trace2 = {
                x: R_values.map(R => R * 0.529177),
                y: E_a,
                mode: 'lines',
                name: 'antibonding PE',
                line: { color: 'red' }
            };

            const data = [trace1, trace2];

            const layout = {
                title: 'Potential Energy',
                xaxis: { title: 'R (Å)', range: [0, 8] },
                yaxis: { range: [-0.6, 0.4] },
                showlegend: true
            };

            Plotly.newPlot('potential-energy-plot-container', data, layout);
        }

        function calculateDensity(R_au) {
            const x = Array.from({ length: 60 }, (_, i) => -3 + i * 0.125);
            const y = Array.from({ length: 60 }, (_, i) => -3 + i * 0.125);
            const z_bonding = [];
            const z_antibonding = [];

            for (const xi of x) {
                const row_bonding = [];
                const row_antibonding = [];
                for (const yi of y) {
                    const dist_A = Math.sqrt((xi + R_au / 2) ** 2 + yi ** 2);
                    const dist_B = Math.sqrt((xi - R_au / 2) ** 2 + yi ** 2);
                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);
                    const S_ab = (1 + R_au + R_au * R_au / 3) * Math.exp(-R_au);
                    const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
                    const N_a = 1 / Math.sqrt(2 * (1 - S_ab));
                    const psi_b = N_b * (phi_A + phi_B);
                    const psi_a = N_a * (phi_A - phi_B);
                    row_bonding.push(psi_b ** 2);
                    row_antibonding.push(psi_a ** 2);
                }
                z_bonding.push(row_bonding);
                z_antibonding.push(row_antibonding);
            }

            return { x, y, z_bonding, z_antibonding };
        }

        function createDensityPlots(x, y, z_bonding, z_antibonding) {
            const trace_bonding = {
                x,
                y,
                z: z_bonding,
                type: 'surface',
                colorscale: 'Viridis',
                showscale: false
            };

            const trace_antibonding = {
                x,
                y,
                z: z_antibonding,
                type: 'surface',
                colorscale: 'Viridis',
                showscale: false
            };

            const layout_bonding = {
                showlegend: false,
                autosize: true,
                scene: {
                    xaxis: { range: [-3, 3], showticklabels: false, zeroline: false },
                    yaxis: { range: [-3, 3], showticklabels: false, zeroline: false },
                    zaxis: { range: [0, 0.5], showticklabels: false, zeroline: false },
                    aspectratio: { x: 1, y: 1, z: 0.5 },
                    camera: {
                        eye: { x: 1.25, y: 1.25, z: 1.25 }
                    }
                }
            };

            const layout_antibonding = { ...layout_bonding };

            Plotly.newPlot('bonding-plot-container', [trace_bonding], layout_bonding);
            Plotly.newPlot('antibonding-plot-container', [trace_antibonding], layout_antibonding);
        }

        function updatePlots(R_angstroms) {
            const R_au = R_angstroms / 0.529177;
            const { z_bonding, z_antibonding } = calculateDensity(R_au);
            Plotly.restyle('bonding-plot-container', { z: [z_bonding] });
            Plotly.restyle('antibonding-plot-container', { z: [z_antibonding] });
        }

        function init() {
            const R_au_initial = 0.001 / 0.529177;
            const { R_values, E_b, E_a } = calculatePotentialEnergy(R_au_initial);
            createPotentialEnergyPlot(R_values, E_b, E_a);
            const { x, y, z_bonding, z_antibonding } = calculateDensity(R_au_initial);
            createDensityPlots(x, y, z_bonding, z_antibonding);
        }

        init();
    </script>
</body>
</html>
