            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal" class="active">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>
            <hr>
            <div>
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>
            <div>
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>
            <div>
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>
            <div>
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let time = 0;
        let playing = false;
        let earthRotation = 0;
        let moonOrbit = 0;
        let moonRotation = 0;
        let rotationMode = 'normal';
        let dayNight = false;
        let nearSide = true;
        let earthMoonLine = true;
        let moonOnly = false;

        const earthRadius = 80;
        const moonRadius = 25;
        const orbitRadius = 250;
        const moonOrbitalPeriod = 672;
        const earthRotationalPeriod = 24;

        function setup() {
            createCanvas(document.getElementById('visualization-area').clientWidth, window.innerHeight);
            frameRate(30);
            updateDisplayTime();
        }

        function draw() {
            clear();
            earthRotation = (time / earthRotationalPeriod) * TWO_PI;
            moonOrbit = (time / moonOrbitalPeriod) * TWO_PI;

            if (rotationMode === 'normal') {
                moonRotation = moonOrbit;
            } else if (rotationMode === 'half') {
                moonRotation = moonOrbit / 2;
            } else if (rotationMode === 'double') {
                moonRotation = moonOrbit * 2;
            } else if (rotationMode === 'none') {
                moonRotation = 0;
            }

            if (!moonOnly) {
                drawEarth();
            }
            drawMoon();
            if (earthMoonLine && !moonOnly) {
                stroke(0, 0, 0, 128);
                strokeWeight(2);
                line(0, 0, moonX, moonY);
            }
        }

        function drawEarth() {
            fill('#3B82F6');
            noStroke();
            ellipse(0, 0, earthRadius * 2);

            fill(255);
            noStroke();
            ellipse(0, -earthRadius * 0.8, earthRadius * 0.2);

            fill(0, 255, 0, 128);
            noStroke();
            beginShape();
            vertex(-earthRadius * 0.5, -earthRadius * 0.5);
            vertex(earthRadius * 0.5, -earthRadius * 0.5);
            vertex(earthRadius * 0.3, earthRadius * 0.3);
            endShape(CLOSE);

            push();
            rotate(earthRotation);
            fill(255, 0, 0);
            noStroke();
            ellipse(earthRadius * 0.9, 0, earthRadius * 0.1);
            line(earthRadius * 0.9, 0, earthRadius * 1.1, 0);
            line(earthRadius * 1.1, 0, earthRadius * 1.1, earthRadius * 0.1);
            line(earthRadius * 1.1, earthRadius * 0.1, earthRadius * 0.9, earthRadius * 0.1);
            pop();

            if (dayNight) {
                fill(0, 0, 0, 128);
                noStroke();
                arc(0, 0, earthRadius * 2, earthRadius * 2, 0, PI);
            }
        }

        function drawMoon() {
            let moonX = orbitRadius * cos(moonOrbit - PI / 2);
            let moonY = orbitRadius * sin(moonOrbit - PI / 2);

            if (moonOnly) {
                moonX = 0;
                moonY = 0;
            }

            push();
            translate(moonX, moonY);
            fill('#808080');
            noStroke();
            ellipse(0, 0, moonRadius * 2);

            if (nearSide) {
                push();
                rotate(moonRotation);
                fill('#22C55E');
                noStroke();
                arc(0, 0, moonRadius * 2, moonRadius * 2, 0, PI);
                fill('#D946EF');
                noStroke();
                ellipse(moonRadius * 0.9, 0, moonRadius * 0.1);
                line(moonRadius * 0.9, 0, moonRadius * 1.1, 0);
                line(moonRadius * 1.1, 0, moonRadius * 1.1, moonRadius * 0.1);
                line(moonRadius * 1.1, moonRadius * 0.1, moonRadius * 0.9, moonRadius * 0.1);
                pop();
            }

            if (dayNight) {
                fill(0, 0, 0, 128);
                noStroke();
                arc(0, 0, moonRadius * 2, moonRadius * 2, 0, PI);
            }
            pop();
        }

        function updateDisplayTime() {
            let days = Math.floor(time / 24);
            let hours = time % 24;
            document.getElementById('display-time').textContent = `${days} d ${hours} h`;
        }

        function handleSliderChange() {
            time = parseFloat(document.getElementById('slider-time').value);
            updateDisplayTime();
            redraw();
        }

        function handlePlayPauseClick() {
            if (playing) {
                noLoop();
                document.getElementById('btn-play-pause').textContent = '▶';
            } else {
                loop();
                document.getElementById('btn-play-pause').textContent = '❚❚';
            }
            playing = !playing;
        }

        function handleStepBackClick() {
            time -= 1;
            if (time < 0) time = 0;
            document.getElementById('slider-time').value = time;
            handleSliderChange();
        }

        function handleResetClick() {
            time = 0;
            document.getElementById('slider-time').value = time;
            handleSliderChange();
        }

        function handleStepForwardClick() {
            time += 1;
            if (time > 672) time = 672;
            document.getElementById('slider-time').value = time;
            handleSliderChange();
        }

        function handleRotationButtonClick(event) {
            const buttons = document.querySelectorAll('#rotation-selector button');
            buttons.forEach(button => button.classList.remove('active'));
            event.target.classList.add('active');
            rotationMode = event.target.id.replace('btn-rot-', '');
            redraw();
        }

        function handleCheckboxChange(event) {
            const checkboxId = event.target.id;
            if (checkboxId === 'check-day-night') {
                dayNight = event.target.checked;
            } else if (checkboxId === 'check-near-side') {
                nearSide = event.target.checked;
            } else if (checkboxId === 'check-earth-moon-line') {
                earthMoonLine = event.target.checked;
            } else if (checkboxId === 'check-moon-only') {
                moonOnly = event.target.checked;
            }
            redraw();
        }

        document.getElementById('slider-time').addEventListener('input', handleSliderChange);
        document.getElementById('btn-play-pause').addEventListener('click', handlePlayPauseClick);
        document.getElementById('btn-step-back').addEventListener('click', handleStepBackClick);
        document.getElementById('btn-reset').addEventListener('click', handleResetClick);
        document.getElementById('btn-step-forward').addEventListener('click', handleStepForwardClick);
        document.querySelectorAll('#rotation-selector button').forEach(button => {
            button.addEventListener('click', handleRotationButtonClick);
        });
        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', handleCheckboxChange);
        });
    </script>
</body>
</html>
