    <!-- Zoom Control Panel -->
    <div id="zoom-controls" class="card">
        <div>
            <label for="slider-x-lower">x axis lower boundary</label>
            <input type="range" id="slider-x-lower" min="900" max="1100" value="902" step="1">
            <span id="span-x-lower-value">902.</span>
        </div>
        <div>
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" value="1091" step="1">
            <span id="span-x-upper-value">1091.</span>
        </div>
    </div>

    <script>
        const ν₀ = 1000; // cm⁻¹
        const B_upper = 0.98; // cm⁻¹
        const A_upper = 1.95; // cm⁻¹
        const B_lower = 1.0; // cm⁻¹
        const A_lower = 2.0; // cm⁻¹
        const zeta = 0.2;
        const T = 300; // K
        const k = 0.695; // cm⁻¹/K
        const J_max = 60;

        function calculateSpectrum(K, ΔK, branch) {
            let data = [];
            for (let J = 0; J <= J_max; J++) {
                for (let J_prime = 0; J_prime <= J_max; J_prime++) {
                    if (Math.abs(J - J_prime) === ΔK) {
                        let wavenumber = ν₀ + (B_lower + J * (J + 1) / 2 - B_upper - J_prime * (J_prime + 1) / 2) + zeta * (J - J_prime);
                        let intensity = Math.exp(-k * (wavenumber ** 2) / (2 * T));
                        data.push({ wavenumber, intensity });
                    }
                }
            }
            return data.filter(d => d.wavenumber >= 900 && d.wavenumber <= 1100);
        }

        function updatePlot() {
            const view = document.querySelector('input[name="view-selector"]:checked').value;
            const K = parseInt(document.querySelector('input[name="k-selector"]:checked').value);
            const subbandType = document.querySelector('input[name="subband-type"]:checked').value;
            const branch = document.querySelector('input[name="branch-selector"]:checked').value;

            let data = [];
            let title = "";

            if (view === "full") {
                for (let K = 0; K <= 5; K++) {
                    data.push(...calculateSpectrum(K, 1, "all"));
                    data.push(...calculateSpectrum(K, -1, "all"));
                }
                title = "Rovibronic Spectrum";
            } else {
                if (subbandType === "full") {
                    data.push(...calculateSpectrum(K, 1, "all"));
                    data.push(...calculateSpectrum(K, -1, "all"));
                    title = `K = ${K} sub-band`;
                } else if (subbandType === "positive") {
                    data.push(...calculateSpectrum(K, 1, branch));
                    title = `K = ${K} sub-band\n"positive" sub-band`;
                } else if (subbandType === "negative") {
                    data.push(...calculateSpectrum(K, -1, branch));
                    title = `K = ${K} sub-band\n"negative" sub-band`;
                }
            }

            const trace = {
                type: 'bar',
                x: data.map(d => d.wavenumber),
                y: data.map(d => d.intensity),
                marker: {
                    color: subbandType === "positive" ? 'red' : subbandType === "negative" ? 'blue' : 'black',
                    width: 0.1
                }
            };

            Plotly.newPlot('plot-div', [trace], {
                title: title,
                xaxis: { title: 'wavenumber (cm⁻¹)' },
                yaxis: { title: 'absorbance', range: [0, 1.2] },
                showlegend: false,
                bargap: 0
            });

            const xLower = parseFloat(document.getElementById('slider-x-lower').value);
            const xUpper = parseFloat(document.getElementById('slider-x-upper').value);
            Plotly.relayout('plot-div', {
                'xaxis.range': [xLower, xUpper]
            });
        }

        document.getElementById('radio-view-full').addEventListener('change', () => {
            document.getElementById('k-selector-group').classList.add('disabled');
            document.getElementById('subband-type-group').classList.add('disabled');
            document.getElementById('branch-selector-group').classList.add('hidden', 'disabled');
            updatePlot();
        });

        document.getElementById('radio-view-sub').addEventListener('change', () => {
            document.getElementById('k-selector-group').classList.remove('disabled');
            document.getElementById('subband-type-group').classList.remove('disabled');
            updatePlot();
        });

        document.querySelectorAll('input[name="k-selector"]').forEach(input => {
            input.addEventListener('change', () => {
                const K = parseInt(input.value);
                document.getElementById('radio-subband-negative').disabled = K === 0;
                updatePlot();
            });
        });

        document.querySelectorAll('input[name="subband-type"]').forEach(input => {
            input.addEventListener('change', () => {
                const subbandType = input.value;
                if (subbandType === "full") {
                    document.getElementById('branch-selector-group').classList.add('hidden', 'disabled');
                } else {
                    document.getElementById('branch-selector-group').classList.remove('hidden', 'disabled');
                }
                updatePlot();
            });
        });

        document.querySelectorAll('input[name="branch-selector"]').forEach(input => {
            input.addEventListener('change', updatePlot);
        });

        document.getElementById('slider-x-lower').addEventListener('input', () => {
            const xLower = parseFloat(document.getElementById('slider-x-lower').value);
            const xUpper = parseFloat(document.getElementById('slider-x-upper').value);
            if (xLower >= xUpper) {
                document.getElementById('slider-x-upper').value = xLower + 1;
            }
            document.getElementById('span-x-lower-value').textContent = xLower.toFixed(1);
            updatePlot();
        });

        document.getElementById('slider-x-upper').addEventListener('input', () => {
            const xLower = parseFloat(document.getElementById('slider-x-lower').value);
            const xUpper = parseFloat(document.getElementById('slider-x-upper').value);
            if (xUpper <= xLower) {
                document.getElementById('slider-x-lower').value = xUpper - 1;
            }
            document.getElementById('span-x-upper-value').textContent = xUpper.toFixed(1);
            updatePlot();
        });

        window.onload = updatePlot;
    </script>
</body>
</html>
