            margin-right: 5px;
            padding: 5px 10px;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-strike">strike angle \(\phi\) of fault plane clockwise from North (0° ≤ \(\phi\) < 360°)</label>
                <input type="range" id="slider-strike" min="0" max="360" step="1" value="180">
                <span id="strike-value">180</span>
            </div>
            <div class="slider-container">
                <label for="slider-dip">dip angle \(\delta\) of fault plane down from horizontal (0° ≤ \(\delta\) ≤ 90°)</label>
                <input type="range" id="slider-dip" min="0" max="90" step="1" value="60">
                <span id="dip-value">60</span>
            </div>
            <div class="slider-container">
                <label for="slider-rake">slip angle \(\lambda\) of fault plane between slip direction and strike (−180° ≤ \(\lambda\) < 180°)</label>
                <input type="range" id="slider-rake" min="-180" max="180" step="1" value="90">
                <span id="rake-value">90</span>
            </div>
            <div class="button-container">
                <label>wave type</label>
                <button id="btn-p" class="active">P</button>
                <button id="btn-sv">SV</button>
                <button id="btn-sh">SH</button>
            </div>
        </div>
        <div id="visualization-panel">
            <div id="canvas-container"></div>
            <div id="overlay-controls">
                <label for="checkbox-box">box</label>
                <input type="checkbox" id="checkbox-box">
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.163.0/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.163.0/examples/jsm/"
            }
        }
    </script>
    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let radiationPattern, boxHelper;
        let currentWaveType = 'P';

        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.z = 20;

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.25;
            controls.enableZoom = true;

            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight1.position.set(1, 1, 1).normalize();
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight2.position.set(-1, -1, -1).normalize();
            scene.add(directionalLight2);

            createRadiationPattern();
            createCompass();
            createArrow();

            window.addEventListener('resize', onWindowResize, false);
        }

        function createRadiationPattern() {
            if (radiationPattern) {
                scene.remove(radiationPattern);
            }

            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                const phi = u * Math.PI * 2;
                const theta = v * Math.PI;
                let r;
                switch (currentWaveType) {
                    case 'P':
                        r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        break;
                    case 'SV':
                        r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        break;
                    case 'SH':
                        r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        break;
                }
                target.set(r * Math.sin(theta) * Math.cos(phi), r * Math.sin(theta) * Math.sin(phi), r * Math.cos(theta));
            }, 32, 32);

            const material = new THREE.MeshStandardMaterial({ color: 0xD2B48C, side: THREE.DoubleSide });
            radiationPattern = new THREE.Mesh(geometry, material);
            scene.add(radiationPattern);

            boxHelper = new THREE.BoxHelper(radiationPattern, 0x000000);
            boxHelper.visible = false;
            scene.add(boxHelper);
        }

        function createCompass() {
            const radius = 5;
            const segments = 64;
            const circleGeometry = new THREE.CircleGeometry(radius, segments);
            const circleMaterial = new THREE.LineDashedMaterial({ color: 0x000000, dashSize: 1, gapSize: 1 });
            const circle = new THREE.Line(circleGeometry, circleMaterial);
            circle.computeLineDistances();
            scene.add(circle);

            const labels = ['N', 'E', 'S', 'W'];
            const labelPositions = [
                [0, radius, 0],
                [radius, 0, 0],
                [0, -radius, 0],
                [-radius, 0, 0]
            ];

            labels.forEach((label, index) => {
                const sprite = createTextSprite(label, { fontsize: 18, backgroundColor: { r: 255, g: 255, b: 255, a: 0.5 } });
                sprite.position.set(...labelPositions[index]);
                scene.add(sprite);
            });

            for (let i = 0; i < 12; i++) {
                const angle = (i * 30) * Math.PI / 180;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const sprite = createTextSprite(`${i * 30}°`, { fontsize: 14, backgroundColor: { r: 255, g: 255, b: 255, a: 0.5 } });
                sprite.position.set(x, y, 0);
                scene.add(sprite);
            }
        }

        function createArrow() {
            const arrowHelper = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 5, 0x000000);
            scene.add(arrowHelper);

            const sprite = createTextSprite('U', { fontsize: 18, backgroundColor: { r: 255, g: 255, b: 255, a: 0.5 } });
            sprite.position.set(0, 0, 5);
            scene.add(sprite);
        }

        function createTextSprite(message, parameters) {
            if (parameters === undefined) parameters = {};
            const fontface = parameters.hasOwnProperty("fontface") ? parameters.fontface : "Arial";
            const fontsize = parameters.hasOwnProperty("fontsize") ? parameters.fontsize : 18;
            const borderThickness = parameters.hasOwnProperty("borderThickness") ? parameters.borderThickness : 4;
            const borderColor = parameters.hasOwnProperty("borderColor") ? parameters.borderColor : { r: 0, g: 0, b: 0, a: 1.0 };
            const backgroundColor = parameters.hasOwnProperty("backgroundColor") ? parameters.backgroundColor : { r: 255, g: 255, b: 255, a: 1.0 };

            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = `${fontsize}px ${fontface}`;
            const metrics = context.measureText(message);
            const textWidth = metrics.width;

            context.fillStyle = `rgba(${backgroundColor.r}, ${backgroundColor.g}, ${backgroundColor.b}, ${backgroundColor.a})`;
            context.strokeStyle = `rgba(${borderColor.r}, ${borderColor.g}, ${borderColor.b}, ${borderColor.a})`;
            context.lineWidth = borderThickness;
            context.fillRect(0, 0, textWidth + borderThickness * 2, fontsize + borderThickness * 2);
            context.strokeRect(0, 0, textWidth + borderThickness * 2, fontsize + borderThickness * 2);

            context.fillStyle = `rgba(${borderColor.r}, ${borderColor.g}, ${borderColor.b}, 1.0)`;
            context.fillText(message, borderThickness, fontsize + borderThickness);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture, useScreenCoordinates: false });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(textWidth / 100, fontsize / 100, 1.0);
            return sprite;
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        document.getElementById('slider-strike').addEventListener('input', (event) => {
            const value = parseInt(event.target.value);
            document.getElementById('strike-value').textContent = value;
            applyRotations();
        });

        document.getElementById('slider-dip').addEventListener('input', (event) => {
            const value = parseInt(event.target.value);
            document.getElementById('dip-value').textContent = value;
            applyRotations();
        });

        document.getElementById('slider-rake').addEventListener('input', (event) => {
            const value = parseInt(event.target.value);
            document.getElementById('rake-value').textContent = value;
            applyRotations();
        });

        function applyRotations() {
            const strike = parseInt(document.getElementById('slider-strike').value);
            const dip = parseInt(document.getElementById('slider-dip').value);
            const rake = parseInt(document.getElementById('slider-rake').value);

            radiationPattern.rotation.set(
                THREE.MathUtils.degToRad(dip),
                THREE.MathUtils.degToRad(strike),
                THREE.MathUtils.degToRad(rake)
            );
        }

        document.getElementById('btn-p').addEventListener('click', () => setWaveType('P'));
        document.getElementById('btn-sv').addEventListener('click', () => setWaveType('SV'));
        document.getElementById('btn-sh').addEventListener('click', () => setWaveType('SH'));

        function setWaveType(type) {
            document.getElementById('btn-p').classList.toggle('active', type === 'P');
            document.getElementById('btn-sv').classList.toggle('active', type === 'SV');
            document.getElementById('btn-sh').classList.toggle('active', type === 'SH');
            currentWaveType = type;
            createRadiationPattern();
        }

        document.getElementById('checkbox-box').addEventListener('change', (event) => {
            boxHelper.visible = event.target.checked;
        });

        init();
        animate();
        MathJax.typeset();
    </script>
</body>
</html>
