            for (let i = 0; i < secretImage.length; i++) {
                for (let j = 0; j < secretImage[i].length; j++) {
                    let value = secretImage[i][j];
                    let x = i * 2;
                    let y = j * 2;

                    if (value === 0) {
                        let pattern = random() > 0.5 ? [[0, 0], [0, 0]] : [[0, 0], [0, 0]];
                        drawPattern(share1_graphic, x, y, pattern);
                        drawPattern(share2_graphic, x, y, pattern);
                    } else {
                        let pattern = random() > 0.5 ? [[0, 0], [0, 0]] : [[0, 0], [0, 0]];
                        drawPattern(share1_graphic, x, y, pattern);
                        drawPattern(share2_graphic, x, y, getComplementaryPattern(pattern));
                    }
                }
            }
        }

        function drawPattern(graphic, x, y, pattern) {
            graphic.noStroke();
            for (let i = 0; i < 2; i++) {
                for (let j = 0; j < 2; j++) {
                    if (pattern[i][j] === 0) {
                        graphic.fill(0);
                    } else {
                        graphic.noFill();
                    }
                    graphic.rect(x + i * 16, y + j * 16, 16, 16);
                }
            }
        }

        function getComplementaryPattern(pattern) {
            return pattern.map(row => row.map(value => 1 - value));
        }

        function draw() {
            background(255);
            let x1 = parseFloat(getComputedStyle(document.getElementById('joystick-handle1')).left) / parseFloat(getComputedStyle(document.getElementById('joystick-share1')).width) * width * 2 - width;
            let y1 = parseFloat(getComputedStyle(document.getElementById('joystick-handle1')).top) / parseFloat(getComputedStyle(document.getElementById('joystick-share1')).height) * height * 2 - height;
            let x2 = parseFloat(getComputedStyle(document.getElementById('joystick-handle2')).left) / parseFloat(getComputedStyle(document.getElementById('joystick-share2')).width) * width * 2 - width;
            let y2 = parseFloat(getComputedStyle(document.getElementById('joystick-handle2')).top) / parseFloat(getComputedStyle(document.getElementById('joystick-share2')).height) * height * 2 - height;

            image(share1_graphic, x1, y1);
            image(share2_graphic, x2, y2);
        }

        let isDragging = false;
        let activeHandle = null;
        let joystickContainer = null;

        function mousePressed() {
            if (document.getElementById('joystick-handle1').contains(mouseX, mouseY)) {
                isDragging = true;
                activeHandle = document.getElementById('joystick-handle1');
                joystickContainer = document.getElementById('joystick-share1');
            } else if (document.getElementById('joystick-handle2').contains(mouseX, mouseY)) {
                isDragging = true;
                activeHandle = document.getElementById('joystick-handle2');
                joystickContainer = document.getElementById('joystick-share2');
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function mouseMoved() {
            if (isDragging && activeHandle) {
                let containerRect = joystickContainer.getBoundingClientRect();
                let handleRect = activeHandle.getBoundingClientRect();

                let newX = mouseX - containerRect.left - handleRect.width / 2;
                let newY = mouseY - containerRect.top - handleRect.height / 2;

                newX = constrain(newX, 0, containerRect.width - handleRect.width);
                newY = constrain(newY, 0, containerRect.height - handleRect.height);

                activeHandle.style.left = `${newX}px`;
                activeHandle.style.top = `${newY}px`;
            }
        }

        document.getElementById('btn-reset').addEventListener('click', () => {
