        }
        #control-panel {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
        }
        .control-group {
            display: inline-block;
            vertical-align: top;
        }
        #canvas-container {
            border: 1px solid #999;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="rings-control-group" class="control-group">
                <label for="rings-radios">rings</label>
                <div>
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>
            <div id="circles-per-ring-control-group" class="control-group">
                <label for="cpr-radios">circles per ring</label>
                <div>
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group" class="control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let locators = [createVector(-50, 0), createVector(50, 0)];
        let draggedLocator = null;
        let inverseMode = false;
        let rings = 1;
        let cpr = 8;

        function setup() {
            const canvas = createCanvas(550, 550);
            canvas.parent('canvas-container');
            angleMode(RADIANS);
            translate(width / 2, height / 2);
        }

        function draw() {
            background('#C1D4E6');
            if (inverseMode) {
                drawInverseMode();
            } else {
                drawNormalMode();
            }
        }

        function drawNormalMode() {
            noFill();
            stroke(0);
            strokeWeight(1);

            const R_max = width / 2 * 0.9;
            const k = (1 - sin(PI / cpr)) / (1 + sin(PI / cpr));
            const R_c = R_max / (1 + sin(PI / cpr));
            const r = R_c * sin(PI / cpr);

            for (let i = 0; i < rings; i++) {
                const R_ci = R_c * pow(k, i);
                const ri = r * pow(k, i);
                for (let j = 0; j < cpr; j++) {
                    const angle = j * 2 * PI / cpr;
                    const x = R_ci * cos(angle);
                    const y = R_ci * sin(angle);
                    fill('#8C929D');
                    ellipse(x, y, ri * 2, ri * 2);
                }
            }

            // Guide lines
            stroke('#A0A0A0');
            strokeWeight(1);
            for (let j = 0; j < cpr; j++) {
                const angle = j * 2 * PI / cpr;
                const x = R_c * cos(angle);
                const y = R_c * sin(angle);
                line(0, 0, x, y);
            }
        }

        function drawInverseMode() {
            noFill();
            stroke(0);
            strokeWeight(1);

            const R_max = width / 2 * 0.9;
            const k = (1 - sin(PI / cpr)) / (1 + sin(PI / cpr));
            const R_c = R_max / (1 + sin(PI / cpr));
            const r = R_c * sin(PI / cpr);

            // Draw locators
            fill('#FF0000');
            noStroke();
            ellipse(locators[0].x, locators[0].y, 8, 8);
            ellipse(locators[1].x, locators[1].y, 8, 8);

            // Draw connecting line
            stroke('#FF0000');
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            // Draw inversion circle
            const C_inv = p5.Vector.add(locators[0], locators[1]).div(2);
            const R_inv = dist(locators[0].x, locators[0].y, locators[1].x, locators[1].y) / 2;
            fill('rgba(255, 0, 0, 0.3)');
            ellipse(C_inv.x, C_inv.y, R_inv * 2, R_inv * 2);

            // Draw inverted circles
            for (let i = 0; i < rings; i++) {
                const R_ci = R_c * pow(k, i);
                const ri = r * pow(k, i);
                for (let j = 0; j < cpr; j++) {
                    const angle = j * 2 * PI / cpr;
                    const C_orig = createVector(R_ci * cos(angle), R_ci * sin(angle));
                    const d = dist(C_inv.x, C_inv.y, C_orig.x, C_orig.y);
                    if (abs(d - ri) < 1e-6) {
                        // Inverse is a line
                        const lineDist = R_inv * R_inv / (2 * d);
                        const lineAngle = atan2(C_orig.y - C_inv.y, C_orig.x - C_inv.x);
                        const x1 = C_inv.x + lineDist * cos(lineAngle - PI / 2);
                        const y1 = C_inv.y + lineDist * sin(lineAngle - PI / 2);
                        const x2 = C_inv.x + lineDist * cos(lineAngle + PI / 2);
                        const y2 = C_inv.y + lineDist * sin(lineAngle + PI / 2);
                        line(x1, y1, x2, y2);
                    } else {
                        // Inverse is a circle
                        const C_new = C_inv.add(p5.Vector.sub(C_orig, C_inv).mult(R_inv * R_inv / (d * d - ri * ri)));
                        const r_new = abs(R_inv * R_inv / (d * d - ri * ri)) * ri;
                        fill('rgba(220, 100, 220, 0.5)');
                        ellipse(C_new.x, C_new.y, r_new * 2, r_new * 2);
                    }
                }
            }
        }

        function updateControls() {
            rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            cpr = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            inverseMode = document.getElementById('checkbox-inverse').checked;
            redraw();
        }

        function mousePressed() {
            if (inverseMode) {
                for (let i = 0; i < locators.length; i++) {
                    if (dist(mouseX - width / 2, mouseY - height / 2, locators[i].x, locators[i].y) < 4) {
                        draggedLocator = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== null && inverseMode) {
                locators[draggedLocator].set(mouseX - width / 2, mouseY - height / 2);
                redraw();
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }

        document.querySelectorAll('input[name="rings"]').forEach(input => input.addEventListener('change', updateControls));
        document.querySelectorAll('input[name="cpr"]').forEach(input => input.addEventListener('change', updateControls));
        document.getElementById('checkbox-inverse').addEventListener('change', updateControls);
    </script>
</body>
</html>
