<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-group">
                <label for="slider-logCa">\(C_a\)</label>
                <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
            </div>
            <div class="slider-group">
                <label for="slider-pka">\(pK_a\)</label>
                <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
            </div>
        </div>
        <div class="visualization-area">
            <canvas id="pHCanvas"></canvas>
            <div id="formula-strong" class="formula-overlay">\(\color{maroon}{pH = - \log C_a}\)</div>
            <div id="formula-weak" class="formula-overlay">\(\color{green}{pH = \frac{1}{2} (pK_a - \log C_a)}\)</div>
        </div>
    </div>

    <script>
        let logCa = -1;
        let pKa = 1.2;
        let canvas;

        function setup() {
            canvas = createCanvas(800, 400);
            canvas.parent('pHCanvas');
            drawPlot();
        }

        function drawPlot() {
            background(255);
            drawAxes();
            drawStrongAcidLine();
            drawWeakAcidLine();
            drawExactPHCurve();
            drawIndicatorPoint();
        }

        function drawAxes() {
            stroke(50);
            line(50, height - 50, width - 50, height - 50); // x-axis
            line(50, height - 50, 50, 50); // y-axis

            for (let i = -4; i <= 7; i++) {
                let x = map(i, -4, 7, 50, width - 50);
                line(x, height - 50, x, height - 45);
                text(i, x - 5, height - 30);
            }

            for (let i = 0; i <= 7; i++) {
                let y = map(i, 0, 7, height - 50, 50);
                line(50, y, 55, y);
                text(i, 60, y + 5);
            }
        }

        function drawStrongAcidLine() {
            stroke(maroon);
            let y = map(-logCa, 0, 7, height - 50, 50);
            line(50, y, width - 50, y);
        }

        function drawWeakAcidLine() {
            stroke(green);
            let slope = 0.5;
            let intercept = 0.5 * (pKa - logCa);
            let y1 = map(slope * (-4) + intercept, 0, 7, height - 50, 50);
            let y2 = map(slope * 7 + intercept, 0, 7, height - 50, 50);
            line(50, y1, width - 50, y2);
        }

        function drawExactPHCurve() {
            stroke(blue);
            strokeWeight(2);
            beginShape();
            for (let i = -4; i <= 7; i += 0.1) {
                let Ka = pow(10, -i);
                let Ca = pow(10, logCa);
                let pH = calculateExactPH(Ka, Ca);
                let x = map(i, -4, 7, 50, width - 50);
                let y = map(pH, 0, 7, height - 50, 50);
                vertex(x, y);
            }
            endShape();
        }

        function calculateExactPH(Ka, Ca) {
            let a = 1;
            let b = Ka;
            let c = -Ka * Ca;
            let discriminant = b * b - 4 * a * c;
            let Hplus = (-b + sqrt(discriminant)) / (2 * a);
            return -log10(Hplus);
        }

        function drawIndicatorPoint() {
            stroke(orange);
            strokeWeight(2);
            fill(orange);
            let Ka = pow(10, -pKa);
            let Ca = pow(10, logCa);
            let pH = calculateExactPH(Ka, Ca);
            let x = map(pKa, -4, 7, 50, width - 50);
            let y = map(pH, 0, 7, height - 50, 50);
            ellipse(x, y, 10, 10);
        }

        document.getElementById('slider-logCa').addEventListener('input', (event) => {
            logCa = parseFloat(event.target.value);
            drawPlot();
        });

        document.getElementById('slider-pka').addEventListener('input', (event) => {
            pKa = parseFloat(event.target.value);
            drawPlot();
        });
    </script>
</body>
