        .joystick-area {
            position: relative;
            width: 100px;
            height: 100px;
            background-color: #ddd;
            border-radius: 50%;
            margin: 0 auto;
        }
        .joystick-area::before,
        .joystick-area::after {
            content: '';
            position: absolute;
            width: 2px;
            height: 50%;
            background-color: #888;
        }
        .joystick-area::before {
            top: 25%;
            left: 50%;
            transform: translateX(-50%);
        }
        .joystick-area::after {
            left: 25%;
            top: 50%;
            transform: translateY(-50%);
        }
        .joystick-handle {
            position: absolute;
            width: 20px;
            height: 20px;
            background-color: #fff;
            border: 2px solid #888;
            border-radius: 50%;
            cursor: pointer;
        }
        #canvas-container {
            width: 70%;
        }
        canvas {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>search algorithm</label>
                <div>
                    <input type="radio" id="radio-dijkstra" name="algorithm" value="dijkstra" checked>
                    <label for="radio-dijkstra">Dijkstra</label>
                    <input type="radio" id="radio-a-star" name="algorithm" value="a-star">
                    <label for="radio-a-star">A*</label>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-step">algorithm step</label>
                <div class="slider-container">
                    <input type="range" id="slider-step">
                    <span id="label-step">+ 1</span>
                </div>
            </div>
            <div class="control-group">
                <label>starting point</label>
                <div id="joystick-start" class="joystick-area">
                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="pathfinding-canvas"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const gridSize = 50;
        const cellSize = 800 / gridSize;
        let history = [];
        let currentStep = 1;
        let start = { x: 0.15, y: 0.85 };
        let end = { x: 0.85, y: 0.15 };
        let obstacles = [];

        const obstaclesLayouts = {
            '1-square': [{ x: 25, y: 25 }],
            '2-squares': [{ x: 25, y: 25 }, { x: 26, y: 25 }],
            'l-shaped-wall': [
                { x: 25, y: 25 }, { x: 26, y: 25 }, { x: 27, y: 25 },
                { x: 25, y: 26 }, { x: 25, y: 27 }
            ]
        };

        function setup() {
            createCanvas(800, 800);
            frameRate(60);

            const sliderStep = document.getElementById('slider-step');
            sliderStep.min = 1;
            sliderStep.max = 176;
            sliderStep.value = 1;
            sliderStep.addEventListener('input', () => {
                currentStep = parseInt(sliderStep.value);
                document.getElementById('label-step').textContent = `+ ${currentStep}`;
                draw();
            });

            document.getElementById('radio-dijkstra').addEventListener('change', computePath);
            document.getElementById('radio-a-star').addEventListener('change', computePath);
            document.getElementById('select-obstacles').addEventListener('change', computePath);

            const joystickStart = document.getElementById('joystick-start');
            const handleStart = document.getElementById('handle-start');
            const joystickEnd = document.getElementById('joystick-end');
            const handleEnd = document.getElementById('handle-end');

            function dragHandle(handle, callback) {
                let isDragging = false;

                handle.addEventListener('mousedown', () => isDragging = true);
                document.addEventListener('mouseup', () => isDragging = false);

                document.addEventListener('mousemove', (event) => {
                    if (!isDragging) return;
                    const rect = handle.parentNode.getBoundingClientRect();
                    const x = constrain((event.clientX - rect.left) / rect.width, 0, 1);
                    const y = constrain((event.clientY - rect.top) / rect.height, 0, 1);
                    handle.style.left = `${x * 100}%`;
                    handle.style.top = `${y * 100}%`;
                    callback({ x, y });
                });
            }

            dragHandle(handleStart, (pos) => {
                start = pos;
                computePath();
            });

            dragHandle(handleEnd, (pos) => {
                end = pos;
                computePath();
            });

            computePath();
        }

        function draw() {
            clear();
            if (history.length === 0) return;

            const frame = history[currentStep - 1];
            for (let y = 0; y < gridSize; y++) {
                for (let x = 0; x < gridSize; x++) {
                    const cell = frame.grid[y][x];
                    fill(getCellColor(cell));
                    rect(x * cellSize, y * cellSize, cellSize, cellSize);
                }
            }

            stroke(34, 168, 83);
            fill(34, 168, 83);
            rect(start.x * gridSize * cellSize, start.y * gridSize * cellSize, cellSize, cellSize);
            rect(end.x * gridSize * cellSize, end.y * gridSize * cellSize, cellSize, cellSize);

            if (currentStep === history.length) {
                stroke(255, 0, 0);
                strokeWeight(3);
                noFill();
                beginShape();
                for (const pos of frame.path) {
                    vertex(pos.x * gridSize * cellSize + cellSize / 2, pos.y * gridSize * cellSize + cellSize / 2);
                }
                endShape();
            }
        }

        function getCellColor(cell) {
            switch (cell.state) {
                case 'empty':
                    return '#FFFFFF';
                case 'obstacle':
                    return '#000000';
                case 'open':
                    return '#BDE0FE';
                case 'closed':
                    return '#0000CD';
                default:
                    return '#FFFFFF';
            }
        }

        function computePath() {
            const algorithm = document.querySelector('input[name="algorithm"]:checked').value;
            obstacles = obstaclesLayouts[document.getElementById('select-obstacles').value];

            const grid = Array.from({ length: gridSize }, () =>
                Array.from({ length: gridSize }, () => ({ state: 'empty' }))
            );

            obstacles.forEach(({ x, y }) => {
                grid[y][x].state = 'obstacle';
            });

            const startNode = { x: Math.floor(start.x * gridSize), y: Math.floor(start.y * gridSize) };
            const endNode = { x: Math.floor(end.x * gridSize), y: Math.floor(end.y * gridSize) };

            history = [];
            currentStep = 1;

            if (algorithm === 'dijkstra') {
                dijkstra(grid, startNode, endNode);
            } else if (algorithm === 'a-star') {
                aStar(grid, startNode, endNode);
            }

            const sliderStep = document.getElementById('slider-step');
            sliderStep.max = history.length;
            sliderStep.value = 1;
            document.getElementById('label-step').textContent = `+ ${currentStep}`;

            draw();
        }

        function dijkstra(grid, start, end) {
            const openSet = new PriorityQueue();
            const closedSet = new Set();
            const cameFrom = {};
            const gScore = Array.from({ length: gridSize }, () =>
                Array(gridSize).fill(Infinity)
            );
            gScore[start.y][start.x] = 0;

            openSet.enqueue(start, 0);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue();

                if (current.x === end.x && current.y === end.y) {
                    reconstructPath(cameFrom, current);
                    break;
                }

                closedSet.add(`${current.x},${current.y}`);

                for (const neighbor of getNeighbors(current)) {
                    if (closedSet.has(`${neighbor.x},${neighbor.y}`)) continue;

                    const tentativeGScore = gScore[current.y][current.x] + 1;

                    if (tentativeGScore < gScore[neighbor.y][neighbor.x]) {
                        cameFrom[`${neighbor.x},${neighbor.y}`] = current;
                        gScore[neighbor.y][neighbor.x] = tentativeGScore;
                        openSet.enqueue(neighbor, tentativeGScore);
                    }
                }

                saveFrame(grid, openSet, closedSet, []);
            }
        }

        function aStar(grid, start, end) {
            const openSet = new PriorityQueue();
            const closedSet = new Set();
            const cameFrom = {};
            const gScore = Array.from({ length: gridSize }, () =>
                Array(gridSize).fill(Infinity)
            );
            const fScore = Array.from({ length: gridSize }, () =>
                Array(gridSize).fill(Infinity)
            );
            gScore[start.y][start.x] = 0;
            fScore[start.y][start.x] = heuristic(start, end);

            openSet.enqueue(start, fScore[start.y][start.x]);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue();

                if (current.x === end.x && current.y === end.y) {
                    reconstructPath(cameFrom, current);
                    break;
                }

                closedSet.add(`${current.x},${current.y}`);

                for (const neighbor of getNeighbors(current)) {
                    if (closedSet.has(`${neighbor.x},${neighbor.y}`)) continue;

                    const tentativeGScore = gScore[current.y][current.x] + 1;

                    if (tentativeGScore < gScore[neighbor.y][neighbor.x]) {
                        cameFrom[`${neighbor.x},${neighbor.y}`] = current;
                        gScore[neighbor.y][neighbor.x] = tentativeGScore;
                        fScore[neighbor.y][neighbor.x] = tentativeGScore + heuristic(neighbor, end);
                        openSet.enqueue(neighbor, fScore[neighbor.y][neighbor.x]);
                    }
                }

                saveFrame(grid, openSet, closedSet, []);
            }
        }

        function reconstructPath(cameFrom, current) {
            const path = [];
            while (current) {
                path.unshift(current);
                current = cameFrom[`${current.x},${current.y}`];
            }
            history[history.length - 1].path = path;
        }

        function saveFrame(grid, openSet, closedSet, path) {
            const frame = { grid: JSON.parse(JSON.stringify(grid)), openSet: [], closedSet: [], path };
            openSet.toArray().forEach(node => frame.openSet.push(node));
            closedSet.forEach(id => {
                const [x, y] = id.split(',').map(Number);
                frame.closedSet.push({ x, y });
            });
            history.push(frame);
        }

        function getNeighbors(node) {
            const directions = [
                { dx: 1, dy: 0 }, { dx: -1, dy: 0 }, { dx: 0, dy: 1 }, { dx: 0, dy: -1 },
                { dx: 1, dy: 1 }, { dx: -1, dy: -1 }, { dx: 1, dy: -1 }, { dx: -1, dy: 1 }
            ];
            const neighbors = [];

            for (const dir of directions) {
                const x = node.x + dir.dx;
                const y = node.y + dir.dy;
                if (x >= 0 && x < gridSize && y >= 0 && y < gridSize && grid[y][x].state !== 'obstacle') {
                    neighbors.push({ x, y });
                }
            }

            return neighbors;
        }

        function heuristic(a, b) {
            return Math.abs(a.x - b.x) + Math.abs(a.y - b.y);
        }

        class PriorityQueue {
            constructor() {
                this.elements = [];
            }

            enqueue(element, priority) {
                this.elements.push({ element, priority });
                this.elements.sort((a, b) => a.priority - b.priority);
            }

            dequeue() {
                return this.elements.shift().element;
            }

            isEmpty() {
                return this.elements.length === 0;
            }

            toArray() {
                return this.elements.map(el => el.element);
            }
        }
    </script>
</body>
</html>
