            // Draw axes
            stroke(0);
            line(-width / 2, 0, width / 2, 0);
            line(0, -height / 2, 0, height / 2);

            // Draw source charge
            fill(0, 0, 255);
            ellipse(0, 0, 10, 10);

            // Draw test charge locator
            fill(0);
            ellipse(x * 40, y * 40, 10, 10);

            // Calculate vectors
            let r = sqrt(x * x + y * y);
            let Ex = k * sourceCharge * x / (r * r * r);
            let Ey = k * sourceCharge * y / (r * r * r);
            let Evec = createVector(Ex, Ey);

            let Fvec = Evec.copy().mult(testCharge);

            // Draw vectors
            stroke(255, 0, 0); // Red for Electric Field
            drawArrow(createVector(x * 40, y * 40), Evec.mult(400), color(255, 0, 0));

            if (testCharge !== 0) {
                stroke(0, 0, 255); // Blue for Electric Force
                drawArrow(createVector(x * 40, y * 40), Fvec.mult(400), color(0, 0, 255));
            }
        }

        function drawArrow(base, vec, col) {
            push();
            stroke(col);
            strokeWeight(3);
            fill(col);
            translate(base.x, base.y);
            line(0, 0, vec.x, vec.y);
            rotate(vec.heading());
            let arrowSize = 7;
            triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
            pop();
        }

        function updateDisplay() {
            let r = sqrt(x * x + y * y);
            let Ex = k * sourceCharge * x / (r * r * r);
            let Ey = k * sourceCharge * y / (r * r * r);
            let Evec = createVector(Ex, Ey);

            let Fvec = Evec.copy().mult(testCharge);

            let Emag = Evec.mag();
            let Fmag = Fvec.mag();

            document.getElementById('display-E-mag').innerText = `|E| = ${Emag.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerText = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerText = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').innerText = `x position = ${x.toFixed(2)} m`;
            document.getElementById('display-y-pos').innerText = `y position = ${y.toFixed(2)} m`;
            document.getElementById('display-force').innerText = `Electric force = ${Fmag.toFixed(3)} N`;
        }

        document.getElementById('slider-source-charge').addEventListener('input', (event) => {
            sourceCharge = parseFloat(event.target.value);
            document.getElementById('label-source-charge').innerText = sourceCharge.toFixed(1);
            redraw();
            updateDisplay();
        });

        document.getElementById('slider-test-charge').addEventListener('input', (event) => {
            testCharge = parseFloat(event.target.value);
            document.getElementById('label-test-charge').innerText = testCharge.toFixed(4);
            redraw();
            updateDisplay();
        });

        function mousePressed() {
            let d = dist(mouseX, mouseY, x * 40 + width / 2, y * 40 + height / 2);
            if (d < 10) {
                dragging = true;
            }
        }

        function mouseDragged() {
            if (dragging) {
                x = (mouseX - width / 2) / 40;
                y = (height / 2 - mouseY) / 40;
                redraw();
                updateDisplay();
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
