    </style>
</head>
<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>
    <div id="controls-container">
        <div id="function-selector">
            <input type="checkbox" id="checkbox-hertz" checked>
            <label for="checkbox-hertz">Hertz</label>
            <input type="checkbox" id="checkbox-overlap" checked>
            <label for="checkbox-overlap">Overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>
        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>
        <div id="sliders-container">
            <div id="slider-r-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" value="2.5" min="0.1" max="5" step="0.1">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" value="50" min="1" max="100" step="0.01">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>
    <p id="hover-info">(Hover over the curves to see math labels.)</p>
    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>
    <script>
        function init() {
            const rSlider = document.getElementById('slider-r');
            const kSlider = document.getElementById('slider-k');
            const rValueSpan = document.getElementById('span-r-value');
            const kValueSpan = document.getElementById('span-k-value');

            rSlider.oninput = () => {
                rValueSpan.textContent = rSlider.value;
                updatePlots();
            };

            kSlider.oninput = () => {
                kValueSpan.textContent = kSlider.value;
                updatePlots();
            };

            const checkboxes = document.querySelectorAll('#function-selector input[type="checkbox"]');
            checkboxes.forEach(checkbox => {
                checkbox.onchange = updatePlots;
            });

            const scaleButtons = document.querySelectorAll('#scale-selector button');
            scaleButtons.forEach(button => {
                button.onclick = () => {
                    scaleButtons.forEach(btn => btn.classList.remove('active'));
                    button.classList.add('active');
                    updatePlots();
                };
            });

            updatePlots();
        }

        function updatePlots() {
            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            const hertzChecked = document.getElementById('checkbox-hertz').checked;
            const overlapChecked = document.getElementById('checkbox-overlap').checked;
            const gaussianChecked = document.getElementById('checkbox-gaussian').checked;

            const scaleType = document.querySelector('#scale-selector button.active').id;

            const rValues = Array.from({ length: 200 }, (_, i) => i * rMax / 199);
            const kValues = Array.from({ length: 200 }, (_, i) => i * kMax / 199);

            const tracesLeft = [];
            const tracesRight = [];

            if (hertzChecked) {
                const fH = rValues.map(r => r <= 1 ? Math.pow(1 - r * r, 2.5) : 0);
                const FH = kValues.map(k => k === 0 ? 15 * Math.PI : 15 * Math.PI * ((3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5));
                tracesLeft.push({ x: rValues, y: fH, mode: 'lines', name: 'Hertz', line: { color: '#ff0000' }, hovertemplate: 'Hertz<br>$f(r) = (1 - r^2)^{5/2}$<extra></extra>' });
                tracesRight.push({ x: kValues, y: FH, mode: 'lines', name: 'Hertz', line: { color: '#ff0000' }, hovertemplate: 'Hertz<br>$F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<extra></extra>' });
            }

            if (overlapChecked) {
                const fO = rValues.map(r => r <= 1 ? Math.pow(1 - r, 2) : 0);
                const FO = kValues.map(k => k === 0 ? 8 * Math.PI : 8 * Math.PI * (2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4));
                tracesLeft.push({ x: rValues, y: fO, mode: 'lines', name: 'Overlap', line: { color: '#000000' }, hovertemplate: 'Overlap<br>$f(r) = (1 - r)^2$<extra></extra>' });
                tracesRight.push({ x: kValues, y: FO, mode: 'lines', name: 'Overlap', line: { color: '#000000' }, hovertemplate: 'Overlap<br>$F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<extra></extra>' });
            }

            if (gaussianChecked) {
                const fG = rValues.map(r => Math.exp(-Math.PI * r * r));
                const FG = kValues.map(k => Math.exp(-k * k / (4 * Math.PI)) / Math.sqrt(Math.PI));
                tracesLeft.push({ x: rValues, y: fG, mode: 'lines', name: 'Gaussian', line: { color: '#0000ff' }, hovertemplate: 'Gaussian<br>$f(r) = e^{-\\pi r^2}$<extra></extra>' });
                tracesRight.push({ x: kValues, y: FG, mode: 'lines', name: 'Gaussian', line: { color: '#0000ff' }, hovertemplate: 'Gaussian<br>$F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}$<extra></extra>' });
            }

            const layoutLeft = {
                title: 'Function',
                xaxis: { title: 'r' },
                yaxis: { type: scaleType.includes('log') ? 'log' : 'linear' },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)',
                font: { color: 'lightgray' }
            };

            const layoutRight = {
                title: '3D Fourier Transform (real part)',
                xaxis: { title: 'k' },
                yaxis: { type: scaleType.includes('log') ? 'log' : 'linear' },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)',
                font: { color: 'lightgray' }
            };

            Plotly.newPlot('plot-left', tracesLeft, layoutLeft);
            Plotly.newPlot('plot-right', tracesRight, layoutRight);

            MathJax.typesetPromise([document.getElementById('plot-left'), document.getElementById('plot-right')]);
        }

        window.onload = init;
    </script>
</body>
</html>
