            background-color: #ccc;
        }
        #fold-slider-container {
            display: flex;
            align-items: center;
        }
        #btn-reset-view {
            padding: 5px 10px;
            cursor: pointer;
            border: 1px solid #ccc;
            background-color: #fff;
        }
        #canvas-container {
            width: 100%;
            height: 600px;
            border: 1px solid #ccc;
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="solid-selector">
                <button id="btn-solid-1" class="solid-btn active">1</button>
                <button id="btn-solid-2" class="solid-btn">2</button>
                <button id="btn-solid-3" class="solid-btn">3</button>
            </div>
            <div id="fold-slider-container">
                <label for="slider-fold">fold-unfold</label>
                <input type="range" id="slider-fold" min="0" max="1" step="0.01" value="0">
            </div>
            <button id="btn-reset-view">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <!-- three.js and OrbitControls -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://threejs.org/examples/js/controls/OrbitControls.js"></script>

    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth * 0.8, 600);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let currentSolid = 1;
        let currentObject = null;
        let controls;

        function initControls() {
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.25;
            controls.enableZoom = true;
        }

        function createSolid(solidNumber) {
            if (currentObject) {
                scene.remove(currentObject);
            }

            let geometry, material, mesh;
            switch (solidNumber) {
                case 1:
                    geometry = new THREE.BoxGeometry(2, 2, 2);
                    material = new THREE.MeshStandardMaterial({ color: 0xE47A55, side: THREE.DoubleSide, flatShading: true });
                    break;
                case 2:
                    geometry = new THREE.SphereGeometry(1, 32, 32);
                    material = new THREE.MeshStandardMaterial({ color: 0xA4C6E3, side: THREE.DoubleSide, flatShading: true });
                    break;
                case 3:
                    geometry = new THREE.TorusGeometry(1, 0.3, 16, 100);
                    material = new THREE.MeshStandardMaterial({ color: 0xF4E0E1, side: THREE.DoubleSide, flatShading: true });
                    break;
            }

            currentObject = new THREE.Mesh(geometry, material);
            scene.add(currentObject);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        document.getElementById('btn-solid-1').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-1').classList.add('active');
            currentSolid = 1;
            createSolid(currentSolid);
        });

        document.getElementById('btn-solid-2').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-2').classList.add('active');
            currentSolid = 2;
            createSolid(currentSolid);
        });

        document.getElementById('btn-solid-3').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-3').classList.add('active');
            currentSolid = 3;
            createSolid(currentSolid);
        });

        document.getElementById('slider-fold').addEventListener('input', (event) => {
            const t = parseFloat(event.target.value);
            if (currentObject) {
                // Example folding logic
                currentObject.rotation.x = t * Math.PI / 2;
                currentObject.rotation.y = t * Math.PI / 2;
                currentObject.rotation.z = t * Math.PI / 2;
            }
        });

        document.getElementById('btn-reset-view').addEventListener('click', () => {
            camera.position.set(5, 5, 5);
            camera.lookAt(scene.position);
            controls.reset();
        });

        initControls();
        createSolid(currentSolid);
        animate();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth * 0.8, 600);
        });
    </script>
</body>
</html>
