<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas" width="600" height="550"></canvas>
        </div>
    </div>

    <script>
        let sliderAC, sliderBC, valueAC, valueBC, btnReset;
        let canvas;

        function setup() {
            canvas = createCanvas(600, 550);
            canvas.parent('ternary-plot-canvas');

            sliderAC = select('#slider-ac');
            sliderBC = select('#slider-bc');
            valueAC = select('#value-ac');
            valueBC = select('#value-bc');
            btnReset = select('#btn-reset');

            sliderAC.input(updatePlot);
            sliderBC.input(updatePlot);
            btnReset.mousePressed(resetSliders);

            updatePlot();
        }

        function updatePlot() {
            valueAC.html(sliderAC.value().toFixed(2));
            valueBC.html(sliderBC.value().toFixed(2));
            drawTernaryPlot();
        }

        function resetSliders() {
            sliderAC.value(0.6);
            sliderBC.value(3.93);
            updatePlot();
        }

        function drawTernaryPlot() {
            const alpha13 = parseFloat(sliderAC.value());
            const alpha23 = parseFloat(sliderBC.value());
            const N = 10;
            const padding = 20;
            const sideLength = width - 2 * padding;

            // Vertices of the equilateral triangle
            const A = { x: padding, y: height - padding };
            const B = { x: width / 2, y: padding };
            const C = { x: width - padding, y: height - padding };

            clear();

            // Function to calculate vapor mole fractions
            function calculateVaporFractions(x1, x2, x3) {
                const D = alpha13 * x1 + alpha23 * x2 + 1 * x3;
                return [
                    (alpha13 * x1) / D,
                    (alpha23 * x2) / D,
                    (1 * x3) / D
                ];
            }

            // Function to calculate the color based on F
            function getColor(F) {
                const v = F / 2.0;
                if (v <= 0.25) {
                    return lerpColor(color('#2c7bb6'), color('#abd9e9'), v / 0.25);
                } else if (v <= 0.5) {
                    return lerpColor(color('#abd9e9'), color('#ffffbf'), (v - 0.25) / 0.25);
                } else if (v <= 0.75) {
                    return lerpColor(color('#ffffbf'), color('#fdae61'), (v - 0.5) / 0.25);
                } else {
                    return lerpColor(color('#fdae61'), color('#d7191c'), (v - 0.75) / 0.25);
                }
            }

            // Draw the ternary plot
            for (let i = 0; i < N; i++) {
                for (let j = 0; j <= i; j++) {
                    const x1 = (N - i - j) / N;
                    const x2 = j / N;
                    const x3 = i / N;
                    const vaporFractions = calculateVaporFractions(x1, x2, x3);
                    const F = Math.pow(vaporFractions[0] - x1, 2) +
                             Math.pow(vaporFractions[1] - x2, 2) +
                             Math.pow(vaporFractions[2] - x3, 2);
                    fill(getColor(F));
                    stroke(0);
                    strokeWeight(1);
                    triangle(
                        lerp(A.x, B.x, x1),
                        lerp(A.y, B.y, x1),
                        lerp(B.x, C.x, x2),
                        lerp(B.y, C.y, x2),
                        lerp(C.x, A.x, x3),
                        lerp(C.y, A.y, x3)
                    );
                }
            }

            // Draw labels and ticks
            noStroke();
            textSize(14);
            textAlign(CENTER, TOP);
            fill(0);
            text('A', A.x, A.y + 5);
            text('B', B.x, B.y - 5);
            text('C', C.x, C.y + 5);

            for (let i = 0; i <= 10; i++) {
                const x1 = (10 - i) / 10;
                const x2 = i / 10;
                const x3 = 0;
                const vaporFractions = calculateVaporFractions(x1, x2, x3);
                const F = Math.pow(vaporFractions[0] - x1, 2) +
                         Math.pow(vaporFractions[1] - x2, 2) +
                         Math.pow(vaporFractions[2] - x3, 2);
                fill(getColor(F));
                stroke(0);
                strokeWeight(1);
                triangle(
                    lerp(A.x, B.x, x1),
                    lerp(A.y, B.y, x1),
                    lerp(B.x, C.x, x2),
                    lerp(B.y, C.y, x2),
                    lerp(C.x, A.x, x3),
                    lerp(C.y, A.y, x3)
                );
            }
        }
    </script>
</body>
</html>
