        </div>
    </div>

    <script>
        let P = [1, 0, 0];
        let U = [1, 5, 3, 1];
        let C1, C2, C3, Q;

        function setup() {
            const canvases = [
                { id: 'plot-canvas-1', data: P, xLabel: 't (hr)', yLabel: 'P (in)', xMax: 5, yMax: 10 },
                { id: 'plot-canvas-2', data: U, xLabel: 't (hr)', yLabel: 'Q (cfs/in)', xMax: 5, yMax: 10 },
                { id: 'plot-canvas-3', data: C1, xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, offset: 0 },
                { id: 'plot-canvas-4', data: C2, xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, offset: 1 },
                { id: 'plot-canvas-5', data: C3, xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, offset: 2 },
                { id: 'plot-canvas-6', data: Q, xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, offset: 0 }
            ];

            canvases.forEach(canvas => {
                const p5Canvas = createCanvas(300, 200).parent(canvas.id);
                drawPlot(p5Canvas, canvas.data, canvas.xLabel, canvas.yLabel, canvas.xMax, canvas.yMax, canvas.offset);
            });

            document.getElementById('toggle-controls').addEventListener('click', () => {
                const slidersArea = document.getElementById('sliders-area');
                if (slidersArea.style.display === 'none') {
                    slidersArea.style.display = 'block';
                } else {
                    slidersArea.style.display = 'none';
                }
            });

            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', update);
            });
        }

        function update() {
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];
            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            C1 = P.map(p => p * U[0]);
            C2 = P.map(p => p * U[1]);
            C3 = P.map(p => p * U[2]);
            Q = [
                P[0] * U[0],
                P[0] * U[1] + P[1] * U[0],
                P[0] * U[2] + P[1] * U[1] + P[2] * U[0],
                P[0] * U[3] + P[1] * U[2] + P[2] * U[1],
                P[1] * U[3] + P[2] * U[2],
                P[2] * U[3]
            ];

            document.getElementById('value-precip-1').textContent = P[0];
            document.getElementById('value-precip-2').textContent = P[1];
            document.getElementById('value-precip-3').textContent = P[2];
            document.getElementById('value-uh-1').textContent = U[0];
            document.getElementById('value-uh-2').textContent = U[1];
            document.getElementById('value-uh-3').textContent = U[2];
            document.getElementById('value-uh-4').textContent = U[3];

            const canvases = [
                { id: 'plot-canvas-1', data: P },
                { id: 'plot-canvas-2', data: U },
                { id: 'plot-canvas-3', data: C1, offset: 0 },
                { id: 'plot-canvas-4', data: C2, offset: 1 },
                { id: 'plot-canvas-5', data: C3, offset: 2 },
                { id: 'plot-canvas-6', data: Q, offset: 0 }
            ];

            canvases.forEach(canvas => {
                const p5Canvas = select(`#${canvas.id}`);
                drawPlot(p5Canvas, canvas.data, canvas.xLabel, canvas.yLabel, canvas.xMax, canvas.yMax, canvas.offset);
            });
        }

        function drawPlot(p5Canvas, data, xLabel, yLabel, xMax, yMax, offset = 0) {
            p5Canvas.clear();
            p5Canvas.background(255);
            p5Canvas.stroke(0);
            p5Canvas.fill('#d4e6f1');
            p5Canvas.strokeWeight(2);

            // Draw axes
            p5Canvas.line(50, p5Canvas.height - 50, 50, 50);
            p5Canvas.line(50, p5Canvas.height - 50, p5Canvas.width - 50, p5Canvas.height - 50);

            // Draw x-axis ticks and labels
            for (let i = 0; i <= xMax; i += 1) {
                const x = map(i, 0, xMax, 50, p5Canvas.width - 50);
                p5Canvas.line(x, p5Canvas.height - 50, x, p5Canvas.height - 45);
                p5Canvas.textAlign(CENTER, TOP);
                p5Canvas.text(i, x, p5Canvas.height - 35);
            }

            // Draw y-axis ticks and labels
            for (let i = 0; i <= yMax; i += 2) {
                const y = map(i, 0, yMax, p5Canvas.height - 50, 50);
                p5Canvas.line(50, y, 55, y);
                p5Canvas.textAlign(RIGHT, CENTER);
                p5Canvas.text(i, 45, y);
            }

            // Draw step chart
            for (let i = 0; i < data.length; i++) {
                const x1 = map(i + offset, 0, xMax, 50, p5Canvas.width - 50);
                const y1 = map(data[i], 0, yMax, p5Canvas.height - 50, 50);
                const x2 = map(i + 1 + offset, 0, xMax, 50, p5Canvas.width - 50);
                const y2 = map(data[i], 0, yMax, p5Canvas.height - 50, 50);
                p5Canvas.rect(x1, y1, x2 - x1, p5Canvas.height - 50 - y1);
                p5Canvas.line(x1, y1, x2, y2);
            }

            // Label axes
            p5Canvas.textAlign(CENTER, BOTTOM);
            p5Canvas.text(xLabel, p5Canvas.width / 2, p5Canvas.height - 10);
            p5Canvas.textAlign(RIGHT, CENTER);
            p5Canvas.text(yLabel, 10, p5Canvas.height / 2);
        }
    </script>
</body>
