            width: 100%;
            height: 400px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>elements of payoff matrix A</h4>
            <div id="matrix-a-controls">
                <div class="slider-container">
                    <label for="slider-a11">\(a_{11}\)</label>
                    <button id="btn-minus-a11">-</button>
                    <input type="range" id="slider-a11" min="-10" max="10" step="1" value="-5">
                    <span id="value-a11">-5</span>
                    <button id="btn-plus-a11">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-a12">\(a_{12}\)</label>
                    <button id="btn-minus-a12">-</button>
                    <input type="range" id="slider-a12" min="-10" max="10" step="1" value="-3">
                    <span id="value-a12">-3</span>
                    <button id="btn-plus-a12">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-a21">\(a_{21}\)</label>
                    <button id="btn-minus-a21">-</button>
                    <input type="range" id="slider-a21" min="-10" max="10" step="1" value="-1">
                    <span id="value-a21">-1</span>
                    <button id="btn-plus-a21">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-a22">\(a_{22}\)</label>
                    <button id="btn-minus-a22">-</button>
                    <input type="range" id="slider-a22" min="-10" max="10" step="1" value="-4">
                    <span id="value-a22">-4</span>
                    <button id="btn-plus-a22">+</button>
                </div>
            </div>
            <h4>elements of payoff matrix B</h4>
            <div id="matrix-b-controls">
                <div class="slider-container">
                    <label for="slider-b11">\(b_{11}\)</label>
                    <button id="btn-minus-b11">-</button>
                    <input type="range" id="slider-b11" min="-10" max="10" step="1" value="-5">
                    <span id="value-b11">-5</span>
                    <button id="btn-plus-b11">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-b12">\(b_{12}\)</label>
                    <button id="btn-minus-b12">-</button>
                    <input type="range" id="slider-b12" min="-10" max="10" step="1" value="-3">
                    <span id="value-b12">-3</span>
                    <button id="btn-plus-b12">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-b21">\(b_{21}\)</label>
                    <button id="btn-minus-b21">-</button>
                    <input type="range" id="slider-b21" min="-10" max="10" step="1" value="-1">
                    <span id="value-b21">-1</span>
                    <button id="btn-plus-b21">+</button>
                </div>
                <div class="slider-container">
                    <label for="slider-b22">\(b_{22}\)</label>
                    <button id="btn-minus-b22">-</button>
                    <input type="range" id="slider-b22" min="-10" max="10" step="1" value="-4">
                    <span id="value-b22">-4</span>
                    <button id="btn-plus-b22">+</button>
                </div>
            </div>
            <h4>payoff matrices A and B</h4>
            <div id="payoff-table-container">
                <table id="payoff-table">
                    <tr>
                        <th></th>
                        <th>\(B_1\)</th>
                        <th>\(B_2\)</th>
                    </tr>
                    <tr>
                        <th>\(A_1\)</th>
                        <td><span id="cell-a11-b11">-5, -5</span></td>
                        <td><span id="cell-a12-b12">-3, -3</span></td>
                    </tr>
                    <tr>
                        <th>\(A_2\)</th>
                        <td><span id="cell-a21-b21">-1, -1</span></td>
                        <td><span id="cell-a22-b22">-4, -4</span></td>
                    </tr>
                </table>
            </div>
        </div>
        <div id="visualization-panel">
            <h4>set of Nash equilibria</h4>
            <div id="equilibria-display"></div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        let a11 = -5, a12 = -3, a21 = -1, a22 = -4;
        let b11 = -5, b12 = -3, b21 = -1, b22 = -4;

        function setup() {
            createCanvas(400, 400);
            noLoop();
        }

        function draw() {
            background(255);
            stroke(0);
            strokeWeight(1);

            // Draw axes
            line(40, height - 40, width - 40, height - 40); // x-axis
            line(40, height - 40, 40, 40); // y-axis

            // Draw tick marks and labels
            for (let i = 0; i <= 5; i++) {
                let x = map(i * 0.2, 0, 1, 40, width - 40);
                let y = map(i * 0.2, 0, 1, height - 40, 40);
                line(x, height - 40, x, height - 35);
                line(40, y, 45, y);
                textSize(12);
                textAlign(CENTER, TOP);
                text(i * 0.2, x, height - 30);
                textAlign(LEFT, CENTER);
                text(i * 0.2, 15, y);
            }

            // Calculate indifference points
            let den1 = a11 - a12 - a21 + a22;
            let y1_indiff = den1 !== 0 ? (a22 - a12) / den1 : NaN;
            let den2 = b11 - b12 - b21 + b22;
            let x1_indiff = den2 !== 0 ? (b22 - b21) / den2 : NaN;

            // Draw Player 1's best response
            stroke(0, 0, 255);
            strokeWeight(2);
            if (isNaN(y1_indiff)) {
                if (a12 - a22 > 0) {
                    line(40, height - 40, 40, 40); // x1 = 1
                } else {
                    line(width - 40, height - 40, width - 40, 40); // x1 = 0
                }
            } else if (y1_indiff >= 0 && y1_indiff <= 1) {
                line(40, height - map(y1_indiff, 0, 1, height - 40, 40), width - 40, height - map(y1_indiff, 0, 1, height - 40, 40));
                if (den1 > 0) {
                    line(40, height - 40, 40, height - map(y1_indiff, 0, 1, height - 40, 40));
                    line(width - 40, height - map(y1_indiff, 0, 1, height - 40, 40), width - 40, 40);
                } else if (den1 < 0) {
                    line(40, height - map(y1_indiff, 0, 1, height - 40, 40), 40, 40);
                    line(width - 40, height - 40, width - 40, height - map(y1_indiff, 0, 1, height - 40, 40));
                }
            } else {
                if (a12 - a22 > 0) {
                    line(40, height - 40, 40, 40); // x1 = 1
                } else {
                    line(width - 40, height - 40, width - 40, 40); // x1 = 0
                }
            }

            // Draw Player 2's best response
            stroke(0, 128, 0);
            strokeWeight(2);
            if (isNaN(x1_indiff)) {
                if (b21 - b11 > 0) {
                    line(40, height - 40, width - 40, height - 40); // y1 = 1
                } else {
                    line(40, height - 40, 40, 40); // y1 = 0
                }
            } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                line(map(x1_indiff, 0, 1, 40, width - 40), height - 40, map(x1_indiff, 0, 1, 40, width - 40), 40);
                if (den2 > 0) {
                    line(40, height - 40, map(x1_indiff, 0, 1, 40, width - 40), height - 40);
                    line(map(x1_indiff, 0, 1, 40, width - 40), height - 40, map(x1_indiff, 0, 1, 40, width - 40), 40);
                } else if (den2 < 0) {
                    line(40, height - 40, map(x1_indiff, 0, 1, 40, width - 40), height - 40);
                    line(map(x1_indiff, 0, 1, 40, width - 40), height - 40, map(x1_indiff, 0, 1, 40, width - 40), 40);
                }
            } else {
                if (b21 - b11 > 0) {
                    line(40, height - 40, width - 40, height - 40); // y1 = 1
                } else {
                    line(40, height - 40, 40, 40); // y1 = 0
                }
            }

            // Draw Nash equilibria
            stroke(255, 0, 0);
            strokeWeight(4);
            fill(255, 0, 0);
            ellipse(40, height - 40, 8, 8);
            ellipse(width - 40, 40, 8, 8);
            ellipse(width - 40, height - 40, 8, 8);
            ellipse(40, 40, 8, 8);
        }

        function updateValues() {
            document.getElementById('value-a11').textContent = a11;
            document.getElementById('value-a12').textContent = a12;
            document.getElementById('value-a21').textContent = a21;
            document.getElementById('value-a22').textContent = a22;
            document.getElementById('value-b11').textContent = b11;
            document.getElementById('value-b12').textContent = b12;
            document.getElementById('value-b21').textContent = b21;
            document.getElementById('value-b22').textContent = b22;

            document.getElementById('cell-a11-b11').textContent = `${a11}, ${b11}`;
            document.getElementById('cell-a12-b12').textContent = `${a12}, ${b12}`;
            document.getElementById('cell-a21-b21').textContent = `${a21}, ${b21}`;
            document.getElementById('cell-a22-b22').textContent = `${a22}, ${b22}`;

            redraw();
        }

        function updateEquilibriaDisplay() {
            let equilibria = "\\{ (0, 1), (\\frac{3}{5}, \\frac{1}{5}), (1, 0) \\}";
            document.getElementById('equilibria-display').innerHTML = equilibria;
            MathJax.typesetPromise([document.getElementById('equilibria-display')]);
        }

        function setupEventListeners() {
            const sliders = [
                { id: 'slider-a11', valueElement: 'value-a11', update: () => a11 = parseInt(document.getElementById('slider-a11').value) },
                { id: 'slider-a12', valueElement: 'value-a12', update: () => a12 = parseInt(document.getElementById('slider-a12').value) },
                { id: 'slider-a21', valueElement: 'value-a21', update: () => a21 = parseInt(document.getElementById('slider-a21').value) },
                { id: 'slider-a22', valueElement: 'value-a22', update: () => a22 = parseInt(document.getElementById('slider-a22').value) },
                { id: 'slider-b11', valueElement: 'value-b11', update: () => b11 = parseInt(document.getElementById('slider-b11').value) },
                { id: 'slider-b12', valueElement: 'value-b12', update: () => b12 = parseInt(document.getElementById('slider-b12').value) },
                { id: 'slider-b21', valueElement: 'value-b21', update: () => b21 = parseInt(document.getElementById('slider-b21').value) },
                { id: 'slider-b22', valueElement: 'value-b22', update: () => b22 = parseInt(document.getElementById('slider-b22').value) }
            ];

            sliders.forEach(slider => {
                document.getElementById(slider.id).addEventListener('input', () => {
                    slider.update();
                    updateValues();
                    updateEquilibriaDisplay();
                });
                document.getElementById(`btn-plus-${slider.id.slice(6)}`).addEventListener('click', () => {
                    document.getElementById(slider.id).value = Math.min(parseInt(document.getElementById(slider.id).value) + 1, 10);
                    slider.update();
                    updateValues();
                    updateEquilibriaDisplay();
                });
                document.getElementById(`btn-minus-${slider.id.slice(6)}`).addEventListener('click', () => {
                    document.getElementById(slider.id).value = Math.max(parseInt(document.getElementById(slider.id).value) - 1, -10);
                    slider.update();
                    updateValues();
                    updateEquilibriaDisplay();
                });
            });
        }

        window.onload = () => {
            setupEventListeners();
            updateValues();
            updateEquilibriaDisplay();
        };
    </script>
</body>
</html>
