```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Interactive Cubic Curve Demo</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f0f0f0;
        }
        #demo-container {
            width: 80%;
            max-width: 800px;
            background-color: white;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            border-radius: 8px;
            overflow: hidden;
        }
        #controls-container {
            padding: 20px;
            border-bottom: 1px solid #ddd;
        }
        #slider-group, #checkbox-group {
            margin-bottom: 10px;
        }
        #canvas-container {
            position: relative;
            width: 100%;
            height: 500px;
        }
        canvas {
            display: block;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="demo-container">
        <div id="controls-container">
            <div id="slider-group">
                <label for="slider-t">choose quadratic</label>
                <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
                <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
                <button id="btn-reset">&#x21BB;</button>
            </div>
            <div id="checkbox-group">
                <input type="checkbox" id="checkbox-family">
                <label for="checkbox-family">family</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let locators = [
            { x: -4, y: 1 },
            { x: -2, y: 1.8 },
            { x: 1, y: -1 },
            { x: 4, y: -1 }
        ];
        let selectedLocator = null;
        let cubicCoeffs = [0, 0, 0, 0];
        let quadraticCoeffs = [0, 0, 0];
        let familyMode = false;
        const scale = 50;
        const canvasSize = 600;

        function setup() {
            createCanvas(canvasSize, canvasSize).parent('canvas-container');
            calculateCubicCoefficients();
            calculateQuadraticCoefficients(0.5);
        }

        function draw() {
            background(255);
            drawAxes();
            drawLocators();
            if (familyMode) {
                drawQuadraticFamily();
            } else {
                drawCubic();
                drawQuadratic();
            }
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(1);
            line(0, height / 2, width, height / 2); // X-axis
            line(width / 2, 0, width / 2, height); // Y-axis
            for (let i = -6; i <= 6; i++) {
                let x = width / 2 + i * scale;
                line(x, height / 2 - 5, x, height / 2 + 5);
                if (i !== 0) {
                    text(i, x, height / 2 + 15);
                }
            }
            for (let i = -5; i <= 5; i++) {
                let y = height / 2 - i * scale;
                line(width / 2 - 5, y, width / 2 + 5, y);
                if (i !== 0) {
                    text(i, width / 2 + 15, y);
                }
            }
        }

        function drawLocators() {
            stroke(0);
            strokeWeight(2);
            fill(255);
            for (let i = 0; i < locators.length; i++) {
                let { x, y } = locators[i];
                let px = width / 2 + x * scale;
                let py = height / 2 - y * scale;
                ellipse(px, py, 10, 10);
                line(px - 5, py, px + 5, py);
                line(px, py - 5, px, py + 5);
            }
        }

        function drawCubic() {
            stroke('#3f51b5');
            strokeWeight(3);
            noFill();
            beginShape();
            for (let x = -6; x <= 6; x += 0.1) {
                let px = width / 2 + x * scale;
                let py = height / 2 - (cubicCoeffs[0] * x**3 + cubicCoeffs[1] * x**2 + cubicCoeffs[2] * x + cubicCoeffs[3]) * scale;
                vertex(px, py);
            }
            endShape();
        }

        function drawQuadratic() {
            stroke('#f57c00');
            strokeWeight(3);
            noFill();
            beginShape();
            for (let x = -6; x <= 6; x += 0.1) {
                let px = width / 2 + x * scale;
                let py = height / 2 - (quadraticCoeffs[0] * x**2 + quadraticCoeffs[1] * x + quadraticCoeffs[2]) * scale;
                vertex(px, py);
            }
            endShape();
        }

        function drawQuadraticFamily() {
            let colors = ['#FF5733', '#33FF57', '#3357FF', '#F3FF33', '#FF33F3'];
            for (let t = -5; t <= 5; t += 0.5) {
                let coeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                let colorIndex = Math.floor((t + 5) / 10 * colors.length) % colors.length;
                let color = colors[colorIndex];
                fill(color, 100);
                noStroke();
                beginShape();
                let yMin = min(quadraticCoeffs.map(c => c[0] * x**2 + c[1] * x + c[2]));
                let yMax = max(quadraticCoeffs.map(c => c[0] * x**2 + c[1] * x + c[2]));
                let yBoundary = (yMin > yMax) ? height / 2 - yMin * scale : height / 2 - yMax * scale;
                vertex(0, yBoundary);
                for (let x = -6; x <= 6; x += 0.1) {
                    let px = width / 2 + x * scale;
                    let py = height / 2 - (coeffs[0] * x**2 + coeffs[1] * x + coeffs[2]) * scale;
                    vertex(px, py);
                }
                vertex(width, yBoundary);
                endShape(CLOSE);
            }
            drawQuadratic();
        }

        function calculateCubicCoefficients() {
            let M = [];
            let Y = [];
            for (let i = 0; i < locators.length; i++) {
                let { x, y } = locators[i];
                M.push([x**3, x**2, x, 1]);
                Y.push(y);
            }
            let det = determinant(M);
            if (det === 0) {
                console.error("Singular matrix");
                return;
            }
            cubicCoeffs = [
                determinant([
                    [Y[0], M[0][1], M[0][2], M[0][3]],
                    [Y[1], M[1][1], M[1][2], M[1][3]],
                    [Y[2], M[2][1], M[2][2], M[2][3]],
                    [Y[3], M[3][1], M[3][2], M[3][3]]
                ]) / det,
                determinant([
                    [M[0][0], Y[0], M[0][2], M[0][3]],
                    [M[1][0], Y[1], M[1][2], M[1][3]],
                    [M[2][0], Y[2], M[2][2], M[2][3]],
                    [M[3][0], Y[3], M[3][2], M[3][3]]
                ]) / det,
                determinant([
                    [M[0][0], M[0][1], Y[0], M[0][3]],
                    [M[1][0], M[1][1], Y[1], M[1][3]],
                    [M[2][0], M[2][1], Y[2], M[2][3]],
                    [M[3][0], M[3][1], Y[3], M[3][3]]
                ]) / det,
                determinant([
                    [M[0][0], M[0][1], M[0][2], Y[0]],
                    [M[1][0], M[1][1], M[1][2], Y[1]],
                    [M[2][0], M[2][1], M[2][2], Y[2]],
                    [M[3][0], M[3][1], M[3][2], Y[3]]
                ]) / det
            ];
        }

        function calculateQuadraticCoefficients(t) {
            quadraticCoeffs = [
                3 * cubicCoeffs[0] * t + cubicCoeffs[1],
                -3 * cubicCoeffs[0] * t**2 + cubicCoeffs[2],
                cubicCoeffs[0] * t**3 + cubicCoeffs[3]
            ];
        }

        function determinant(matrix) {
            return matrix[0][0] * (matrix[1][1] * (matrix[2][2] * matrix[3][3] - matrix[2][3] * matrix[3][2]) -
                                   matrix[1][2] * (matrix[2][1] * matrix[3][3] - matrix[2][3] * matrix[3][1]) +
                                   matrix[1][3] * (matrix[2][1] * matrix[3][2] - matrix[2][2] * matrix[3][1])) -
                   matrix[0][1] * (matrix[1][0] * (matrix[2][2] * matrix[3][3] - matrix[2][3] * matrix[3][2]) -
                                   matrix[1][2] * (matrix[2][0] * matrix[3][3] - matrix[2][3] * matrix[3][0]) +
                                   matrix[1][3] * (matrix[2][0] * matrix[3][2] - matrix[2][2] * matrix[3][0])) +
                   matrix[0][2] * (matrix[1][0] * (matrix[2][1] * matrix[3][3] - matrix[2][3] * matrix[3][1]) -
                                   matrix[1][1] * (matrix[2][0] * matrix[3][3] - matrix[2][3] * matrix[3][0]) +
                                   matrix[1][3] * (matrix[2][0] * matrix[3][1] - matrix[2][1] * matrix[3][0])) -
                   matrix[0][3] * (matrix[1][0] * (matrix[2][1] * matrix[3][2] - matrix[2][2] * matrix[3][1]) -
                                   matrix[1][1] * (matrix[2][0] * matrix[3][2] - matrix[2][2] * matrix[3][0]) +
                                   matrix[1][2] * (matrix[2][0] * matrix[3][1] - matrix[2][1] * matrix[3][0]));
        }

        function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
            return [
                3 * cubicCoeffs[0] * t + cubicCoeffs[1],
                -3 * cubicCoeffs[0] * t**2 + cubicCoeffs[2],
                cubicCoeffs[0] * t**3 + cubicCoeffs[3]
            ];
        }

        function mousePressed() {
            for (let i = 0; i < locators.length; i++) {
                let { x, y } = locators[i];
                let px = width / 2 + x * scale;
                let py = height / 2 - y * scale;
                if (dist(mouseX, mouseY, px, py) < 10) {
                    selectedLocator = i;
                    break;
                }
            }
        }

        function mouseReleased() {
            selectedLocator = null;
        }

        function mouseDragged() {
            if (selectedLocator !== null) {
                let { x, y } = locators[selectedLocator];
                locators[selectedLocator].x = (mouseX - width / 2) / scale;
                locators[selectedLocator].y = -(mouseY - height / 2) / scale;
                calculateCubicCoefficients();
                calculateQuadraticCoefficients(slider.value());
                redraw();
            }
        }

        function keyPressed() {
            if (keyCode === RETURN || keyCode === ENTER) {
                let t = parseFloat(inputT.value());
                if (!isNaN(t)) {
                    slider.value(t);
                    calculateQuadraticCoefficients(t);
                    redraw();
                }
            }
        }

        const slider = document.getElementById('slider-t');
        const inputT = document.getElementById('input-t');
        const checkboxFamily = document.getElementById('checkbox-family');
        const btnReset = document.getElementById('btn-reset');

        slider.addEventListener('input', () => {
            inputT.value = slider.value;
            calculateQuadraticCoefficients(slider.value);
            redraw();
        });

        inputT.addEventListener('input', () => {
            let t = parseFloat(inputT.value);
            if (!isNaN(t)) {
                slider.value = t;
                calculateQuadraticCoefficients(t);
                redraw();
            }
        });

        checkboxFamily.addEventListener('change', () => {
            familyMode = checkboxFamily.checked;
            redraw();
        });

        btnReset.addEventListener('click', () => {
            locators = [
                { x: -4, y: 1 },
                { x: -2, y: 1.8 },
                { x: 1, y: -1 },
                { x: 4, y: -1 }
            ];
            slider.value = 0.5;
            inputT.value = 0.5;
            checkboxFamily.checked = false;
            familyMode = false;
            calculateCubicCoefficients();
            calculateQuadraticCoefficients(0.5);
            redraw();
        });
    </script>
</body>
</html>
