<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="slider-v0">initial volume v₀</label>
                <input type="range" id="slider-v0" class="slider" min="100" max="500" step="1" value="211">
            </div>
            <div>
                <label for="slider-T">temperature T</label>
                <input type="range" id="slider-T" class="slider" min="-273" max="727" step="1" value="270">
            </div>
            <div>
                <label>temperature units</label>
                <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                <label for="radio-kelvin">Kelvin</label>
                <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                <label for="radio-celsius">Celsius</label>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
            <div id="piston-container"></div>
        </div>
    </div>

    <script>
        const T0 = 273.15;
        let v0 = 211;
        let T_current = 270;
        let k = v0 / T0;
        let tempUnit = 'C';

        const plotContainer = document.getElementById('plot-container');
        const pistonContainer = document.getElementById('piston-container');
        const sliderV0 = document.getElementById('slider-v0');
        const sliderT = document.getElementById('slider-T');
        const radioKelvin = document.getElementById('radio-kelvin');
        const radioCelsius = document.getElementById('radio-celsius');

        function updateTemperatureSlider() {
            if (tempUnit === 'C') {
                sliderT.min = -273;
                sliderT.max = 727;
                sliderT.value = T_current;
                sliderT.step = 1;
            } else {
                sliderT.min = 0;
                sliderT.max = 1000;
                sliderT.value = T_current + T0;
                sliderT.step = 1;
            }
        }

        function updatePlot() {
            const x = [];
            const y = [];
            for (let i = sliderT.min; i <= sliderT.max; i++) {
                x.push(i);
                y.push(k * (tempUnit === 'C' ? i + T0 : i));
            }

            const trace1 = {
                x: x,
                y: y,
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                marker: { color: x.map(t => t / (tempUnit === 'C' ? 727 : 1000)), colorscale: 'Viridis' }
            };

            const trace2 = {
                x: [T_current],
                y: [k * (tempUnit === 'C' ? T_current + T0 : T_current)],
                mode: 'markers',
                marker: { size: 12, color: 'red' }
            };

            const data = [trace1, trace2];
            const layout = {
                title: 'Volume vs Temperature',
                xaxis: { title: `temperature ${tempUnit}`, range: [sliderT.min, sliderT.max] },
                yaxis: { title: 'volume', range: [0, 2000] }
            };

            Plotly.newPlot(plotContainer, data, layout);
        }

        function updatePiston() {
            const V_current = k * (tempUnit === 'C' ? T_current + T0 : T_current);
            const pistonHeight = map(V_current, 0, 2000, 0, height);
            background(255);
            fill(0, 102, 153);
            rect(0, height - 10, width, 10); // Base
            rect(0, height - 10 - pistonHeight, width, 10); // Gas
            fill(150);
            rect(0, height - 20 - pistonHeight, width, 10); // Piston
            noFill();
            stroke(255, 165, 0);
            for (let i = 0; i < 10; i++) {
                line(width / 2 - 5, height - 30 - pistonHeight - i * 10, width / 2 + 5, height - 30 - pistonHeight - i * 10);
            }
        }

        function setup() {
            createCanvas(pistonContainer.clientWidth, 400).parent('piston-container');
            updatePlot();
            updatePiston();
        }

        function draw() {
            updatePiston();
        }

        sliderV0.addEventListener('input', () => {
            v0 = parseInt(sliderV0.value);
            k = v0 / T0;
            updatePlot();
            updatePiston();
        });

        sliderT.addEventListener('input', () => {
            T_current = parseInt(sliderT.value);
            if (tempUnit === 'K') {
                T_current -= T0;
            }
            updatePlot();
            updatePiston();
        });

        radioKelvin.addEventListener('change', () => {
            if (radioKelvin.checked) {
                tempUnit = 'K';
                T_current = T_current + T0;
                updateTemperatureSlider();
                updatePlot();
                updatePiston();
            }
        });

        radioCelsius.addEventListener('change', () => {
            if (radioCelsius.checked) {
                tempUnit = 'C';
                T_current = T_current - T0;
                updateTemperatureSlider();
                updatePlot();
                updatePiston();
            }
        });

        window.addEventListener('resize', () => {
            resizeCanvas(pistonContainer.clientWidth, 400);
        });
    </script>
</body>
</html>
